-- Records of articles
-- ----------------------------
INSERT INTO `articles` VALUES (2, 'Báo chí cần truyền cảm hứng, giá trị tốt đẹp', '2022-09-14 10:06:37', 12, 'TP HCM - Báo chí cần kể những câu chuyện ý nghĩa, truyền cảm hứng, thổi bùng giá trị tốt đẹp, giúp hóa giải bế tắc, theo Thứ trưởng Thông tin và Truyền thông.', '<h1 class=\"title-detail\">\'B&aacute;o ch&iacute; cần truyền cảm hứng, gi&aacute; trị tốt đẹp\'</h1>\r\n<p class=\"description\"><span class=\"location-stamp\">TP HCM</span>B&aacute;o ch&iacute; cần kể những c&acirc;u chuyện &yacute; nghĩa, truyền cảm hứng, thổi b&ugrave;ng gi&aacute; trị tốt đẹp, gi&uacute;p h&oacute;a giải bế tắc, theo Thứ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Nội dung được Thứ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Thanh L&acirc;m n&oacute;i tại&nbsp;<em>Hội nghị b&aacute;o ch&iacute; to&agrave;n quốc tổng kết c&ocirc;ng t&aacute;c năm 2022, triển khai nhiệm vụ năm 2023</em>, s&aacute;ng 24/12, với sự tham dự hơn 700 đại diện c&aacute;c cơ quan b&aacute;o ch&iacute;, quản l&yacute; tr&ecirc;n cả nước.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RBnK-tPZw14h9AVsh4Qvng 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ttyozjsls2H-GN3vc11iJg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7G_-_D2r2-oWjc-mYmxOOw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RBnK-tPZw14h9AVsh4Qvng 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ttyozjsls2H-GN3vc11iJg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7G_-_D2r2-oWjc-mYmxOOw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RBnK-tPZw14h9AVsh4Qvng\" alt=\"Thứ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Thanh L&acirc;m ph&aacute;t biểu tại hội nghị, s&aacute;ng 24/12. Ảnh: Thanh Thuỳ\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/thu-truong-bttttlam-1671879214-7131-7751-1671879850.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RBnK-tPZw14h9AVsh4Qvng\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Thứ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Thanh L&acirc;m ph&aacute;t biểu tại hội nghị, s&aacute;ng 24/12. Ảnh:&nbsp;<em>Thanh Thuỳ</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">&Ocirc;ng L&acirc;m dẫn chứng qua gần ba năm cả nước chống chọi v&agrave; vượt qua Covid-19, nhiều tờ b&aacute;o đ&atilde; mở tuyến b&agrave;i hay, &yacute; nghĩa về ch&acirc;n dung y b&aacute;c sĩ, chiến sĩ tuyến đầu chiến đấu với dịch; vinh danh nh&acirc;n vật hết l&ograve;ng v&igrave; cộng đồng... B&ecirc;n cạnh đ&oacute; kh&ocirc;ng gắn với yếu tố thời sự n&oacute;ng, một số cơ quan b&aacute;o ch&iacute; khắc hoạ những con người b&igrave;nh dị, thầm lặng, c&oacute; sức lan toả trong đời sống... Những chuy&ecirc;n mục, tin b&agrave;i n&oacute;i tr&ecirc;n khi đăng tải tạo hiệu ứng tốt, thu h&uacute;t bạn đọc.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n &ocirc;ng L&acirc;m n&ecirc;u vẫn c&ograve;n nhiều tin b&agrave;i thiếu t&iacute;nh nh&acirc;n văn, chạy theo thị hiếu giật g&acirc;n để c&acirc;u view m&agrave; qu&ecirc;n đi gi&aacute; trị cốt l&otilde;i của b&aacute;o ch&iacute;. Đ&acirc;y l&agrave; những biểu hiện lệch lạc, cẩu thả trong t&aacute;c nghiệp của bộ phận ph&oacute;ng vi&ecirc;n, thậm ch&iacute; của một số to&agrave; soạn b&aacute;o v&agrave; tạp ch&iacute;. \"Khi b&aacute;o ch&iacute; phản &aacute;nh cuộc sống với lăng k&iacute;nh m&eacute;o m&oacute;, th&ocirc;ng tin ti&ecirc;u cực sẽ trở th&agrave;nh d&ograve;ng chủ lưu, che lấp những mặt tốt đẹp, t&iacute;ch cực của x&atilde; hội\", &ocirc;ng L&acirc;m n&oacute;i.</p>\r\n<p class=\"Normal\">Theo Thứ trưởng Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng, b&aacute;o ch&iacute; kh&ocirc;ng được đ&aacute;nh mất vai tr&ograve; gi&aacute;m s&aacute;t, phản biện x&atilde; hội, đấu tranh chống c&aacute;i sai, nhưng phản biện, ph&ecirc; ph&aacute;n nhằm x&acirc;y dựng, để độc giả kh&ocirc;ng nh&igrave;n x&atilde; hội to&agrave;n điều bất an, tăm tối. Đ&acirc;y l&agrave; việc kh&oacute; của b&aacute;o ch&iacute; nhưng kh&ocirc;ng phải kh&ocirc;ng thể thực hiện, muốn l&agrave;m được cần sự đồng l&ograve;ng của những nh&agrave; b&aacute;o, ph&oacute;ng vi&ecirc;n ch&acirc;n ch&iacute;nh, c&oacute; nghề.</p>\r\n<p class=\"Normal\">Ng&agrave;y 9/12, Thủ tướng đ&atilde; ban h&agrave;nh Đề &aacute;n \"Tuy&ecirc;n truyền gương điển h&igrave;nh ti&ecirc;n tiến giai đoạn 2022-2025\", trong đ&oacute; xem b&aacute;o ch&iacute; l&agrave; lực lượng quan trọng ph&aacute;t hiện, nh&acirc;n rộng, lan toả c&aacute;c tập thể, c&aacute; nh&acirc;n ti&ecirc;u biểu, xuất sắc trong đời sống...</p>\r\n<p class=\"Normal\">Ngo&agrave;i nội dung b&aacute;o ch&iacute; cần g&oacute;p phần lan toả gi&aacute; trị tốt đẹp, vấn đề siết chặt quản l&yacute;, quy hoạch b&aacute;o ch&iacute; để hạn chế c&aacute;c sai phạm cũng được nhiều đại biểu quan t&acirc;m. Ph&oacute; ban Tuy&ecirc;n gi&aacute;o Trung ương Trần Thanh L&acirc;m đ&aacute;nh gi&aacute; năm qua một số sở th&ocirc;ng tin v&agrave; truyền th&ocirc;ng e d&egrave;, n&eacute; tr&aacute;nh trong xử l&yacute; vi phạm hoạt động b&aacute;o ch&iacute;. Vai tr&ograve; của hội nh&agrave; b&aacute;o c&aacute;c cấp c&ograve;n hạn chế, chưa chủ động xử l&yacute; vi phạm đạo đức nghề nghiệp, quy tắc sử dụng mạng x&atilde; hội của người l&agrave;m b&aacute;o.</p>\r\n<p class=\"Normal\">&Ocirc;ng L&acirc;m cho rằng một số cơ quan b&aacute;o ch&iacute; c&oacute; tỷ lệ th&ocirc;ng tin t&iacute;ch cực v&agrave; mặt tr&aacute;i chưa c&acirc;n đối. T&iacute;nh định hướng, dẫn dắt trong một số vụ việc c&ograve;n chậm, thiếu nhạy b&eacute;n. T&igrave;nh trạng \"b&aacute;o h&oacute;a\" tạp ch&iacute;, biểu hiệu \"tư nh&acirc;n h&oacute;a\" b&aacute;o ch&iacute; chưa được khắc phục triệt để.</p>\r\n<p class=\"Normal\">Năm 2022, Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng đ&atilde; kiểm tra v&agrave; xử phạt vi phạm h&agrave;nh ch&iacute;nh 32 lượt cơ quan b&aacute;o ch&iacute; với tổng số tiền phạt gần 1,9 tỷ đồng; tước quyền sử dụng giấy ph&eacute;p trong thời hạn ba th&aacute;ng đối với hai cơ quan b&aacute;o ch&iacute;; thu hồi thẻ nh&agrave; b&aacute;o đối với một l&atilde;nh đạo cơ quan b&aacute;o ch&iacute;.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1_YlMd0_V6F7vKv5XMNQRg 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hWfLcBBMizBroTWtoAsRlA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XG_2yjZ5Ysze2q9tRCt0-w 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1_YlMd0_V6F7vKv5XMNQRg 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hWfLcBBMizBroTWtoAsRlA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XG_2yjZ5Ysze2q9tRCt0-w 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1_YlMd0_V6F7vKv5XMNQRg\" alt=\"Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa chỉ đạo tại hội nghị s&aacute;ng 24/12. Ảnh: Trần Đạt\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3984614828189-73a63f4195400e4-8872-3048-1671865727.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1_YlMd0_V6F7vKv5XMNQRg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa ph&aacute;t biểu tại hội nghị. Ảnh:&nbsp;<em>Trần Đạt</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa n&ecirc;u thời gian qua nhiều trường hợp lợi dụng thẻ nh&agrave; b&aacute;o, danh nghĩa b&aacute;o ch&iacute; để vi phạm ph&aacute;p luật. &Ocirc;ng cho biết năm tới sẽ ho&agrave;n thiện hệ thống ph&aacute;p luật li&ecirc;n quan b&aacute;o ch&iacute;; giải quyết điểm nghẽn để quản l&yacute; chặt hơn qu&aacute; tr&igrave;nh quy hoạch b&aacute;o ch&iacute; nhằm tinh gọn bộ m&aacute;y, tập trung x&acirc;y dựng tổ chức Đảng trong c&aacute;c cơ quan b&aacute;o.</p>\r\n<p class=\"Normal\">Tại hội nghị, &ocirc;ng Trần Thanh L&acirc;m đề nghị cơ quan b&aacute;o ch&iacute; cần đẩy nhanh chuyển đổi số, &aacute;p dụng c&ocirc;ng nghệ để tr&aacute;nh tụt hậu. Hiện, nhiều cơ quan b&aacute;o ch&iacute; đ&atilde; đổi mới, đưa th&ocirc;ng tin l&ecirc;n c&aacute;c hạ tầng, nền tảng truyền th&ocirc;ng để tăng khả năng tiếp cận cho người d&acirc;n. Tuy nhi&ecirc;n, đầu tư cơ sở vật chất, kỹ thuật của cơ quan b&aacute;o ch&iacute; chưa theo kịp y&ecirc;u cầu. Tr&igrave;nh độ chuy&ecirc;n m&ocirc;n v&agrave; kỹ năng t&aacute;c nghiệp của kh&ocirc;ng &iacute;t người l&agrave;m b&aacute;o chưa theo kịp ứng dụng c&ocirc;ng nghệ.</p>\r\n<p class=\"Normal\">Việt Nam hiện c&oacute; 127 b&aacute;o v&agrave; 670 tạp ch&iacute;; 72 đ&agrave;i ph&aacute;t thanh, truyền h&igrave;nh. Đến th&aacute;ng 9/2022, doanh thu của c&aacute;c b&aacute;o, tạp ch&iacute; từ quảng c&aacute;o, ph&aacute;t h&agrave;nh l&agrave; 4.700 tỷ đồng, c&ograve;n ng&acirc;n s&aacute;ch nh&agrave; nước cấp 4.800 tỷ đồng. Với đ&agrave;i ph&aacute;t thanh, truyền h&igrave;nh, t&iacute;nh đến 11/2022, tổng kinh ph&iacute; hoạt động l&agrave; 15.092 tỷ đồng, trong đ&oacute; nh&agrave; nước cấp 4.911 tỷ đồng, c&ograve;n lại doanh thu dịch vụ. Nh&acirc;n sự trong lĩnh vực b&aacute;o ch&iacute; khoảng 41.000 người (khối ph&aacute;t thanh, truyền h&igrave;nh khoảng 16.500 người). Trong đ&oacute;, hơn 19.300 trường hợp được cấp thẻ nh&agrave; b&aacute;o.</p>\r\n</article>', 1, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (3, 'Trưởng ban Tuyên giáo: \'Lãnh đạo phải mạnh dạn trả lời báo chí\'', '2022-12-25 10:11:51', 14, 'Lãnh đạo phải mạnh dạn gặp gỡ báo chí, trả lời phỏng vấn để thông tin cho người dân, theo Trưởng ban Tuyên giáo Trung ương Nguyễn Trọng Nghĩa.', '<h1 class=\"title-detail\">Trưởng ban Tuy&ecirc;n gi&aacute;o: \'L&atilde;nh đạo phải mạnh dạn trả lời b&aacute;o ch&iacute;\'</h1>\r\n<p class=\"description\">L&atilde;nh đạo phải mạnh dạn gặp gỡ b&aacute;o ch&iacute;, trả lời phỏng vấn để th&ocirc;ng tin cho người d&acirc;n, theo Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">\"L&atilde;nh đạo phải sẵn s&agrave;ng trả lời b&aacute;o ch&iacute; từ vấn đề thuận lợi tới kh&oacute; khăn. Việc xảy ra đến bước n&agrave;o th&igrave; th&ocirc;ng b&aacute;o ngay tới đ&oacute;, kh&ocirc;ng trốn tr&aacute;nh\", Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa n&oacute;i tại Hội nghị to&agrave;n quốc tổng kết c&ocirc;ng t&aacute;c tuy&ecirc;n gi&aacute;o năm 2022 v&agrave; triển khai nhiệm vụ năm tới, ng&agrave;y 23/12.</p>\r\n<p class=\"Normal\">Theo &ocirc;ng Nghĩa, b&aacute;o ch&iacute; c&oacute; vai tr&ograve; quan trọng trong định hướng tư tưởng. Do đ&oacute;, trong c&ocirc;ng t&aacute;c th&ocirc;ng tin, tuy&ecirc;n truyền, b&aacute;o ch&iacute; phải lu&ocirc;n phải đặt lợi &iacute;ch quốc gia, d&acirc;n tộc l&ecirc;n tr&ecirc;n hết. Năm tới, to&agrave;n ng&agrave;nh sẽ kiện to&agrave;n tổ chức bộ m&aacute;y tuy&ecirc;n gi&aacute;o. Đồng thời, x&acirc;y dựng quy chế l&agrave;m việc nội bộ cũng như giữa ng&agrave;nh tuy&ecirc;n gi&aacute;o với c&aacute;c bộ ng&agrave;nh, đơn vị.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nUGRP1s6G6EZbC6kiAMm9g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jPV3YN8_UmaPLIiTarMsUA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mO5wN5COeRiqmsdIK4hz5g 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nUGRP1s6G6EZbC6kiAMm9g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jPV3YN8_UmaPLIiTarMsUA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mO5wN5COeRiqmsdIK4hz5g 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nUGRP1s6G6EZbC6kiAMm9g\" alt=\"Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa ph&aacute;t biểu tại hội nghị. Ảnh: VGP/Vũ Phong\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/z398182520434129e18e86b0c7b6a5-8814-2731-1671793914.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nUGRP1s6G6EZbC6kiAMm9g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa ph&aacute;t biểu tại hội nghị. Ảnh:&nbsp;<em>VGP</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương đ&aacute;nh gi&aacute; 2023 l&agrave; năm bản lề để thực hiện Nghị quyết Đại hội XIII của Đảng, nhưng bối cảnh nhiều kh&oacute; khăn khi t&igrave;nh h&igrave;nh thế giới, khu vực dự b&aacute;o tiếp tục diễn biến nhanh ch&oacute;ng, phức tạp. Đ&acirc;y cũng l&agrave; năm diễn ra nhiều sự kiện quan trọng như Hội nghị Trung ương 7, 8 kho&aacute; XIII; kỳ họp thứ 5, 6 Quốc hội kh&oacute;a XV; lấy phiếu t&iacute;n nhiệm định kỳ đối với th&agrave;nh vi&ecirc;n l&atilde;nh đạo cấp ủy v&agrave; c&aacute;c cơ quan Đảng, Nh&agrave; nước, Mặt trận Tổ quốc, c&aacute;c đo&agrave;n thể ch&iacute;nh trị - x&atilde; hội.</p>\r\n<p class=\"Normal\">Trước y&ecirc;u cầu đ&oacute;, &ocirc;ng Nghĩa đề nghị c&ocirc;ng t&aacute;c tuy&ecirc;n gi&aacute;o phải đ&aacute;p ứng 4 y&ecirc;u cầu: c&oacute; t&iacute;nh đảng, t&iacute;nh chiến đấu, truyền cảm hứng xuống cơ sở, tạo được sự đồng thuận trong đảng vi&ecirc;n; triển khai to&agrave;n diện, đồng bộ hoạt động tuy&ecirc;n gi&aacute;o nhưng phải c&oacute; trọng t&acirc;m, trọng điểm, c&oacute; kh&acirc;u đột ph&aacute;; đặt quyết t&acirc;m ch&iacute;nh trị cao, nỗ lực lớn, h&agrave;nh động quyết liệt; chủ động, thiết thực, kịp thời, hiệu quả, kh&ocirc;ng để v&ugrave;ng trắng về c&ocirc;ng t&aacute;c tư tưởng.</p>\r\n<p class=\"Normal\">Năm tới, đội ngũ c&aacute;n bộ c&ocirc;ng chức, vi&ecirc;n chức, người lao động trong to&agrave;n ng&agrave;nh tuy&ecirc;n gi&aacute;o được y&ecirc;u cầu tập trung n&acirc;ng cao chất lượng tham mưu với ba nội dung gồm: c&ocirc;ng t&aacute;c x&acirc;y dựng đảng về ch&iacute;nh trị, tư tưởng, đạo đức; thực hiện c&aacute;c nh&oacute;m nhiệm vụ trọng t&acirc;m; v&agrave; trong lĩnh vực khoa gi&aacute;o của Đảng.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Ph&oacute; thường trực ban Tuy&ecirc;n gi&aacute;o Trung ương Lại Xu&acirc;n M&ocirc;n cho biết năm 2022 ng&agrave;nh tuy&ecirc;n gi&aacute;o đạt được nhiều kết quả như ho&agrave;n th&agrave;nh đ&uacute;ng tiến độ 22 đề &aacute;n lớn, kh&oacute;, nhạy cảm, chưa c&oacute; tiền lệ; tham mưu tổ chức tốt Hội nghị nghi&ecirc;n cứu, học tập, qu&aacute;n triệt văn kiện Hội nghị Trung ương 5, 6 kh&oacute;a XIII; tăng cường kiểm tra, gi&aacute;m s&aacute;t; l&agrave;m tốt c&ocirc;ng t&aacute;c bảo vệ nền tư tưởng của Đảng, định hướng dư luận x&atilde; hội, b&aacute;o ch&iacute; - xuất bản v&agrave; tổ chức nhiều sự kiện lớn so với năm 2021.</p>\r\n<p class=\"Normal\">Theo &ocirc;ng M&ocirc;n, năm 2022, mặc d&ugrave; bối cảnh quốc tế, khu vực c&oacute; nhiều biến động, song ng&agrave;nh tuy&ecirc;n gi&aacute;o đ&atilde; thể hiện r&otilde; vai tr&ograve; \"đi trước mở đường, đi c&ugrave;ng thực hiện, đi sau tổng kết v&agrave; l&agrave; vai tr&ograve; ti&ecirc;n phong tr&ecirc;n mặt trận tư tưởng của Đảng\".</p>\r\n</article>\r\n<p>&nbsp;</p>', 1, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (4, 'Bắc Ninh kỷ luật hàng loạt cán bộ', '2022-12-20 10:13:09', 42, 'Hàng loạt cán bộ ở Bắc Ninh bị kỷ luật, trong đó Giám đốc Sở Y tế Tô Thị Mai Hoa bị khiển trách, Giám đốc Bảo hiểm xã hội Phạm Đức Cường bị khai trừ Đảng. ', '<h1 class=\"title-detail\">Bắc Ninh kỷ luật h&agrave;ng loạt c&aacute;n bộ</h1>\r\n<p class=\"description\">H&agrave;ng loạt c&aacute;n bộ ở Bắc Ninh bị kỷ luật, trong đ&oacute; Gi&aacute;m đốc Sở Y tế T&ocirc; Thị Mai Hoa bị khiển tr&aacute;ch, Gi&aacute;m đốc Bảo hiểm x&atilde; hội Phạm Đức Cường bị khai trừ Đảng.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 16 v&agrave; 19/12, Ban Thường vụ Tỉnh ủy v&agrave; Ban Chấp h&agrave;nh Đảng bộ tỉnh Bắc Ninh họp xem x&eacute;t, kỷ luật một số tổ chức Đảng v&agrave; c&aacute; nh&acirc;n vi phạm.</p>\r\n<p class=\"Normal\">Ban Chấp h&agrave;nh Đảng bộ tỉnh thống nhất&nbsp;<strong>khiển tr&aacute;ch</strong>&nbsp;b&agrave;<strong>&nbsp;</strong>T&ocirc; Thị Mai Hoa, B&iacute; thư Đảng ủy, Gi&aacute;m đốc Sở Y tế Bắc Ninh v&agrave; xin &yacute; kiến Ủy ban Kiểm tra Trung ương.</p>\r\n<p class=\"Normal\">Theo kết luận của Ủy ban Kiểm tra Trung ương, b&agrave; Hoa chịu tr&aacute;ch nhiệm ch&iacute;nh, tr&aacute;ch nhiệm người đứng đầu về những vi phạm, khuyết điểm của Đảng ủy Sở Y tế nhiệm kỳ 2020-2025, Sở Y tế, Trung t&acirc;m Kiểm so&aacute;t bệnh tật (CDC) v&agrave; đơn vị trực thuộc.</p>\r\n<p class=\"Normal\">B&agrave; Hoa c&oacute; vi phạm, khuyết điểm trong việc k&yacute; c&aacute;c quyết định ph&ecirc; duyệt dự to&aacute;n mua sắm trang thiết bị, vật tư; kh&ocirc;ng chỉ đạo CDC tỉnh b&aacute;o c&aacute;o v&agrave; b&aacute;o c&aacute;o UBND tỉnh, kh&ocirc;ng l&atilde;nh đạo, kiểm tra gi&aacute;m s&aacute;t c&aacute;c nguồn hỗ trợ của c&aacute;c tổ chức, c&aacute; nh&acirc;n phục vụ ph&ograve;ng, chống dịch Covid-19. Những vi phạm n&ecirc;u tr&ecirc;n \"đến mức phải xem x&eacute;t kỷ luật\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FggcCITv-VrXAx7A3mgxjw 1x, https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WDGjeVissurl-WYE5lzmxA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=D3zeV407CXC4oj6bTY_Vog 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FggcCITv-VrXAx7A3mgxjw 1x, https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WDGjeVissurl-WYE5lzmxA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=D3zeV407CXC4oj6bTY_Vog 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FggcCITv-VrXAx7A3mgxjw\" alt=\"B&agrave; T&ocirc; Thị Mai Hoa, Gi&aacute;m đốc Sở Y tế Bắc Ninh. Ảnh: Ngọc Th&agrave;nh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/22/mrs-Hoa-Bac-Ninh-1-8766-151126-8400-2718-1671689291.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FggcCITv-VrXAx7A3mgxjw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">B&agrave; T&ocirc; Thị Mai Hoa, Gi&aacute;m đốc Sở Y tế Bắc Ninh. Ảnh:&nbsp;<em>Ngọc Th&agrave;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ban Chấp h&agrave;nh Đảng bộ tỉnh Bắc Ninh&nbsp;<strong>đề nghị cấp c&oacute; thẩm quyền kỷ luật</strong>&nbsp;&ocirc;ng Nguyễn Xu&acirc;n Thanh, Gi&aacute;m đốc Sở T&agrave;i nguy&ecirc;n M&ocirc;i trường v&agrave; &ocirc;ng Nguyễn Văn Hải, nguy&ecirc;n B&iacute; thư Đảng ủy, nguy&ecirc;n Gi&aacute;m đốc Sở T&agrave;i ch&iacute;nh.</p>\r\n<p class=\"Normal\">&Ocirc;ng Thanh bị x&aacute;c định sai phạm tại một số sự &aacute;n d&acirc;n cư dịch vụ phường Đồng Nguy&ecirc;n, TP Từ Sơn, khi l&agrave;m Trưởng ph&ograve;ng T&agrave;i nguy&ecirc;n M&ocirc;i trường, Ph&oacute; chủ tịch thị x&atilde;, Ph&oacute; b&iacute; thư Thị ủy Từ Sơn. &Ocirc;ng chịu tr&aacute;ch nhiệm trong việc k&yacute; văn bản b&agrave;n giao đất, trả tiền bồi thường kh&ocirc;ng đ&uacute;ng quy định; kh&ocirc;ng kịp thời nắm bắt t&igrave;nh h&igrave;nh, kh&ocirc;ng b&aacute;o c&aacute;o Thường trực Thị ủy, Ban Thường vụ Thị Ủy; thiếu l&atilde;nh đạo, chỉ đạo, kiểm tra, gi&aacute;m s&aacute;t, k&yacute; văn bản li&ecirc;n quan đến 79 l&ocirc; đất tại dự &aacute;n d&acirc;n cư dịch vụ Đồng Sen kh&ocirc;ng đ&uacute;ng quy định.</p>\r\n<p class=\"Normal\">&Ocirc;ng Thanh đ&atilde; bị Cơ quan an ninh điều tra C&ocirc;ng an tỉnh Bắc Ninh khởi tố, tạm giam về tội Lợi dụng chức vụ, quyền hạn trong khi thi h&agrave;nh c&ocirc;ng vụ.</p>\r\n<p class=\"Normal\">&Ocirc;ng Hải th&igrave; bị x&aacute;c định thực hiện kh&ocirc;ng đ&uacute;ng c&aacute;c quy định trong việc x&aacute;c định gi&aacute; khởi điểm đối với 79 l&ocirc; đất tại dự &aacute;n Đồng Sen, hiện đ&atilde; bị bắt.</p>\r\n<p class=\"Normal\">&Ocirc;ng Trần Quang Huy, nguy&ecirc;n B&iacute; thư Thị ủy, Chủ tịch HĐND thị x&atilde; Từ Sơn bị&nbsp;<strong>cảnh c&aacute;o</strong>&nbsp;v&igrave; thiếu kiểm tra, gi&aacute;m s&aacute;t việc thực hiện c&aacute;c dự &aacute;n d&acirc;n cư dịch vụ tr&ecirc;n địa b&agrave;n, để cấp dưới vi phạm m&agrave; chưa c&oacute; biện ph&aacute;p xử l&yacute;, ngăn chặn, dẫn đến nhiều tổ chức, c&aacute; nh&acirc;n phải xử l&yacute; cả về Đảng, ch&iacute;nh quyền v&agrave; h&igrave;nh sự.</p>\r\n<p class=\"Normal\">Li&ecirc;n quan đến c&aacute;c sai phạm n&oacute;i tr&ecirc;n, Ban Thường vụ Tỉnh ủy cũng quyết định&nbsp;<strong>khai trừ Đảng</strong>&nbsp;với c&aacute;c &ocirc;ng Nguyễn Văn Quỹ (B&iacute; thư Đảng ủy, Ch&aacute;nh Văn ph&ograve;ng Đo&agrave;n đại biểu Quốc hội v&agrave; HĐND tỉnh, nguy&ecirc;n Ph&oacute; b&iacute; thư Thị ủy, nguy&ecirc;n Chủ tịch thị x&atilde; Từ Sơn), Mầu Quang Thắng (Ph&oacute; ban quản l&yacute; c&aacute;c Khu c&ocirc;ng nghiệp Bắc Ninh, nguy&ecirc;n Ph&oacute; ch&aacute;nh Văn ph&ograve;ng UBND tỉnh), Phạm Quốc Tuấn (Ph&oacute; gi&aacute;m đốc Sở T&agrave;i ch&iacute;nh), Nguyễn Thế Tuấn (Ph&oacute; chủ tịch Thường trực th&agrave;nh phố Từ Sơn, nguy&ecirc;n Trưởng ph&ograve;ng T&agrave;i ch&iacute;nh - Kế hoạch thị x&atilde; Từ Sơn), Nguyễn Ngọc Sơn (Ph&oacute; gi&aacute;m đốc Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường).</p>\r\n<p class=\"Normal\">Đồng thời, Ban Thường vụ Tỉnh ủy khai trừ Đảng với &ocirc;ng Phạm Đức Cường, B&iacute; thư Đảng ủy, Gi&aacute;m đốc Bảo hiểm x&atilde; hội tỉnh; &ocirc;ng Phạm Hồng &Aacute;nh, Ph&oacute; gi&aacute;m đốc Bảo hiểm x&atilde; hội tỉnh. Hai người n&agrave;y đ&atilde; bị bắt.</p>\r\n</article>\r\n<p>&nbsp;</p>', 1, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (5, 'Phát hiện 10 quả lựu đạn khi sửa vỉa hè', '2022-12-08 10:14:47', 1000, 'ĐÀ NẴNG - Đào xới cải tạo vỉa hè đường Nguyễn Duy Hiệu, quận Sơn Trà, nhóm công nhân bất ngờ phát hiện 10 quả lựu đạn trước số nhà 21.', '<h1 class=\"title-detail\">Ph&aacute;t hiện 10 quả lựu đạn khi sửa vỉa h&egrave;</h1>\r\n<p class=\"description\"><span class=\"location-stamp\">Đ&Agrave; NẴNG</span>Đ&agrave;o xới cải tạo vỉa h&egrave; đường Nguyễn Duy Hiệu, quận Sơn Tr&agrave;, nh&oacute;m c&ocirc;ng nh&acirc;n bất ngờ ph&aacute;t hiện 10 quả lựu đạn trước số nh&agrave; 21.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 24/12, ngay khi sự việc được tr&igrave;nh b&aacute;o, Ban chỉ huy Qu&acirc;n sự phường An Hải Đ&ocirc;ng v&agrave; quận Sơn Tr&agrave; đ&atilde; phong tỏa hiện trường, thu gom to&agrave;n bộ số lựu đạn, lập bi&ecirc;n bản đưa đi ti&ecirc;u hủy.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VpgHisrX-6Q_LbHsEZ93rA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ofgQKMDumGysngYtLeeAmw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sbXjays0X8dhOYeh5wnUVw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VpgHisrX-6Q_LbHsEZ93rA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ofgQKMDumGysngYtLeeAmw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sbXjays0X8dhOYeh5wnUVw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VpgHisrX-6Q_LbHsEZ93rA\" alt=\"Số lựu đạn được nhận định c&oacute; thể bị s&oacute;t lại sau chiến tranh. Ảnh: Nam Sơn\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/z3985049177052-d065e359c8e4514-5056-2431-1671882753.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VpgHisrX-6Q_LbHsEZ93rA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Số lựu đạn được nhận định c&oacute; thể bị s&oacute;t lại sau chiến tranh. Ảnh:&nbsp;<em>Nam Sơn</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">\"Nhiều khả năng số lựu đạn n&agrave;y s&oacute;t lại sau chiến tranh, b&ecirc;n ngo&agrave;i đ&atilde; rỉ s&eacute;t theo thời gian\", đại diện Ban chỉ huy Qu&acirc;n sự quận Sơn Tr&agrave; cho hay.</p>\r\n<p class=\"Normal\">Tuyến đường Nguyễn Duy Hiệu nằm ở khu vực đ&ocirc;ng d&acirc;n cư, gần cầu Nguyễn Văn Trỗi bắc qua s&ocirc;ng H&agrave;n, do qu&acirc;n đội Mỹ x&acirc;y dựng để chuy&ecirc;n chở kh&iacute; t&agrave;i chiến tranh từ cảng Ti&ecirc;n Sa v&agrave;o nội đ&ocirc; Đ&agrave; Nẵng.</p>\r\n</article>\r\n<p>&nbsp;</p>', 2, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (6, 'Sài Gòn xuống 19 độ C trong ngày Giáng sinh', '2022-12-25 10:16:02', 190, 'Không khí lạnh tăng cường từ phía Bắc khiến nhiệt độ ở Sài Gòn sáng sớm còn 19 độ, thấp nhất từ đầu năm, các tỉnh Nam Bộ có nơi hơn 14 độ C.', '<p class=\"description\">Kh&ocirc;ng kh&iacute; lạnh tăng cường từ ph&iacute;a Bắc khiến nhiệt độ ở S&agrave;i G&ograve;n s&aacute;ng sớm c&ograve;n 19 độ, thấp nhất từ đầu năm, c&aacute;c tỉnh Nam Bộ c&oacute; nơi hơn 14 độ C.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MtLKGwFo3lKk4omwMcDI0w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=khSVt6AehA00Tn1ryNxMdQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=-mIcu_sMEP5lrq7iom1aBw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MtLKGwFo3lKk4omwMcDI0w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=khSVt6AehA00Tn1ryNxMdQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=-mIcu_sMEP5lrq7iom1aBw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MtLKGwFo3lKk4omwMcDI0w\" alt=\"Người l&aacute;i xe m&aacute;y luồng tay v&agrave;o ống &aacute;o kho&aacute;c khi dừng đ&egrave;n đỏ để giảm &ecirc; buốt, s&aacute;ng 24/12. Ảnh: Đ&igrave;nh Văn\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5124-JPG-5957-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MtLKGwFo3lKk4omwMcDI0w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Người l&aacute;i xe m&aacute;y luồn tay v&agrave;o ống &aacute;o kho&aacute;c khi dừng đ&egrave;n đỏ, s&aacute;ng 24/12. Ảnh:&nbsp;<em>Đ&igrave;nh Văn</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">S&aacute;ng 24/12, thời tiết TP HCM se lạnh, gi&oacute; l&ugrave;a khiến nhiều người phải mặc nhiều lớp &aacute;o, găng tay, qu&agrave;ng cổ khi đi tr&ecirc;n đường. Khi đến c&aacute;c đoạn đ&egrave;n đỏ, họ tranh thủ luồn b&agrave;n tay v&agrave;o ống &aacute;o kho&aacute;c, khoanh trước ngực để giữ ấm. Nhiều người b&aacute;n h&agrave;ng rong, uống c&agrave; ph&ecirc; ven đường ngồi co ro nhưng tỏ ra h&agrave;o hứng v&igrave; \"trời lạnh đ&uacute;ng ng&agrave;y Noel\".</p>\r\n<p class=\"Normal\">\"T&ocirc;i thường dậy sớm để b&aacute;n b&aacute;nh m&igrave; nhưng h&ocirc;m nay thấy lạnh hơn mọi ng&agrave;y rất nhiều\", anh Sang, 39 tuổi, n&oacute;i.</p>\r\n<p class=\"Normal\">Trung t&acirc;m dự b&aacute;o kh&iacute; tượng thủy văn Nam Bộ cho biết nhiệt độ thấp nhất đo được tại TP HCM s&aacute;ng nay l&agrave; 19 độ C ở trạm T&acirc;n Sơn Nhất. Tại c&aacute;c tỉnh Nam Nam Bộ, Đồng Xo&agrave;i (B&igrave;nh Phước) gần 18 độ C, B&igrave;nh Dương 18,5 độ C, thấp nhất l&agrave; T&agrave; L&agrave;i (Đồng Nai) 14,5 độ C. C&aacute;c tỉnh miền T&acirc;y 19-21 độ C.</p>\r\n<p class=\"Normal\">B&agrave; L&ecirc; Thị Xu&acirc;n Lan, nguy&ecirc;n ph&oacute; ph&ograve;ng Dự b&aacute;o, Đ&agrave;i kh&iacute; tượng thuỷ văn khu vực Nam Bộ, cho biết kh&ocirc;ng kh&iacute; lạnh s&aacute;ng nay ở TP HCM giảm 1-2 độ so với nhiều đợt lạnh từ đầu th&aacute;ng 12. \"Năm nay trời lạnh đ&uacute;ng ng&agrave;y Gi&aacute;ng sinh l&agrave; hiếm v&igrave; những dịp trước thường nắng n&oacute;ng\", b&agrave; Lan n&oacute;i. Theo chuy&ecirc;n gia, từ nay đến Tết Nguy&ecirc;n đ&aacute;n th&agrave;nh phố sẽ ghi nhận &iacute;t nhất 3 đợt lạnh, dự b&aacute;o đợt tiếp theo v&agrave;o ng&agrave;y 29/12, nhiệt độ sẽ giảm mạnh hơn nữa.</p>\r\n<p class=\"Normal\">Nhiệt độ ch&ecirc;nh lệch lớn giữa s&aacute;ng sớm v&agrave; trưa (khoảng 12-13 độ C) n&ecirc;n người d&acirc;n cần giữ ấm cơ thể khi ra đường, đặc biệt trẻ em, người lớn tuổi. Người lao động l&agrave;m việc l&acirc;u ngo&agrave;i trời dễ bị mệt mỏi, cần uống nhiều nước.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wYBDWoGjzX-nqkm7LW-ChA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GKXoKFqBjhtKSdIyEJ6G8A 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=71iMzEuBfFEzE7w8GC5fUg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wYBDWoGjzX-nqkm7LW-ChA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GKXoKFqBjhtKSdIyEJ6G8A 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=71iMzEuBfFEzE7w8GC5fUg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wYBDWoGjzX-nqkm7LW-ChA\" alt=\"Người d&acirc;n ngồi co ro tr&ecirc;n vỉa h&egrave; khi trời se lạnh. Ảnh: Đ&igrave;nh Văn\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/IMG-5104-JPG-8136-1671842979.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=wYBDWoGjzX-nqkm7LW-ChA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Người d&acirc;n ngồi co ro tr&ecirc;n vỉa h&egrave; khi trời se lạnh. Ảnh:&nbsp;<em>Đ&igrave;nh Văn</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">TP HCM từng xuống 17 độ C v&agrave;o cuối năm 2009. Những năm sau đ&oacute;, nhiệt độ thấp nhất mỗi năm phổ biến mức 18 độ C v&agrave;o đầu năm 2015, 2016... Nhiệt độ thấp nhất được ghi nhận ở TP HCM l&agrave; 16,4 độ C được ghi nhận v&agrave;o ng&agrave;y 25/12/1999 đo ở trạm T&acirc;n Sơn H&ograve;a.</p>\r\n</article>', 2, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (7, 'Tỷ lệ đất xây bãi đỗ xe \'rất thấp so với quy chuẩn\'', '2022-12-03 10:17:21', 19, 'Bộ Xây dựng cho biết tỷ lệ đất xây dựng bến, bãi đỗ xe thực tế trên đất xây dựng đô thị rất thấp so với quy chuẩn, yêu cầu của các đồ án quy hoạch.', '<p class=\"description\">Bộ X&acirc;y dựng cho biết tỷ lệ đất x&acirc;y dựng bến, b&atilde;i đỗ xe thực tế tr&ecirc;n đất x&acirc;y dựng đ&ocirc; thị rất thấp so với quy chuẩn, y&ecirc;u cầu của c&aacute;c đồ &aacute;n quy hoạch.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Cử tri TP Đ&agrave; Nẵng mới đ&acirc;y gửi Bộ X&acirc;y dựng kiến nghị quy hoạch b&atilde;i đỗ xe khi x&acirc;y dựng c&aacute;c t&ograve;a nh&agrave; cao tầng v&igrave; đ&acirc;y l&agrave; nỗi nhức nhối ở c&aacute;c th&agrave;nh phố lớn, ảnh hưởng nghi&ecirc;m trọng đến giao th&ocirc;ng.</p>\r\n<p class=\"Normal\">Trả lời vấn đề n&agrave;y, đại diện Bộ X&acirc;y dựng cho biết kết quả khảo s&aacute;t tại c&aacute;c địa phương cho thấy thực trạng ph&aacute;t triển b&atilde;i đỗ xe c&ograve;n nhiều bất cập. Số lượng b&atilde;i đỗ trong đ&ocirc; thị, đặc biệt tại c&aacute;c khu vực trung t&acirc;m như H&agrave; Nội, TP HCM, Đ&agrave; Nẵng... thiếu nhiều so với nhu cầu của người d&acirc;n.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QNbpt2QnN588SzXcUqgrCA 1x, https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2g5GTPdPEtqNrpLVq0AwbA 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=EWHUewpaWLpf3ja7X9Aikw 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QNbpt2QnN588SzXcUqgrCA 1x, https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2g5GTPdPEtqNrpLVq0AwbA 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=EWHUewpaWLpf3ja7X9Aikw 2x\"><img class=\"lazy loading\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QNbpt2QnN588SzXcUqgrCA\" alt=\"Xe đỗ k&iacute;n hai b&ecirc;n lề đường, khiến diện t&iacute;ch l&ograve;ng đường bị thu hẹp, tr&ecirc;n phố Đinh N&uacute;p, Cầu Giấy, H&agrave; Nội, s&aacute;ng 16/9. Ảnh: Phạm Nga\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/12/23/bai-do-xe-1671770716-3029-1671770736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QNbpt2QnN588SzXcUqgrCA\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Xe đỗ k&iacute;n hai b&ecirc;n lề đường, khiến diện t&iacute;ch l&ograve;ng đường bị thu hẹp, tr&ecirc;n phố Đinh N&uacute;p, Cầu Giấy, H&agrave; Nội, s&aacute;ng 16/9. Ảnh:&nbsp;<em>Phạm Nga</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đơn cử như tại H&agrave; Nội, số liệu của Sở Giao th&ocirc;ng Vận tải cho thấy c&oacute; khoảng 6,9 triệu phương tiện giao th&ocirc;ng (&ocirc;t&ocirc;, xe m&aacute;y), chưa kể lượng xe ngoại tỉnh ra v&agrave;o th&agrave;nh phố h&agrave;ng ng&agrave;y. Trong khi đ&oacute;, tổng diện t&iacute;ch giao th&ocirc;ng tĩnh l&agrave; hơn 90 ha, chỉ đ&aacute;p ứng được 8-10% nhu cầu đỗ xe. Điều n&agrave;y khiến c&aacute;c điểm đỗ xe tr&aacute;i ph&eacute;p, sai quy hoạch mọc l&ecirc;n.</p>\r\n<p class=\"Normal\">Ở TP HCM, &aacute;p lực thiếu b&atilde;i đỗ xe, đặc biệt l&agrave; cho &ocirc;t&ocirc; ở khu vực trung t&acirc;m cũng rất lớn khi lượng xe th&agrave;nh phố quản l&yacute; l&agrave; hơn 850.000 chiếc c&ugrave;ng hơn 7,8 triệu xe m&aacute;y, chưa t&iacute;nh xe v&atilde;ng lai. Tuy nhi&ecirc;n, hệ thống bến b&atilde;i ở th&agrave;nh phố hiện chỉ đạt khoảng 20% quy hoạch, thiếu hơn 900 ha so với chỉ ti&ecirc;u gần 1.200 ha sau năm 2020.</p>\r\n<p class=\"Normal\">Theo Bộ X&acirc;y dựng, nguy&ecirc;n nh&acirc;n thiếu b&atilde;i đỗ xe chủ yếu l&agrave; do kh&oacute; khăn trong giải ph&oacute;ng mặt bằng, nhất l&agrave; ở c&aacute;c khu vực trung t&acirc;m. B&ecirc;n cạnh đ&oacute;, ch&iacute;nh s&aacute;ch về đầu tư, gi&aacute;, ph&iacute; trong đầu tư, x&acirc;y dựng, khai th&aacute;c chưa thực sự khuyến kh&iacute;ch th&uacute;c đẩy sự ph&aacute;t triển của bến b&atilde;i.</p>\r\n<p class=\"Normal\">C&aacute;c văn bản ph&aacute;p luật hiện nay đ&atilde; h&igrave;nh th&agrave;nh h&agrave;nh lang ph&aacute;p l&yacute; tương đối đầy đủ, l&agrave; cơ sở cho đầu tư x&acirc;y dựng bến, b&atilde;i đỗ xe trong đ&ocirc; thị. Đơn cử như những nội dung quy định bắt buộc về s&acirc;n, b&atilde;i đỗ, đặc biệt với c&aacute;c khu vực trung t&acirc;m, nơi ph&aacute;t sinh nhu cầu đỗ xe lớn như t&ograve;a nh&agrave; chung cư, trung t&acirc;m thương mại, c&ocirc;ng sở...</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Bộ X&acirc;y dựng đ&atilde; ban h&agrave;nh nhiều văn bản quy định về diện t&iacute;ch chỗ để xe, bao gồm &ocirc;t&ocirc;, xe m&aacute;y, xe m&aacute;y điện, xe đạp, để đ&aacute;p ứng nhu cầu đỗ xe tại nhiều điểm ph&aacute;t sinh nhu cầu lớn như t&ograve;a nh&agrave; cao tầng, chung cư. Trong qu&aacute; tr&igrave;nh x&acirc;y dựng luật Quản l&yacute; v&agrave; ph&aacute;t triển đ&ocirc; thị, luật Quy hoạch đ&ocirc; thị v&agrave; n&ocirc;ng th&ocirc;n, Bộ sẽ hướng x&acirc;y dựng quy định y&ecirc;u cầu c&aacute;c t&ograve;a nh&agrave; cao tầng phải c&oacute; giải ph&aacute;p về b&atilde;i đậu đỗ xe ph&ugrave; hợp thực tế.</p>\r\n</article>\r\n<p>&nbsp;</p>', 2, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (8, 'Khởi công hai dự án gần 16.000 tỷ đồng ở Tân Sơn Nhất', '2022-09-21 10:19:08', 999, 'TP HCM - Ga T3 Tân Sơn Nhất cùng tuyến đường nối dài 4 km, tổng vốn gần 16.000 tỷ đồng được xây dựng giúp sân bay khai thác 50 triệu khách mỗi năm, giảm ùn tắc cho khu vực.', '<p class=\"description\">Ga T3 T&acirc;n Sơn Nhất c&ugrave;ng tuyến đường nối d&agrave;i 4 km, tổng vốn gần 16.000 tỷ đồng được x&acirc;y dựng gi&uacute;p s&acirc;n bay khai th&aacute;c 50 triệu kh&aacute;ch mỗi năm, giảm &ugrave;n tắc cho khu vực.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Hai dự &aacute;n được khởi c&ocirc;ng chiều 24/12, dự kiến ho&agrave;n th&agrave;nh sau hai năm, gi&uacute;p giảm tải cho khu vực&nbsp;<a href=\"https://vnexpress.net/chu-de/san-bay-tan-son-nhat-4744\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4551970&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-SanBayTanSonNhat&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">s&acirc;n bay T&acirc;n Sơn Nhất</a>&nbsp;- đầu mối vận tải h&agrave;ng kh&ocirc;ng lớn nhất về diện t&iacute;ch, c&ocirc;ng suất nh&agrave; ga c&ugrave;ng lượng kh&aacute;ch trong cả nước.</p>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/thu-tuong-giao-acv-dau-tu-nha-ga-t3-tan-son-nhat-4101784.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4551970&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-GaT3&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Ga T3</a>&nbsp;s&acirc;n bay T&acirc;n Sơn Nhất được đầu tư với kinh ph&iacute; gần 11.000 tỷ đồng, từ nguồn vốn của Tổng c&ocirc;ng ty h&agrave;ng kh&ocirc;ng (ACV). Nh&agrave; ga được x&acirc;y dựng với c&ocirc;ng suất 20 triệu lượt kh&aacute;ch mỗi năm, c&ugrave;ng c&aacute;c c&ocirc;ng tr&igrave;nh phụ trợ đồng bộ. C&ocirc;ng tr&igrave;nh được thiết kế đ&aacute;p ứng ti&ecirc;u chuẩn s&acirc;n bay quốc tế về an to&agrave;n, chất lượng dịch vụ; đảm bảo kh&aacute;ch đi lại thuận tiện cũng như được tiếp cận nhiều tiện nghi, khu mua sắm...</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7sTfWOpFeyZGbdL1nS84sg 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kGHSmAh4AZzzJwfTnNpqtQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=46Y4_XiiaxOlwbLGRTVaEg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7sTfWOpFeyZGbdL1nS84sg 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kGHSmAh4AZzzJwfTnNpqtQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=46Y4_XiiaxOlwbLGRTVaEg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7sTfWOpFeyZGbdL1nS84sg\" alt=\"Phối cảnh nh&agrave; ga T3 s&acirc;n bay T&acirc;n Sơn Nhất. Ảnh: ACV\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/9493-1665406476-jpeg-1653-1671-1680-5224-1671813732.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7sTfWOpFeyZGbdL1nS84sg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Phối cảnh nh&agrave; ga T3 s&acirc;n bay T&acirc;n Sơn Nhất. Ảnh:&nbsp;<em>ACV</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trước đ&oacute;, Hội đồng thẩm định chọn&nbsp;<a href=\"https://vnexpress.net/thiet-ke-nha-ga-t3-tan-son-nhat-lay-y-tuong-ao-dai-4487669.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4551970&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-KienTrucGaT3&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">kiến tr&uacute;c ga T3</a>&nbsp;từ &yacute; tưởng &aacute;o d&agrave;i truyền thống - một trong biểu tượng văn h&oacute;a của Việt Nam. Nh&agrave; ga c&oacute; lớp m&aacute;i cong, trải d&agrave;i đến vườn trung t&acirc;m của c&ocirc;ng tr&igrave;nh phức hợp thương mại, văn ph&ograve;ng. Tại khu phức hợp thương mại, văn ph&ograve;ng trong nh&agrave; ga, việc thiết kế cũng hướng đến kiến tr&uacute;c xanh, t&iacute;ch hợp c&aacute;c yếu tố tự nhi&ecirc;n v&agrave; cảnh quan, tạo m&ocirc;i trường th&acirc;n thiện...</p>\r\n<p class=\"Normal\">Dự &aacute;n ga T3 được Ch&iacute;nh phủ duyệt chủ trương đầu tư hai năm trước, nhằm n&acirc;ng c&ocirc;ng suất khai th&aacute;c ở s&acirc;n bay T&acirc;n Sơn Nhất l&ecirc;n 50 triệu kh&aacute;ch mỗi năm, c&ugrave;ng với hai nh&agrave; ga T1 v&agrave; T2 hiện hữu. C&ocirc;ng tr&igrave;nh trước đ&oacute; dự t&iacute;nh khởi c&ocirc;ng năm 2021, song chưa triển khai do vướng mắc về giải ph&oacute;ng mặt bằng, giao đất quốc ph&ograve;ng, đổi mục đ&iacute;ch sử dụng đất...</p>\r\n<p class=\"Normal\">Để kết nối đồng bộ với ga T3,&nbsp;<a href=\"https://vnexpress.net/6-du-an-giam-ket-xe-khu-vuc-tan-son-nhat-4180291.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4551970&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-DuAn&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">dự &aacute;n&nbsp;</a><strong>đường nối Trần Quốc Ho&agrave;n - Cộng Ho&agrave;</strong>, d&agrave;i 4 km, cũng được TP HCM triển khai với kinh ph&iacute; hơn 4.800 tỷ đồng. Chiều nay, th&agrave;nh phố khởi c&ocirc;ng trước g&oacute;i thầu x&acirc;y lắp số 9 (hầm chui n&uacute;t giao Trần Quốc Ho&agrave;n - Phan Th&uacute;c Duyện), sang năm sẽ thực hiện đồng loạt c&aacute;c hạng mục kh&aacute;c khi mặt bằng được b&agrave;n giao.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wq9vjloKEgPJInTLjOSI6g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=061onBng32GSdfc35VyzGw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=F2fkhqdx1HUOOgoixvsYvg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wq9vjloKEgPJInTLjOSI6g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=061onBng32GSdfc35VyzGw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=F2fkhqdx1HUOOgoixvsYvg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wq9vjloKEgPJInTLjOSI6g\" alt=\"Lộ tr&igrave;nh đường nối Trần Quốc Ho&agrave;n - Cộng Ho&agrave;. Đồ hoạ: Tuấn Việt\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/6862-1665988208-4678-1671790967.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wq9vjloKEgPJInTLjOSI6g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Lộ tr&igrave;nh đường nối Trần Quốc Ho&agrave;n - Cộng Ho&agrave;. Đồ hoạ:&nbsp;<em>Tuấn Việt</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Tuyến đường nối n&ecirc;u tr&ecirc;n được TP HCM x&acirc;y mới, điểm đầu ở n&uacute;t giao Trần Quốc Ho&agrave;n - Phan Th&uacute;c Duyện; điểm cuối tại đoạn giao giữa c&aacute;c đường C12 - Cộng Ho&agrave; - Trường Chinh. C&ocirc;ng tr&igrave;nh l&agrave;m tuyến ch&iacute;nh với chiều rộng 25-48 m cho 6 l&agrave;n xe, vận tốc 50 km/h, c&ugrave;ng hai đường nh&aacute;nh kết nối với quy m&ocirc; 3-4 l&agrave;n. Tr&ecirc;n tuyến c&oacute; một cầu cạn d&agrave;i gần một km, 4 l&agrave;n xe, c&ugrave;ng hai hầm chui ở c&aacute;c n&uacute;t giao, mỗi hầm cho 2 l&agrave;n xe.</p>\r\n<p class=\"Normal\">Dự &aacute;n đường nối Trần Quốc Ho&agrave;n - Cộng H&ograve;a được TP HCM quyết định chủ trương đầu tư từ năm 2016, quy m&ocirc; 4 l&agrave;n xe, tổng mức đầu tư hơn 1.400 tỷ đồng nhưng chưa triển khai. Sau khi ga T3 được đưa v&agrave;o quy hoạch, TP HCM điều chỉnh quy m&ocirc; dự &aacute;n đường nối lớn hơn, để đồng bộ về hạ tầng. Hiện, tuyến đường nối được th&agrave;nh phố đặt mục ti&ecirc;u ho&agrave;n th&agrave;nh th&aacute;ng 8/2024, ngo&agrave;i gi&uacute;p kết nối đồng bộ với nh&agrave; ga T3 c&ograve;n g&oacute;p phần giảm &ugrave;n tắc cho cửa ng&otilde; T&acirc;n Sơn Nhất, ho&agrave;n thiện hệ thống tại quận T&acirc;n B&igrave;nh...</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Svk5IVAP4RpzFPLnxMkK4g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w-cgZgRHCxBQV3g7G2jnBA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dw1UcQWNKwK5yBjubtPaww 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Svk5IVAP4RpzFPLnxMkK4g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w-cgZgRHCxBQV3g7G2jnBA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dw1UcQWNKwK5yBjubtPaww 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Svk5IVAP4RpzFPLnxMkK4g\" alt=\"Kẹt xe tr&ecirc;n đường Trường Sơn trước cổng s&acirc;n bay T&acirc;n Sơn Nhất, năm 2020. Ảnh: Gia Minh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-truong-son-7349-16146153-8900-5980-1671790967.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Svk5IVAP4RpzFPLnxMkK4g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Kẹt xe tr&ecirc;n đường Trường Sơn trước cổng s&acirc;n bay T&acirc;n Sơn Nhất, năm 2020. Ảnh:&nbsp;<em>Gia Minh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Quy hoạch đến năm 2020, s&acirc;n bay T&acirc;n Sơn Nhất c&ocirc;ng suất 25 triệu lượt kh&aacute;ch mỗi năm, nhưng từ 2017 đ&atilde; đ&oacute;n gần 40 triệu lượt v&agrave; li&ecirc;n tục trong khoảng n&agrave;y c&aacute;c năm gần đ&acirc;y, khiến nơi đ&acirc;y thường xuy&ecirc;n qu&aacute; tải, nhất l&agrave; dịp lễ Tết. Năm nay, tổng lượng kh&aacute;ch qua T&acirc;n Sơn Nhất ước t&iacute;nh hơn&nbsp;<a href=\"https://vnexpress.net/khach-den-tan-son-nhat-dip-tet-du-bao-tang-ky-luc-4549984.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4551970&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-34TrieuNguoi&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">34 triệu người</a>, vượt 22% c&ocirc;ng suất thiết kế.</p>\r\n</article>\r\n<p>&nbsp;</p>', 3, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (9, 'Áo choàng tàng hình \'che mắt\' camera an ninh', '2022-08-17 10:21:41', 189, 'TRUNG QUỐC - Nhóm nghiên cứu ở Đại học Vũ Hán phát minh áo choàng tàng hình giá rẻ giúp che giấu cơ thể người khỏi camera an ninh trang bị công nghệ AI bất kể ngày hay đêm.', '<p class=\"description\">Nh&oacute;m nghi&ecirc;n cứu ở Đại học Vũ H&aacute;n ph&aacute;t minh &aacute;o cho&agrave;ng t&agrave;ng h&igrave;nh gi&aacute; rẻ gi&uacute;p che giấu cơ thể người khỏi camera an ninh trang bị c&ocirc;ng nghệ AI bất kể ng&agrave;y hay đ&ecirc;m.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=57sEu7fjWzXtO3mR4eQ-yw 1x, https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_BPEEMCMMnURQ6QsX4SUkg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7biC2eCLpkcOVQNCNbDWyg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=57sEu7fjWzXtO3mR4eQ-yw 1x, https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_BPEEMCMMnURQ6QsX4SUkg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7biC2eCLpkcOVQNCNbDWyg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=57sEu7fjWzXtO3mR4eQ-yw\" alt=\"&Aacute;o cho&agrave;ng InvisDefense khiến camera an ninh kh&ocirc;ng thể ph&aacute;t hiện cơ thể người đi qua. Ảnh: Wei Hui\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/05/Capture-JPG-7672-1670237149.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=57sEu7fjWzXtO3mR4eQ-yw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Aacute;o cho&agrave;ng InvisDefense khiến camera an ninh kh&ocirc;ng thể ph&aacute;t hiện cơ thể người đi qua. Ảnh: Wei Hui</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">&Aacute;o cho&agrave;ng InvisDefense c&oacute; thể nh&igrave;n thấy bằng mắt thường nhưng được bao phủ họa tiết c&oacute; thể qua mắt c&aacute;c camera v&agrave;o ban ng&agrave;y v&agrave; ph&aacute;t ra t&iacute;n hiệu nhiệt kh&aacute;c thường v&agrave;o ban đ&ecirc;m. Sản phẩm của nh&oacute;m nghi&ecirc;n cứu sinh ở trường khoa học m&aacute;y t&iacute;nh thuộc Đại học Vũ H&aacute;n đoạt giải nhất cuộc thi s&aacute;ng chế h&ocirc;m 27/11. Người hướng dẫn dự &aacute;n l&agrave; gi&aacute;o sư Wang Zheng ở Đại học Vũ H&aacute;n. B&agrave;i b&aacute;o m&ocirc; tả ph&aacute;t minh của nh&oacute;m nghi&ecirc;n cứu sẽ được c&ocirc;ng bố tại AAAI 2023, hội nghị h&agrave;ng đầu về tr&iacute; tuệ nh&acirc;n tạo diễn ra v&agrave;o ng&agrave;y 11/2 năm sau.</p>\r\n<p class=\"Normal\">\"Ng&agrave;y nay, nhiều thiết bị gi&aacute;m s&aacute;t c&oacute; thể ph&aacute;t hiện cơ thể người. Camera tr&ecirc;n đường c&oacute; chức năng ph&aacute;t hiện người đi bộ v&agrave; xe hơi th&ocirc;ng minh c&oacute; thể nhận dạng người qua đường, đường s&aacute; v&agrave; chướng ngại vật. &Aacute;o cho&agrave;ng InvisDefense của ch&uacute;ng t&ocirc;i cho ph&eacute;p camera ghi h&igrave;nh bạn nhưng kh&ocirc;ng thể x&aacute;c định bạn c&oacute; phải con người hay kh&ocirc;ng\", Wang cho biết.</p>\r\n<p class=\"Normal\">Ban ng&agrave;y, camera thường ph&aacute;t hiện cơ thể người th&ocirc;ng qua nhận dạng chuyển động v&agrave; h&igrave;nh khối. Với họa tiết ngụy trang được thiết kế đặc biệt tr&ecirc;n bề mặt, InvisDefense c&oacute; thể can thiệp v&agrave;o thuật to&aacute;n nhận dạng của thị gi&aacute;c m&aacute;y, l&agrave;m m&ugrave; hiệu quả camera khiến n&oacute; kh&ocirc;ng thể biết người mặc l&agrave; con người. V&agrave;o ban đ&ecirc;m, camera theo d&otilde;i chuyển động cơ thể th&ocirc;ng qua chụp ảnh nhiệt hồng ngoại. C&aacute;c module kiểm so&aacute;t nhiệt độ c&oacute; h&igrave;nh dạng lạ mắt nằm ở mặt trong của &aacute;o cho&agrave;ng InvisDefense tạo ra kiểu nhiệt độ kh&aacute;c thường khiến camera hồng ngoại bị rối.</p>\r\n<p class=\"Normal\">\"Phần kh&oacute; khăn nhất l&agrave; c&acirc;n bằng họa tiết ngụy trang. Giới nghi&ecirc;n cứu thường sử dụng h&igrave;nh ảnh s&aacute;ng để t&aacute;c động tới thị gi&aacute;c m&aacute;y. Nhưng n&oacute; rất nổi bật với mắt người, l&agrave;m người d&ugrave;ng trở n&ecirc;n c&agrave;ng r&otilde; r&agrave;ng hơn\", Wei Hui, nghi&ecirc;n cứu sinh tiến sĩ phụ tr&aacute;ch thuật to&aacute;n cốt l&otilde;i, cho biết. \"Ch&uacute;ng t&ocirc;i sử dụng thuật to&aacute;n để thiết kế họa tiết &iacute;t lộ nhất, c&oacute; thể v&ocirc; hiệu h&oacute;a thị gi&aacute;c m&aacute;y t&iacute;nh\".</p>\r\n<p class=\"Normal\">Nh&oacute;m nghi&ecirc;n cứu tiến h&agrave;nh h&agrave;ng trăm th&iacute; nghiệm trong hơn 3 th&aacute;ng trước khi r&uacute;t ra họa tiết tốt nhất. Một lợi thế kh&aacute;c của InvisDefense l&agrave; chi ph&iacute; thấp. Việc in họa tiết l&ecirc;n bề mặt tương đối rẻ v&agrave; chỉ cần 4 module kiểm so&aacute;t nhiệt để che mắt camera hồng ngoại.</p>\r\n<p class=\"Normal\">Theo Wang, chi ph&iacute; cho một bộ InvisDefense ho&agrave;n chỉnh chưa đến 70 USD. \"Đ&acirc;y l&agrave; sản phẩm đầu ti&ecirc;n trong ng&agrave;nh c&ocirc;ng nghiệp c&oacute; thể tr&aacute;nh ph&aacute;t hiện người đi bộ v&agrave; kh&ocirc;ng g&acirc;y nghi ngờ khi nh&igrave;n bằng mắt thường. Th&ocirc;ng qua kiểm tra trong s&acirc;n trường, khả năng ph&aacute;t hiện ch&iacute;nh x&aacute;c người đi bộ giảm 57%, con số c&oacute; thể cao hơn trong tương lai. InvisDefense c&oacute; thể sử dụng trong chiến đấu chống drone hoặc đụng độ giữa con người v&agrave; m&aacute;y m&oacute;c tr&ecirc;n chiến trường\", Wang n&oacute;i.</p>\r\n</article>\r\n<p>&nbsp;</p>', 4, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (10, '4 công trình giao thông trọng điểm hoàn thành năm 2022', '2022-10-26 10:23:27', 122, 'Cao tốc Vân Đồn - Móng Cái, Trung Lương - Mỹ Thuận, cầu Thủ Thiêm 2... hoàn thành giúp mở nhiều hướng giao thông mới, thay đổi diện mạo các địa phương. ', '<p class=\"description\">Cao tốc V&acirc;n Đồn - M&oacute;ng C&aacute;i, Trung Lương - Mỹ Thuận, cầu Thủ Thi&ecirc;m 2... ho&agrave;n th&agrave;nh gi&uacute;p mở nhiều hướng giao th&ocirc;ng mới, thay đổi diện mạo c&aacute;c địa phương.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gbnoXUlllmYX04hryhspUw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oU7UjPJ9G8u4XGWZ7j58Iw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7madYiVGLt_Cc_8snNP_yw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gbnoXUlllmYX04hryhspUw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oU7UjPJ9G8u4XGWZ7j58Iw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7madYiVGLt_Cc_8snNP_yw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gbnoXUlllmYX04hryhspUw\" alt=\"Cao tốc V&acirc;n Đồn - M&oacute;ng C&aacute;i. Ảnh: L&ecirc; T&acirc;n\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/IMG-1660662839917-166091121317-3268-5949-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gbnoXUlllmYX04hryhspUw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cao tốc V&acirc;n Đồn - M&oacute;ng C&aacute;i. Ảnh:&nbsp;<em>L&ecirc; T&acirc;n</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Kh&aacute;nh th&agrave;nh h&ocirc;m 1/9, sau gần ba năm rưỡi thi c&ocirc;ng, cao tốc<strong>&nbsp;<a href=\"https://vnexpress.net/ba-thap-ky-mong-cho-tuyen-cao-toc-van-don-mong-cai-4521841.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4548072&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-VanDonMongCai&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">V&acirc;n Đồn - M&oacute;ng C&aacute;i</a>&nbsp;</strong>(Quảng Ninh) d&agrave;i gần 80 km với 4 l&agrave;n xe, mặt đường rộng hơn 25 m thiết kế cho xe chạy với tốc độ tối đa 120 km/h.</p>\r\n<p class=\"Normal\">C&ocirc;ng tr&igrave;nh c&oacute; tổng mức đầu tư gần 9.800 tỷ đồng. Trong đ&oacute;, đoạn V&acirc;n Đồn - Ti&ecirc;n Y&ecirc;n d&agrave;i 16,8 km thực hiện theo h&igrave;nh thức đầu tư c&ocirc;ng với kinh ph&iacute; hơn 3.650 tỷ đồng. Đoạn Ti&ecirc;n Y&ecirc;n - M&oacute;ng C&aacute;i d&agrave;i 63,2 km l&agrave;m theo h&igrave;nh thức đối t&aacute;c c&ocirc;ng tư (PPP) với kinh ph&iacute; hơn 9.110 tỷ đồng.</p>\r\n<p class=\"Normal\">Tuyến cao tốc gi&uacute;p r&uacute;t ngắn thời gian từ V&acirc;n Đồn đi M&oacute;ng C&aacute;i xuống c&ograve;n 50 ph&uacute;t thay v&igrave; 2 giờ như trước, đồng thời li&ecirc;n th&ocirc;ng hai cao tốc Hải Ph&ograve;ng - Hạ Long, Hạ Long - V&acirc;n Đồn th&agrave;nh trục d&agrave;i 176 km, đưa Quảng Ninh th&agrave;nh tỉnh c&oacute; số km cao tốc lớn nhất cả nước. Trục đường n&agrave;y cũng kết nối cao tốc H&agrave; Nội - Hải Ph&ograve;ng, r&uacute;t ngắn thời gian từ H&agrave; Nội đến M&oacute;ng C&aacute;i c&ograve;n 3 giờ, thay v&igrave; 5,5 giờ.</p>\r\n<p class=\"Normal\">Dự &aacute;n<strong>&nbsp;</strong>n&acirc;ng cấp<strong>&nbsp;đường băng&nbsp;<a href=\"https://vnexpress.net/hoan-thanh-nang-cap-duong-bang-tan-son-nhat-noi-bai-4218914.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4548072&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-TanSonNhatVaNoiBai&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">T&acirc;n Sơn Nhất v&agrave; Nội B&agrave;i&nbsp;</a></strong>với tổng mức đầu tư 4.000 tỷ đồng ho&agrave;n th&agrave;nh đầu năm, sau một năm rưỡi thi c&ocirc;ng, gi&uacute;p tăng năng lực khai th&aacute;c hai s&acirc;n bay lớn nhất nước.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m7c2R4OnahwF1ec9rxLhNw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nIdoirzrx2mcL2K6wNrblw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mRsLwNRofdr3wT91tuKctQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m7c2R4OnahwF1ec9rxLhNw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nIdoirzrx2mcL2K6wNrblw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mRsLwNRofdr3wT91tuKctQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m7c2R4OnahwF1ec9rxLhNw\" alt=\"M&aacute;y bay di chuyển qua đường lăn vừa n&acirc;ng cấp tại T&acirc;n Sơn Nhất, chiều 25/4. Ảnh: Gia Minh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/duong-bang-jpg-8408-1650879570-9077-4958-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m7c2R4OnahwF1ec9rxLhNw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">M&aacute;y bay di chuyển qua đường lăn vừa n&acirc;ng cấp tại T&acirc;n Sơn Nhất, chiều 25/4. Ảnh:&nbsp;<em>Gia Minh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trong đ&oacute;, tại Nội B&agrave;i đường băng<strong>&nbsp;</strong>11L/29R (1A) v&agrave; đường băng 11R/29L (1B) được n&acirc;ng cấp v&agrave; x&acirc;y mới 3 đường lăn tho&aacute;t nhanh với kinh ph&iacute; 2.000 tỷ. Tại T&acirc;n Sơn Nhất, dự &aacute;n cũng c&oacute; tổng vốn hơn 2.000 tỷ đồng bao gồm sửa chữa đường băng 25R/07L v&agrave; cải tạo, x&acirc;y mới bốn đường lăn nối E1, NS1, W6, W4, thay mới hệ thống đ&egrave;n hiệu, biển b&aacute;o h&agrave;ng kh&ocirc;ng, trạm điện...</p>\r\n<p class=\"Normal\">Dự &aacute;n gi&uacute;p năng lực khai th&aacute;c trung b&igrave;nh hai s&acirc;n bay tăng l&ecirc;n 40 chuyến bay mỗi giờ thay v&igrave; 32 như trước, cao điểm c&oacute; thể l&ecirc;n tới 44 chuyến mỗi giờ.</p>\r\n<p class=\"Normal\">Cao tốc<strong>&nbsp;<a href=\"https://vnexpress.net/khanh-thanh-cao-toc-trung-luong-my-thuan-4456378.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4548072&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-TrungLuongMyThuan&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Trung Lương - Mỹ Thuận</a></strong>&nbsp;(Tiền Giang) d&agrave;i 51 km, rộng 16 m, 4 l&agrave;n xe tốc độ thiết kế 80 km/h được khai th&aacute;c từ cuối th&aacute;ng 4. C&ocirc;ng tr&igrave;nh c&oacute; tổng mức đầu tư hơn 12.600 tỷ đồng, thực hiện theo h&igrave;nh thức BOT (kinh doanh - x&acirc;y dựng - chuyển giao). Dự &aacute;n khởi c&ocirc;ng từ năm 2009, song qu&aacute; tr&igrave;nh triển khai, gặp nhiều kh&oacute; khăn, nhiều lần đổi chủ đầu tư v&agrave; cơ quan quản l&yacute; n&ecirc;n phải mất 13 năm mới ho&agrave;n th&agrave;nh.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RfoctXPV2sx68UVSa2kctw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=CWP2UpvmaRZAGeO9weS-Bg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=M9azs9t30JoyCrkwJtwSOQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RfoctXPV2sx68UVSa2kctw 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=CWP2UpvmaRZAGeO9weS-Bg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=M9azs9t30JoyCrkwJtwSOQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RfoctXPV2sx68UVSa2kctw\" alt=\"Cao tốc Trung Lương - Mỹ Thuận. Ảnh: Ho&agrave;ng Nam\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/bcbdaae0-8d59-4f7a-99e9-4f2063-9732-7927-1671784383.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RfoctXPV2sx68UVSa2kctw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cao tốc Trung Lương - Mỹ Thuận. Ảnh:&nbsp;<em>Ho&agrave;ng Nam</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Tuyến đường n&agrave;y kết nối cao tốc TP HCM - Trung Lương (62 km) th&agrave;nh trục d&agrave;i hơn 110 km, gi&uacute;p r&uacute;t ngắn h&agrave;nh tr&igrave;nh từ TP HCM đến Mỹ Thuận (Tiền Giang) từ 3 giờ xuống c&ograve;n 1 giờ 45, đồng thời giảm tải cho quốc lộ 1.</p>\r\n<p class=\"Normal\">Do dự &aacute;n bị trễ hẹn đến 9 năm so với kế hoạch n&ecirc;n d&ugrave; mới khai th&aacute;c, tuyến đ&atilde; bị qu&aacute; tải do lượng xe qu&aacute; lớn. Mới đ&acirc;y, Bộ Giao th&ocirc;ng Vận tải đề nghị c&aacute;c b&ecirc;n li&ecirc;n quan nghi&ecirc;n cứu phương &aacute;n mở rộng cao tốc n&agrave;y l&ecirc;n 6 l&agrave;n xe, hai l&agrave;n dừng khẩn cấp.</p>\r\n<p class=\"Normal\">Cầu&nbsp;<a href=\"https://vnexpress.net/chu-de/cau-thu-thiem-2-5336\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4548072&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-ThuThiem2&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\"><strong>Thủ Thi&ecirc;m 2&nbsp;</strong></a>bắc qua s&ocirc;ng S&agrave;i G&ograve;n kh&aacute;nh th&agrave;nh v&agrave;o cuối th&aacute;ng 4, tạo hướng kết nối mới giữa Khu đ&ocirc; thị Thủ Thi&ecirc;m, TP Thủ Đức với trung t&acirc;m TP HCM. C&ocirc;ng tr&igrave;nh c&oacute; tổng mức đầu tư gần 3.100 tỷ đồng - kinh ph&iacute; lớn nhất trong c&aacute;c c&acirc;y cầu tại th&agrave;nh phố đến thời điểm n&agrave;y.</p>\r\n<p class=\"Normal\">Cầu d&agrave;i gần 1,5 km gồm cả phần cầu v&agrave; đường dẫn với 6 l&agrave;n xe, thiết kế d&acirc;y văng, được xem l&agrave; biểu tượng mới ở TP HCM. Dự &aacute;n bắt đầu triển khai từ năm 2015, nhưng phải 7 năm sau mới ho&agrave;n th&agrave;nh v&igrave; qu&aacute; tr&igrave;nh thi c&ocirc;ng gặp kh&oacute; khăn về giải ph&oacute;ng mặt bằng, thủ tục thanh to&aacute;n theo hợp đồng BT (x&acirc;y dựng - chuyển giao)...</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Du_ba5o4s4jCP590YAGmbg 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ul3CjcmN4dt7to1M9f3qlQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=E1Gl3KZqG2b140t-M-H1ZA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Du_ba5o4s4jCP590YAGmbg 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ul3CjcmN4dt7to1M9f3qlQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=E1Gl3KZqG2b140t-M-H1ZA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Du_ba5o4s4jCP590YAGmbg\" alt=\"Cầu Ba Son th&aacute;ng 12/2022. Ảnh: Quỳnh Trần\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/5dddd26d3316eb48b207-3777-1671842911.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Du_ba5o4s4jCP590YAGmbg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cầu Ba Son th&aacute;ng 12/2022. Ảnh:&nbsp;<em>Quỳnh Trần</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Hiện, cầu Thủ Thi&ecirc;m 2 được TP HCM quyết định đổi t&ecirc;n th&agrave;nh&nbsp;<a href=\"https://vnexpress.net/cau-bieu-tuong-moi-cua-tp-hcm-mang-ten-ba-son-4545493.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-ThoiSu_GiaoThong-4548072&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-BaSon&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Ba Son</a>, để ghi dấu ng&agrave;nh c&ocirc;ng nghiệp đ&oacute;ng v&agrave; sửa chữa t&agrave;u thuỷ b&ecirc;n bờ s&ocirc;ng S&agrave;i G&ograve;n, c&oacute; lịch sử từ hơn 200 năm trước. C&ocirc;ng tr&igrave;nh cũng đang được th&agrave;nh phố nghi&ecirc;n cứu thiết kế hệ thống chiếu s&aacute;ng mỹ thuật, gi&uacute;p tạo điểm nhấn kiến tr&uacute;c nổi bật tr&ecirc;n s&ocirc;ng S&agrave;i G&ograve;n cả ng&agrave;y lẫn đ&ecirc;m.</p>\r\n<p class=\"Normal\">Theo quy hoạch, c&oacute; 4 c&acirc;y cầu v&agrave; một hầm kết nối Khu đ&ocirc; thị mới Thủ Thi&ecirc;m với trung t&acirc;m th&agrave;nh phố. Trong đ&oacute;, cầu Thủ Thi&ecirc;m 1 nối đường Nguyễn Hữu Cảnh với trục đại lộ Đ&ocirc;ng - T&acirc;y đưa khai th&aacute;c từ năm 2008. Sau cầu Thủ Thi&ecirc;m 2, TP HCM đang chuẩn bị đầu tư hai cầu Thủ Thi&ecirc;m 3 (nối quận 4) v&agrave; Thủ Thi&ecirc;m 4 (nối quận 7) trước năm 2030.</p>\r\n</article>', 3, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (11, 'Trung tâm đào tạo chậm đầu tư cabin tập lái xe', '2022-10-20 10:24:25', 322, 'Một tuần nữa đến thời điểm học viên phải thực hành trên cabin tập lái, song nhiều trung tâm chưa lắp đặt thiết bị do chi phí đầu tư cao, thiếu đơn vị cung cấp.', '<p class=\"description\">Một tuần nữa đến thời điểm học vi&ecirc;n phải thực h&agrave;nh tr&ecirc;n cabin tập l&aacute;i, song nhiều trung t&acirc;m chưa lắp đặt thiết bị do chi ph&iacute; đầu tư cao, thiếu đơn vị cung cấp.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Th&ocirc;ng tư 04/2022 của Bộ Giao th&ocirc;ng Vận tải quy định c&aacute;c trung t&acirc;m đ&agrave;o tạo phải đầu tư thiết bị cabin tập l&aacute;i để học vi&ecirc;n học từ ng&agrave;y 1/1/2023. Học vi&ecirc;n c&oacute; tối thiểu 3 giờ thực h&agrave;nh c&aacute;c b&agrave;i cơ bản như c&aacute;ch vận h&agrave;nh số xe, l&ecirc;n dốc, đường vu&ocirc;ng g&oacute;c, đường quanh co giống như b&agrave;i sa h&igrave;nh thi s&aacute;t hạch; đồng thời học n&acirc;ng cao tr&ecirc;n c&aacute;c địa h&igrave;nh đồi n&uacute;i, cao tốc. Học vi&ecirc;n c&oacute; đủ thời gian học tr&ecirc;n cabin v&agrave; thực h&agrave;nh tr&ecirc;n đường mới được thi s&aacute;t hạch l&aacute;i xe.</p>\r\n<p class=\"Normal\">Để thực hiện quy định, Trung t&acirc;m Đ&agrave;o tạo v&agrave; S&aacute;t hạch l&aacute;i xe Đ&ocirc;ng Đ&ocirc; (Bắc Ninh) dự kiến phải đầu tư 20-30 cabin, mỗi chiếc khoảng 400-500 triệu đồng, song hiện chưa lắp. Gi&aacute;m đốc Trung t&acirc;m Trần Văn Toản cho biết việc đầu tư kh&aacute; tốn k&eacute;m, trong khi nguồn thu sụt giảm trong thời điểm dịch Covid. Trước mắt đơn vị dự t&iacute;nh đầu tư 2-3 cabin, số lượng &iacute;t c&oacute; thể g&acirc;y qu&aacute; tải v&agrave; k&eacute;o d&agrave;i thời gian đ&agrave;o tạo khi lượng học vi&ecirc;n lớn.</p>\r\n<p class=\"Normal\">Trung t&acirc;m Gi&aacute;o dục nghề nghiệp v&agrave; S&aacute;t hạch l&aacute;i xe Việt Đức (Hải Dương) cần đầu tư 10-5 cabin tập l&aacute;i, trị gi&aacute; khoảng 6-7 tỷ đồng, chưa kể chi ph&iacute; vận h&agrave;nh, bảo tr&igrave;. Theo l&atilde;nh đạo Trung t&acirc;m, ng&agrave;y 31/12 phải kết th&uacute;c lắp đặt, nghĩa l&agrave; từ giờ phải c&oacute; thiết bị để thử nghiệm, song đơn vị vẫn chưa t&igrave;m được nh&agrave; cung cấp.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7UmrKhg6IkQnoxR7L4Db_g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=teXXkwkcp2LTvjt3kWPO8g 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=a5b2VbGTKce-LTaMuaYdbg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7UmrKhg6IkQnoxR7L4Db_g 1x, https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=teXXkwkcp2LTvjt3kWPO8g 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=a5b2VbGTKce-LTaMuaYdbg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7UmrKhg6IkQnoxR7L4Db_g\" alt=\"Một mẫu cabin tập l&aacute;i. Ảnh: Anh Duy\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/23/cabin-9635-1651652513-8549-1671767866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7UmrKhg6IkQnoxR7L4Db_g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Một mẫu cabin tập l&aacute;i. Ảnh:&nbsp;<em>Anh Duy</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>&Iacute;t nh&agrave; cung cấp cabin tập l&aacute;i cũng l&agrave; mối lo của c&aacute;c đơn vị đ&agrave;o tạo l&aacute;i xe.</strong>&nbsp;B&agrave; Nguyễn Thanh Phi&ecirc;n Thanh, C&ocirc;ng ty TNHH MTV Trường trung cấp nghề c&ocirc;ng tr&igrave;nh 1, nhận định đến ng&agrave;y 15/11/2022 cơ quan quản l&yacute; mới chọn được đơn vị kiểm nghiệm chất lượng khiến cơ sở đ&agrave;o tạo, s&aacute;t hạch l&aacute;i xe c&oacute; qu&aacute; &iacute;t cơ hội lựa chọn nh&agrave; cung cấp. Cabin l&agrave; t&agrave;i sản, nếu kh&ocirc;ng lựa chọn kỹ sau n&agrave;y sẽ g&acirc;y thiệt hại cho c&aacute;c đơn vị.</p>\r\n<p class=\"Normal\">Chủ tịch Hiệp hội Vận tải &ocirc;t&ocirc; Việt Nam Nguyễn Văn Quyền cũng đ&aacute;nh gi&aacute; việc c&oacute; &iacute;t đơn vị cung ứng cabin tập l&aacute;i khiến gi&aacute; bị đẩy l&ecirc;n cao, dẫn đến độc quyền. \"C&aacute;ch đ&acirc;y mấy th&aacute;ng, một số nh&agrave; cung cấp giới thiệu cabin chưa hợp quy, gi&aacute; 400-430 triệu đồng, nhưng đến ng&agrave;y 5/12 th&igrave; c&oacute; đơn vị đ&atilde; ch&agrave;o b&aacute;n 550 triệu đồng\", &ocirc;ng Quyền n&oacute;i.</p>\r\n<p class=\"Normal\">C&aacute;c trung t&acirc;m đ&agrave;o tạo muốn gia hạn thời gian lắp đặt cabin v&agrave; giảm giờ học của học vi&ecirc;n để giảm chi ph&iacute; đầu tư thiết bị. &Ocirc;ng Trần Văn Toản, Gi&aacute;m đốc Trung t&acirc;m Đ&agrave;o tạo l&aacute;i xe Đ&ocirc;ng Đ&ocirc;, đề xuất Bộ Giao th&ocirc;ng Vận tải cho ph&eacute;p giảm từ 3 giờ xuống c&ograve;n 1,5-2 giờ với học vi&ecirc;n bằng B1 (xe số tự động), bởi học vi&ecirc;n vẫn cần được thực h&agrave;nh tr&ecirc;n &ocirc;t&ocirc; thật thay v&igrave; thiết bị m&ocirc; phỏng.</p>\r\n<p class=\"Normal\">\"Hiệu quả sử dụng cabin tập l&aacute;i chưa được chứng minh m&agrave; ng&agrave;nh giao th&ocirc;ng y&ecirc;u cầu c&aacute;c trung t&acirc;m đầu tư đại tr&agrave; l&agrave; vội v&agrave;ng, g&acirc;y tốn k&eacute;m\", &ocirc;ng Toản n&oacute;i.</p>\r\n<p class=\"Normal\">&Ocirc;ng Lương Duy&ecirc;n Thống, Trưởng ph&ograve;ng Vận tải, phương tiện người l&aacute;i, Cục Đường bộ Việt Nam, th&ocirc;ng tin Bộ Giao th&ocirc;ng Vận tải vẫn bảo lưu quy định học vi&ecirc;n được đ&agrave;o tạo tr&ecirc;n cabin từ ng&agrave;y 1/1/2023, kh&ocirc;ng gia hạn. Cục đề nghị đơn vị thử nghiệm, đơn vị chứng nhận kiểm tra xem sản phẩm của doanh nghiệp cung cấp ra b&ecirc;n ngo&agrave;i c&oacute; ph&ugrave; hợp với sản phẩm mẫu đ&atilde; đăng k&yacute;, nếu thiết bị kh&ocirc;ng đảm bảo th&igrave; thu hồi, trả lại tiền cho cơ sở đ&agrave;o tạo.</p>\r\n</article>\r\n<p>&nbsp;</p>', 3, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (12, 'Trung Quốc phát triển \'pin nước biển\' sản xuất điện sạch', '2022-09-13 10:26:00', 344, 'Các nhà khoa học Trung Quốc kết hợp vi sinh vật với nước biển để chuyển đổi ánh sáng thành đường, sau đó sử dụng đường để tạo ra điện.', '<p class=\"description\">C&aacute;c nh&agrave; khoa học Trung Quốc kết hợp vi sinh vật với nước biển để chuyển đổi &aacute;nh s&aacute;ng th&agrave;nh đường, sau đ&oacute; sử dụng đường để tạo ra điện.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Theo&nbsp;<em>SCMP&nbsp;</em>đưa tin h&ocirc;m 10/10, loại pin sinh học n&agrave;y c&oacute; thể cung cấp tối đa 380 microwatt v&agrave; hoạt động ổn định trong hơn một th&aacute;ng, ph&ugrave; hợp với c&aacute;c cơ sở sử dụng năng lượng cực thấp. Hiện tại, hiệu suất của pin kh&ocirc;ng thể s&aacute;nh bằng quang điện b&aacute;n dẫn nhưng n&oacute; cho thấy một c&aacute;ch tương th&iacute;ch với m&ocirc;i trường hơn v&agrave; c&oacute; khả năng tiết kiệm chi ph&iacute; hơn để tạo ra điện trực tiếp từ &aacute;nh s&aacute;ng.</p>\r\n<p class=\"Normal\">C&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu - do Ph&ograve;ng th&iacute; nghiệm Trọng điểm Nh&agrave; nước Trung Quốc về T&agrave;i nguy&ecirc;n Vi sinh vật, Viện Năng lượng Sinh học v&agrave; C&ocirc;ng nghệ Xử l&yacute; Sinh học Thanh Đảo, Viện C&ocirc;ng nghệ Sinh học C&ocirc;ng nghiệp Thi&ecirc;n T&acirc;n thuộc Viện H&agrave;n l&acirc;m Khoa học Trung Quốc phối hợp thực hiện - đ&atilde; được xuất bản tr&ecirc;n tạp ch&iacute;&nbsp;<em>Nature Communications</em>.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FrqNgmqatagIl2AanEN8IQ 1x, https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pLy7-bpZh7HgU3FgDowGuQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IgoZGsqT-Q2y3GIa4xVG7w 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FrqNgmqatagIl2AanEN8IQ 1x, https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pLy7-bpZh7HgU3FgDowGuQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IgoZGsqT-Q2y3GIa4xVG7w 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FrqNgmqatagIl2AanEN8IQ\" alt=\"M&ocirc; phỏng hệ sinh th&aacute;i vi sinh vật biển (tr&aacute;i) v&agrave; pin đại dương sinh học (phải). Ảnh: Zhu Huawei\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-9806-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FrqNgmqatagIl2AanEN8IQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">M&ocirc; phỏng hệ sinh th&aacute;i vi sinh vật biển (tr&aacute;i) v&agrave; pin đại dương sinh học (phải). Ảnh:&nbsp;<em>Zhu Huawei</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">\"Pin đại dương sinh học thu nhỏ được lấy cảm hứng từ c&aacute;c hệ sinh th&aacute;i vi sinh vật biển\", t&aacute;c giả ch&iacute;nh Zhu Huawei từ Ph&ograve;ng th&iacute; nghiệm Trọng điểm Nh&agrave; nước Trung Quốc về T&agrave;i nguy&ecirc;n Vi sinh vật, cho biết.</p>\r\n<p class=\"Normal\">C&aacute;c loại tảo như tảo lục lam, c&ograve;n được gọi l&agrave; vi khuẩn lam, l&agrave; sinh vật sản xuất ch&iacute;nh trong hệ sinh th&aacute;i vi sinh vật biển. Ch&uacute;ng hấp thụ năng lượng mặt trời v&agrave; carbon dioxide cố định để sản xuất chất hữu cơ. Th&ocirc;ng qua đ&oacute;, năng lượng mặt trời được chuyển cho c&aacute;c electron v&agrave; lưu trữ trong chất hữu cơ.</p>\r\n<p class=\"Normal\">Sau khi ph&acirc;n hủy, một số chất hữu cơ lắng xuống trầm t&iacute;ch dưới đ&aacute;y biển, nơi ch&uacute;ng trở th&agrave;nh dinh dưỡng cho vi sinh vật v&agrave; tham gia v&agrave;o qu&aacute; tr&igrave;nh trao đổi chất của ch&uacute;ng.</p>\r\n<p class=\"Normal\">Năng lượng mặt trời l&agrave; động lực ch&iacute;nh trong c&aacute;c chu tr&igrave;nh sinh địa h&oacute;a n&agrave;y. Để tăng hiệu suất, c&aacute;c nh&agrave; khoa học đ&atilde; chế tạo một thiết bị đặc biệt nhằm thực hiện chu tr&igrave;nh n&agrave;y trong một vi&ecirc;n pin.</p>\r\n<p class=\"Normal\">Nh&oacute;m nghi&ecirc;n cứu đ&atilde; đưa bốn lo&agrave;i vi sinh vật, bao gồm cả tảo, v&agrave;o một thiết bị chứa đầy nước biển để bắt chước cấu tr&uacute;c của hệ sinh th&aacute;i vi sinh vật: trong đ&oacute; một lo&agrave;i đ&oacute;ng vai tr&ograve; l&agrave; sinh vật sản xuất ch&iacute;nh, một lo&agrave;i l&agrave; sinh vật ph&acirc;n hủy ch&iacute;nh v&agrave; hai lo&agrave;i c&ograve;n lại l&agrave; sinh vật ti&ecirc;u thụ cuối c&ugrave;ng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3SmeVzD4qc90ASDwVAioMQ 1x, https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UFJRc3fnARAvCFn7M3TdJA 1.5x, https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=s7BD_vTF4tLFauzWHZRxrA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3SmeVzD4qc90ASDwVAioMQ 1x, https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UFJRc3fnARAvCFn7M3TdJA 1.5x, https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=s7BD_vTF4tLFauzWHZRxrA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3SmeVzD4qc90ASDwVAioMQ\" alt=\"C&aacute;c loại tảo l&agrave; sinh vật sản xuất ch&iacute;nh của hệ sinh th&aacute;i đại dương. Ảnh: SCMP\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/10/11/-7947-1665478069.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3SmeVzD4qc90ASDwVAioMQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">C&aacute;c loại tảo l&agrave; sinh vật sản xuất ch&iacute;nh của hệ sinh th&aacute;i đại dương. Ảnh:&nbsp;<em>SCMP</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đầu ti&ecirc;n, sinh vật sản xuất tạo ra đường sucrose th&ocirc;ng qua quang hợp. Sau đ&oacute;, sinh vật ph&acirc;n hủy ph&aacute; vỡ đường sucrose th&agrave;nh lactate. Tiếp theo, c&aacute;c sinh vật ti&ecirc;u thụ tiếp tục ph&acirc;n hủy lactate v&agrave; cuối c&ugrave;ng tạo ra điện.</p>\r\n<p class=\"Normal\">\"Điều n&agrave;y kh&ocirc;ng chỉ chứng minh hệ thống bốn lo&agrave;i l&agrave; tối ưu về mật độ năng lượng v&agrave; độ ổn định, m&agrave; c&ograve;n cho thấy việc duy tr&igrave; cấu tr&uacute;c sinh th&aacute;i ba cấp ho&agrave;n chỉnh l&agrave; một c&aacute;ch hiệu quả để chuyển đổi quang điện sinh học\", Zhu nhấn mạnh. \"Pin c&oacute; thể đ&oacute;ng vai tr&ograve; như một nguồn năng lượng điện thay thế cho c&aacute;c cơ sở năng lượng si&ecirc;u thấp, chẳng hạn như cảm biến m&ocirc;i trường của Internet Vạn vật. Một pin đại dương sinh học thu nhỏ tạo ra h&agrave;ng trăm microwatt l&agrave; đủ để hỗ trợ những cơ sở như vậy\".</p>\r\n<p class=\"Normal\">Với năng lượng mặt trời l&agrave; đầu v&agrave;o duy nhất, loại pin sinh học n&agrave;y thậm ch&iacute; c&oacute; tiềm năng hoạt động tr&ecirc;n sao Hỏa, miễn l&agrave; c&oacute; nước, carbon dioxide v&agrave; kho&aacute;ng chất.</p>\r\n<p class=\"Normal\">Nh&oacute;m nghi&ecirc;n cứu đang t&igrave;m kiếm những c&aacute;ch hiệu quả để tăng sản lượng điện. \"Việc chế tạo pin nước biển hiện nay tương đối phức tạp. Ch&uacute;ng t&ocirc;i đang xem x&eacute;t tự động h&oacute;a quy tr&igrave;nh n&agrave;y bằng c&aacute;ch sử dụng c&ocirc;ng nghệ in 3D\", Zhu n&oacute;i th&ecirc;m.</p>\r\n</article>\r\n<p>&nbsp;</p>', 4, 0, 1, 0, NULL);
INSERT INTO `articles` VALUES (13, 'Camera dưới nước không cần pin', '2022-09-13 10:27:05', 676, 'MỸ - Các nhà nghiên cứu ở Viện Công nghệ Massachusetts (MIT) phát triển loại camera mới không cần pin và truyền ảnh chụp không dây qua mặt nước.', '<p class=\"description\">C&aacute;c nh&agrave; nghi&ecirc;n cứu ở Viện C&ocirc;ng nghệ Massachusetts (MIT) ph&aacute;t triển loại camera mới kh&ocirc;ng cần pin v&agrave; truyền ảnh chụp kh&ocirc;ng d&acirc;y qua mặt nước.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2uKo1zjrvMQnLZliRAyj8w 1x, https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Vt3OiG_0o7LNof4J1KnliA 1.5x, https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=s3sXhndzz-U168HmRHgDvQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2uKo1zjrvMQnLZliRAyj8w 1x, https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Vt3OiG_0o7LNof4J1KnliA 1.5x, https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=s3sXhndzz-U168HmRHgDvQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2uKo1zjrvMQnLZliRAyj8w\" alt=\"Thiết kế camera truyền ảnh kh&ocirc;ng d&acirc;y dưới nước của MIT. Ảnh: MIT\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/09/27/-3461-1664273146.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2uKo1zjrvMQnLZliRAyj8w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Thiết kế camera truyền ảnh kh&ocirc;ng d&acirc;y dưới nước của MIT. Ảnh:&nbsp;<em>MIT</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Thay v&igrave; d&ugrave;ng pin hay cuộn d&acirc;y điện d&agrave;i, mẫu camera của MIT t&iacute;ch hợp h&agrave;ng loạt bộ chuyển đổi gắn ở mặt ngo&agrave;i. Khi s&oacute;ng &acirc;m từ c&aacute;c nguồn như động vật hoặc phương tiện dưới nước truyền tới một bộ chuyển đổi, &aacute;p suất tạo bởi s&oacute;ng đ&oacute; khiến vật liệu đặc biệt b&ecirc;n trong bộ chuyển đổi rung l&ecirc;n. Do vật liệu c&oacute; hiệu ứng &aacute;p điện, ch&uacute;ng sản sinh d&ograve;ng điện để đ&aacute;p lại rung động. Năng lượng tạo ra theo c&aacute;ch n&agrave;y sẽ được lưu trữ trong si&ecirc;u tụ điện d&ugrave;ng để chụp ảnh.</p>\r\n<p class=\"Normal\">Để giữ nhu cầu năng lượng cho việc chụp ảnh thấp hết mức c&oacute; thể, c&aacute;c nh&agrave; nghi&ecirc;n cứu sử dụng cảm biến chụp ảnh cực tiết kiệm điện. Tuy nhi&ecirc;n, c&aacute;c cảm biến chỉ c&oacute; thể chụp ảnh m&agrave;u x&aacute;m. Nhằm khắc phục hạn chế đ&oacute;, mỗi bức ảnh bao gồm 3 lớp phơi s&aacute;ng ri&ecirc;ng biệt, lần lượt d&ugrave;ng đ&egrave;n LED đỏ, đ&egrave;n LED xanh l&aacute; c&acirc;y v&agrave; xanh dương. D&ugrave; mỗi lớp tr&ocirc;ng như m&agrave;u trắng - đen, n&oacute; thể hiện c&aacute;ch vật thể phản chiếu &aacute;nh s&aacute;ng ở cả bước s&oacute;ng m&agrave;u đỏ, xanh l&aacute; c&acirc;y hoặc xanh dương. Kết quả l&agrave; khi cả 3 bức ảnh được ph&acirc;n t&iacute;ch v&agrave; tổng hợp, ch&uacute;ng c&oacute; thể tạo th&agrave;nh một bức ảnh m&agrave;u tổng hợp.</p>\r\n<p class=\"Normal\">Để nhận bức ảnh kỹ thuật số kh&ocirc;ng d&acirc;y m&atilde; h&oacute;a theo hệ nhị ph&acirc;n, một thiết bị thu ph&aacute;t ở bề mặt truyền t&iacute;n hiệu s&oacute;ng &acirc;m qua nước tới m&aacute;y ảnh. Module trong m&aacute;y ảnh đ&aacute;p lại bằng c&aacute;ch phản chiếu t&iacute;n hiệu trở lại thiết bị thu ph&aacute;t (k&yacute; hiệu 1) hoặc hấp thụ t&iacute;n hiệu (k&yacute; hiệu 0). Do đ&oacute;, th&ocirc;ng qua theo d&otilde;i t&iacute;n hiệu n&agrave;o truyền trở lại thiết bị thu ph&aacute;t v&agrave; t&iacute;n hiệu n&agrave;o kh&ocirc;ng, m&aacute;y t&iacute;nh c&oacute; thể ghi lại mẫu k&yacute; tự 1 v&agrave; 0 đại diện cho bức ảnh.</p>\r\n<p class=\"Normal\">T&iacute;nh đến nay, c&ocirc;ng nghệ c&oacute; phạm vi tối đa dưới nước l&agrave; 40 m v&agrave; đ&atilde; được sử dụng th&agrave;nh c&ocirc;ng cho c&aacute;c nhiệm vụ như ghi lại sự ph&aacute;t triển của thực vật trong hơn một tuần. Nh&oacute;m nghi&ecirc;n cứu ở MIT hy vọng c&oacute; thể tăng phạm vi hoạt động v&agrave; bộ nhớ của camera tới mức c&oacute; thể truyền ảnh theo thời gian thực, thậm ch&iacute; ghi h&igrave;nh video chuyển động. Họ c&ocirc;ng bố kết quả nghi&ecirc;n cứu h&ocirc;m 26/9 tr&ecirc;n tạp ch&iacute; Nature Communications.</p>\r\n</article>\r\n<p>&nbsp;</p>', 4, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (14, 'Phục dựng gương mặt pharaoh quyền lực nhất', '2022-08-23 10:29:26', 321, 'Một nhóm nhà nghiên cứu dựng lại gương mặt của pharaoh Ramesses II cách đây 3.200 năm, sử dụng dữ liệu chụp cắt lớp hộp sọ. ', '<p class=\"description\">Một nh&oacute;m nh&agrave; nghi&ecirc;n cứu dựng lại gương mặt của pharaoh Ramesses II c&aacute;ch đ&acirc;y 3.200 năm, sử dụng dữ liệu chụp cắt lớp hộp sọ.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D8wYFAO1w4lX6KX53_1Q6w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=klrLwvlHWrTjFYGhN30Chw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9FUTeQRj17s96sQ-SaUpOQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D8wYFAO1w4lX6KX53_1Q6w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=klrLwvlHWrTjFYGhN30Chw 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9FUTeQRj17s96sQ-SaUpOQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D8wYFAO1w4lX6KX53_1Q6w\" alt=\"Pharoh Ramesses II c&oacute; sống mũi cao v&agrave; xương h&agrave;m r&otilde; n&eacute;t. Ảnh: Face Lab\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-Ram-2663-1671876992.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D8wYFAO1w4lX6KX53_1Q6w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Pharoh Ramesses II c&oacute; sống mũi cao v&agrave; xương h&agrave;m r&otilde; n&eacute;t. Ảnh:&nbsp;<em>Face Lab</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; khoa học đến từ Ai Cập v&agrave; Anh cộng t&aacute;c để phục dựng ch&acirc;n dung pharaoh Ramesses II ở thời điểm qua đời, sử dụng m&ocirc; h&igrave;nh 3D hộp sọ của &ocirc;ng để t&aacute;i tạo c&aacute;c đặc điểm. Sau đ&oacute;, họ đảo ngược qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, quay ngược thời gian trở về gần nửa thế kỷ trước để dựng lại gương mặt của vị pharaoh ở thời kỳ đỉnh cao quyền lực. Kết quả l&agrave; bản phục dựng gương mặt đầu ti&ecirc;n của pharaoh dựa tr&ecirc;n ảnh chụp cắt lớp vi t&iacute;nh hộp sọ.</p>\r\n<p class=\"Normal\">Nh&agrave; nghi&ecirc;n cứu Sahar Saleem ở Đại học Cairo,người tạo ra m&ocirc; h&igrave;nh 3D hộp sọ, cho biết kết quả h&eacute; lộ một vị vua \"rất đẹp trai\" với sống mũi cao v&agrave; xương h&agrave;m r&otilde; n&eacute;t.</p>\r\n<p class=\"Normal\">Caroline Wilkinson, gi&aacute;m đốc ph&ograve;ng th&iacute; nghiệm Face Lab ở Đại học John Moores, nơi phụ tr&aacute;ch x&acirc;y dựng h&igrave;nh d&aacute;ng của vị pharaoh, chia sẻ về qu&aacute; tr&igrave;nh phục dựng. \"Ch&uacute;ng t&ocirc;i chụp cắt lớp vi t&iacute;nh để x&aacute;c định h&igrave;nh d&aacute;ng 3D của hộp sọ v&agrave; đưa v&agrave;o hệ thống m&aacute;y t&iacute;nh. Sau đ&oacute;, ch&uacute;ng t&ocirc;i nạp v&agrave;o một cơ sở dữ liệu về giải phẫu gương mặt v&agrave; thay đổi cho ph&ugrave; hợp với hộp sọ. Về cơ bản, ch&uacute;ng t&ocirc;i x&acirc;y dựng gương mặt th&ocirc;ng qua cấu tr&uacute;c cơ, c&aacute;c lớp mỡ v&agrave; cuối c&ugrave;ng l&agrave; lớp da\", Wilkinson giải th&iacute;ch.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; dự &aacute;n phục dựng thứ hai m&agrave; Sahar chỉ đạo gần đ&acirc;y sau khi ho&agrave;n th&agrave;nh phục dựng gương mặt pharaoh Tutankhamun c&ugrave;ng với nh&agrave; đi&ecirc;u khắc Christian Corbet. Tuy nhi&ecirc;n, phục dựng gương mặt của một pharaoh đ&atilde; qua đời từ l&acirc;u đi k&egrave;m với nhiều th&aacute;ch thức. V&iacute; dụ, chỉ ri&ecirc;ng hộp sọ kh&ocirc;ng thể tiết lộ mọi mặt diện mạo.</p>\r\n<p class=\"Normal\">\"Kh&oacute; khăn sau khi x&aacute;c định h&igrave;nh d&aacute;ng gương mặt l&agrave; th&ocirc;ng tin về m&agrave;u da, những nốt mụn v&agrave; nếp nhăn, cũng như m&agrave;u t&oacute;c v&agrave; mắt. Trong trường hợp n&agrave;y, ch&uacute;ng t&ocirc;i được Sahar v&agrave; c&aacute;c đồng nghiệp của c&ocirc; ấy gợi &yacute; m&agrave;u mắt, m&agrave;u t&oacute;c v&agrave; m&agrave;u da. Ch&uacute;ng t&ocirc;i cũng lấy th&ocirc;ng tin về pharaoh Ramesses II từ sử s&aacute;ch. Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i cũng t&igrave;m hiểu m&ocirc; mềm gương mặt ở x&aacute;c ướp của &ocirc;ng\", Wilkinson chia sẻ.</p>\r\n<p class=\"Normal\">Qu&aacute; tr&igrave;nh thử nghiệm cũng được kiểm tra với chủ thể sống, cho ph&eacute;p so s&aacute;nh kết quả phục dựng dựa tr&ecirc;n dữ liệu chụp cắt lớp với vật sống.</p>\r\n<p class=\"Normal\">Ramesses II c&ograve;n được biết tới với t&ecirc;n gọi Ramesses Đại đế l&agrave; vị Pharaoh thứ ba của Vương triều thứ 19 trong lịch sử Ai Cập. Theo c&aacute;c t&agrave;i liệu, &ocirc;ng được ghi nhận l&agrave; một trong những pharaoh vĩ đại, quyền lực v&agrave; được ca tụng nhiều nhất trong lịch sử với nhiều chiến thắng lẫy lừng v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh đồ sộ.</p>\r\n</article>\r\n<p>&nbsp;</p>', 5, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (15, '\'Xây dựng chính sách chấp nhận rủi ro trong khoa học\'', '2022-12-05 10:31:09', 444, 'TP HCM - Chính sách về khoa học công nghệ và đổi mới sáng tạo sẽ chú trọng tính vượt trội, chấp nhận rủi ro, theo Bộ trưởng Khoa học Công nghệ Huỳnh Thành Đạt.', '<p class=\"description\">Chính sách v&ecirc;̀ khoa học c&ocirc;ng ngh&ecirc;̣ và đ&ocirc;̉i mới sáng tạo sẽ chú trọng tính vượt tr&ocirc;̣i, ch&acirc;́p nh&acirc;̣n rủi ro, theo B&ocirc;̣ trưởng Khoa học C&ocirc;ng ngh&ecirc;̣ Huỳnh Thành Đạt.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Th&ocirc;ng tin được Bộ trưởng Khoa học v&agrave; C&ocirc;ng nghệ n&oacute;i tại hội nghị thường ni&ecirc;n ph&aacute;t triển khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo v&agrave; nguồn nh&acirc;n lực tổ chức tại TP HCM s&aacute;ng 24/12. Tại đ&acirc;y &ocirc;ng nhắc lại lời cựu Thủ tướng Israel Ehud Barak để thấy trong khoa học v&agrave; s&aacute;ng tạo phải chấp nhận sự rủi ro. \"Nếu kh&ocirc;ng thử l&agrave;m sao biết đ&uacute;ng hay sai. L&agrave;m sao biết con đường đ&oacute; đi đ&uacute;ng hay kh&ocirc;ng. Thất bại sẽ mang tới kinh nghiệm để từ đ&oacute; c&oacute; được th&agrave;nh c&ocirc;ng trong tương lai\".</p>\r\n<p class=\"Normal\">Theo đ&oacute;, cơ chế ch&iacute;nh s&aacute;ch về khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo, đang được Bộ ho&agrave;n thiện theo hướng ch&uacute; trọng \"x&acirc;y dựng thể chế vượt trội, chấp nhận rủi ro v&agrave; độ trễ trong hoạt động khoa học c&ocirc;ng nghệ\". &Ocirc;ng mong c&aacute;c bộ ng&agrave;nh, đơn vị li&ecirc;n quan nỗ lực to&agrave;n diện v&agrave; đồng bộ để th&aacute;o gỡ những bất cập trong hệ thống luật ph&aacute;p v&agrave; ch&iacute;nh s&aacute;ch để khoa học c&ocirc;ng nghệ v&agrave; đổi mới s&aacute;ng tạo phục vụ ph&aacute;t triển kinh tế x&atilde; hội, đặc biệt l&agrave; c&aacute;c r&agrave;o cản, vướng mắc từ c&aacute;c cơ chế, ch&iacute;nh s&aacute;ch về kinh tế, đầu tư, thương mại.</p>\r\n<p class=\"Normal\">\"Nguồn lực x&atilde; hội đầu tư cho khoa học c&ocirc;ng nghệ nhất l&agrave; từ doanh nghiệp cần được tăng cường đầu tư hơn nữa\", &ocirc;ng Đạt n&oacute;i.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Oa2pcFo4aYSGhKaOjmBjRA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8N-bIq43OrzCJN6OjmpvJQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Kgs44ou6i2iYNGdj6RFa8Q 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Oa2pcFo4aYSGhKaOjmBjRA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8N-bIq43OrzCJN6OjmpvJQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Kgs44ou6i2iYNGdj6RFa8Q 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Oa2pcFo4aYSGhKaOjmBjRA\" alt=\"Bộ trưởng Huỳnh Th&agrave;nh Đạt ph&aacute;t biểu tại hội nghị thường ni&ecirc;n về khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo tại ĐH Quốc gia TP HCM s&aacute;ng 24/12. Ảnh: H&agrave; An\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/hoi-nghi-khcn-dhqg-jpg-4991-16-7117-9268-1671870103.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Oa2pcFo4aYSGhKaOjmBjRA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Bộ trưởng Huỳnh Th&agrave;nh Đạt ph&aacute;t biểu tại hội nghị thường ni&ecirc;n về khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo tại ĐH Quốc gia TP HCM s&aacute;ng 24/12. Ảnh:&nbsp;<em>H&agrave; An</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">&Ocirc;ng cho biết, Bộ Khoa học C&ocirc;ng nghệ đang thực hiện t&aacute;i cơ cấu c&aacute;c chương tr&igrave;nh, nhiệm vụ khoa học c&ocirc;ng nghệ gắn với nhu cầu x&atilde; hội, chuỗi gi&aacute; trị của sản phẩm, tạo gi&aacute; trị gia tăng, trong đ&oacute; ch&uacute; trọng lĩnh vực n&ocirc;ng nghiệp. Đẩy mạnh ph&aacute;t triển thị trường khoa học c&ocirc;ng nghệ gắn với x&acirc;y dựng cơ sở dữ liệu quốc gia của ng&agrave;nh. Hoạt động hợp t&aacute;c quốc tế về khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo được đẩy mạnh thời gian tới nhằm ph&aacute;t huy thế mạnh của Việt Nam v&agrave; huy động tối đa nguồn lực quốc tế.</p>\r\n<p class=\"Normal\">Theo Bộ trưởng, trong hoạt động đổi mới s&aacute;ng tạo, doanh nghiệp giữ vai tr&ograve; trung t&acirc;m, viện nghi&ecirc;n cứu v&agrave; trường đại học l&agrave; chủ thể nghi&ecirc;n cứu mạnh. Đ&acirc;y l&agrave; cầu nối để khoa học c&ocirc;ng nghệ phục vụ trực tiếp ph&aacute;t triển kinh tế x&atilde; hội, qua đ&oacute; tạo bứt ph&aacute; trong việc n&acirc;ng cao năng suất, chất lượng, hiệu quả v&agrave; sức cạnh tranh của nền kinh tế, g&oacute;p phần đổi mới m&ocirc; h&igrave;nh tăng trưởng.</p>\r\n<p class=\"Normal\">Người đứng đầu ng&agrave;nh khoa học c&ocirc;ng nghệ cho rằng, cần khuyến kh&iacute;ch c&aacute;c m&ocirc; h&igrave;nh kinh doanh mới, kinh tế số, x&atilde; hội số; n&acirc;ng cao năng lực đổi mới, hấp thụ v&agrave; l&agrave;m chủ c&ocirc;ng nghệ của doanh nghiệp.</p>\r\n<p class=\"Normal\">&Ocirc;ng Vũ Hải Qu&acirc;n, Gi&aacute;m đốc Đại học Quốc gia TP HCM n&ecirc;u thực tế, hiện c&aacute;c thủ tục đ&atilde; c&oacute; nhiều đổi mới nhưng c&aacute;c nh&agrave; khoa học vẫn phải l&agrave;m c&aacute;c c&ocirc;ng việc như một kế to&aacute;n cho ch&iacute;nh c&ocirc;ng tr&igrave;nh của m&igrave;nh. \"Việc thanh quyết to&aacute;n, giải ng&acirc;n đề t&agrave;i mất rất nhiều thời gian thực sự l&agrave; th&aacute;ch thức cho c&aacute;c giảng vi&ecirc;n\", &ocirc;ng Qu&acirc;n n&oacute;i.</p>\r\n<p class=\"Normal\">&Ocirc;ng đề xuất cơ quan chức năng n&ecirc;n x&acirc;y dựng cơ chế đồng gi&aacute;m s&aacute;t c&aacute;c kết quả, đề t&agrave;i nh&agrave; khoa học, để họ tự đ&aacute;nh gi&aacute; ch&eacute;o nghi&ecirc;n cứu của nhau gi&uacute;p giảm bớt thủ tục, tăng t&iacute;nh minh bạch cho c&aacute;c nghi&ecirc;n cứu. \"Quy định luật vi&ecirc;n chức khiến nh&agrave; khoa học kh&ocirc;ng được mở doanh nghiệp, th&agrave;nh lập c&aacute;c spin-off (doanh nghiệp khởi nguồn). Sinh vi&ecirc;n khởi nghiệp kh&ocirc;ng được c&ocirc;ng nhận trong c&aacute;c t&iacute;n chỉ\", &ocirc;ng Qu&acirc;n n&oacute;i kh&oacute; khăn về ch&iacute;nh s&aacute;ch trong hoạt động khởi nghiệp, mong muốn sự thay đổi thể chế để tạo điều kiện thuận lợi cho nh&agrave; khoa học.</p>\r\n<p class=\"Normal\">Ở g&oacute;c độ nh&agrave; khoa học, TS Hồ Nhựt Quang (ĐH Quốc tế TP HCM) kiến nghị cơ chế ch&iacute;nh s&aacute;ch th&uacute;c đẩy li&ecirc;n kết giữa đại học với c&aacute;c ph&ograve;ng nghi&ecirc;n cứu ph&aacute;t triển (R&amp;D) của c&aacute;c tập đo&agrave;n lớn nước ngo&agrave;i tại Việt Nam. &Ocirc;ng cho rằng, c&aacute;c doanh nghiệp c&oacute; nguồn lực cơ sở vật chất, nh&acirc;n lực chất lượng cao với nhiều &yacute; tưởng s&aacute;ng tạo, nhưng họ cũng c&oacute; những vấn đề cần giải quyết. \"Nếu x&acirc;y dựng được ch&iacute;nh s&aacute;ch tạo động lực th&uacute;c đẩy mối quan hệ n&agrave;y, th&igrave; việc ph&aacute;t triển khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo hiệu quả hơn, c&oacute; nh&acirc;n lực với kỹ năng tạo ra &yacute; tưởng s&aacute;ng tạo tốt hơn\", TS Quang n&oacute;i.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VP0u85rpiBpJu2xeiwi6_w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pKDmL48KmID5aItRhljYkQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8gmHp5ZTx0-JXd6bmhW6Xw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VP0u85rpiBpJu2xeiwi6_w 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pKDmL48KmID5aItRhljYkQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8gmHp5ZTx0-JXd6bmhW6Xw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VP0u85rpiBpJu2xeiwi6_w\" alt=\"Nghi&ecirc;n cứu khoa học tại Đại học Quốc tế TP HCM năm 2021. Ảnh: H&agrave; An\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/Hoi-nghi-thuong-nien-2-6321-1671862835.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VP0u85rpiBpJu2xeiwi6_w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nghi&ecirc;n cứu khoa học tại Đại học Quốc tế TP HCM. Ảnh:&nbsp;<em>H&agrave; An</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Kết luận hội nghị, Bộ trưởng Huỳnh Th&agrave;nh Đạt đồng t&igrave;nh với c&aacute;c đ&oacute;ng g&oacute;p của c&aacute;c chuy&ecirc;n gia v&agrave; cho rằng c&aacute;c &yacute; kiến gi&uacute;p Bộ ho&agrave;n thiện c&aacute;c ch&iacute;nh s&aacute;ch về khoa học c&ocirc;ng nghệ đổi mới s&aacute;ng tạo thời gian tới.</p>\r\n<p class=\"Normal\">&Ocirc;ng cho biết, Ch&iacute;nh phủ giao Bộ Khoa học C&ocirc;ng nghệ l&agrave;m việc với c&aacute;c doanh nghiệp, tập đo&agrave;n lớn khuyến kh&iacute;ch họ th&agrave;nh lập c&aacute;c trung t&acirc;m R&amp;D để nghi&ecirc;n cứu, ph&aacute;t triển c&ocirc;ng nghệ, n&acirc;ng cao năng lực cạnh tranh của ch&iacute;nh họ v&agrave; sức cạnh tranh của nền kinh tế trong nước. \"Khi c&aacute;c trung t&acirc;m n&agrave;y h&igrave;nh th&agrave;nh, tạo cơ hội phối hợp với c&aacute;c trường viện với sự hỗ trợ của nh&agrave; nước nhằm th&uacute;c đẩy cho mối quan hệ hợp t&aacute;c n&agrave;y\", Bộ trưởng Đạt n&oacute;i.</p>\r\n<p class=\"Normal\">Hội nghị thường ni&ecirc;n lần 2 ph&aacute;t triển khoa học c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo v&agrave; nguồn nh&acirc;n lực do Bộ Khoa học v&agrave; C&ocirc;ng nghệ, Đại học Quốc gia TP HCM, Đại học Quốc gia H&agrave; Nội, Viện H&agrave;n l&acirc;m Khoa học C&ocirc;ng nghệ v&agrave; Viện H&agrave;n L&acirc;m Khoa học X&atilde; hội Việt Nam tổ chức. Hội nghị nhằm định hướng nghi&ecirc;n cứu, đ&agrave;o tạo gắn với chiến lược ph&aacute;t triển khoa học c&ocirc;ng nghệ đến năm 2030. Đ&acirc;y l&agrave; nơi trao đổi c&aacute;c vấn đề tồn tại, vướng mắc về cơ chế tự chủ của tổ chức khoa học c&ocirc;ng nghệ, trao đổi đề xuất c&aacute;c ch&iacute;nh s&aacute;ch thử nghiệm (sandbox) để th&uacute;c đẩy hoạt động khoa học, c&ocirc;ng nghệ v&agrave; đổi mới s&aacute;ng tạo tại c&aacute;c viện, trường v&agrave; địa phương...</p>\r\n</article>\r\n<p>&nbsp;</p>', 5, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (16, '3 tiểu hành tinh khổng lồ đến gần Trái Đất vào Giáng sinh', '2022-12-25 10:32:14', 234, 'Ba tiểu hành tinh bay qua tương đối gần Trái Đất vào ngày 25/12 nhưng không có nguy cơ va chạm với hành tinh. ', '<p class=\"description\">Ba tiểu h&agrave;nh tinh bay qua tương đối gần Tr&aacute;i Đất v&agrave;o ng&agrave;y 25/12 nhưng kh&ocirc;ng c&oacute; nguy cơ va chạm với h&agrave;nh tinh.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dZcBY3DYPlux5f8mjJS9vA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6JOBWlAD-w7pHmkKa2f3sQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=k8t81SvCtlx_Px35Rz2YBA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dZcBY3DYPlux5f8mjJS9vA 1x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6JOBWlAD-w7pHmkKa2f3sQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=k8t81SvCtlx_Px35Rz2YBA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dZcBY3DYPlux5f8mjJS9vA\" alt=\"M&ocirc; phỏng tiểu h&agrave;nh tinh bay qua Tr&aacute;i Đất. Ảnh: Newsweek\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/24/VNE-As-9143-1671853757.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dZcBY3DYPlux5f8mjJS9vA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">M&ocirc; phỏng tiểu h&agrave;nh tinh bay qua Tr&aacute;i Đất. Ảnh:&nbsp;<em>Newsweek</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ba tiểu h&agrave;nh tinh mang t&ecirc;n 2022 YL1, 2013 YA14 v&agrave; 2022 TE14, c&oacute; đường k&iacute;nh ước t&iacute;nh lần lượt l&agrave; 37,8 &ndash; 84,7 m, 50,9 &ndash; 109 m v&agrave; 95 &ndash; 210 m. Do đ&oacute;, 2022 YL1 c&oacute; k&iacute;ch thước tương đương sải c&aacute;nh của m&aacute;y bay Boeing 777, 2013 YA14 lớn ngang s&acirc;n b&oacute;ng đ&aacute; Mỹ c&ograve;n 2022 TE14 to cỡ t&ograve;a nh&agrave; 50 tầng.</p>\r\n<p class=\"Normal\">Cả 3 tiểu h&agrave;nh tinh quay quanh Mặt Trời, đ&ocirc;i khi tới gần Tr&aacute;i Đất. Phần lớn tiểu h&agrave;nh tinh trong hệ Mặt Trời quay quanh ng&ocirc;i sao ở v&agrave;nh đai tiểu h&agrave;nh tinh, nằm giữa sao Hỏa v&agrave; sao Mộc. Ước t&iacute;nh c&oacute; khoảng 1,1 triệu tiểu h&agrave;nh tinh ở khu vực đ&oacute;.</p>\r\n<p class=\"Normal\">\"Tiểu h&agrave;nh tinh l&agrave; những mảnh vỡ của h&agrave;nh tinh. Tuy nhi&ecirc;n, do tương đối nhỏ, ch&uacute;ng dễ d&agrave;ng chịu t&aacute;c động v&agrave; bay theo quỹ đạo cắt ngang c&aacute;c h&agrave;nh tinh\", Jay Tate, gi&aacute;m đốc đ&agrave;i quan s&aacute;t Trung t&acirc;m Spaceguard ở Anh, cho biết.</p>\r\n<p class=\"Normal\">C&aacute;c tiểu h&agrave;nh tinh 2022 YL1, 2013 YA14 v&agrave; 2022 TE14 lần lượt bay qua Tr&aacute;i Đất ở khoảng c&aacute;ch 0,01959, 0,00691 v&agrave; 0,02872 đơn vị thi&ecirc;n văn, theo cơ sở dữ liệu NEO Earth Close Approaches của NASA. Mỗi đơn vị thi&ecirc;n văn tương đương khoảng c&aacute;ch giữa Tr&aacute;i Đất v&agrave; Mặt Trời, c&oacute; nghĩa 3 tiểu h&agrave;nh tinh lần lượt c&aacute;ch Tr&aacute;i Đất 2.929.000, 1.033.200 v&agrave; 4.296.948 km.</p>\r\n<p class=\"Normal\">D&ugrave; khoảng c&aacute;ch như vậy c&oacute; vẻ cực kỳ xa, c&aacute;c tiểu h&agrave;nh tinh ở tương đối gần Tr&aacute;i Đất về mặt thi&ecirc;n văn. So với ch&uacute;ng, Mặt Trăng c&aacute;ch Tr&aacute;i Đất 384.472 km trong khi h&agrave;nh tinh gần nhất l&agrave; sao Kim ở c&aacute;ch 247,2 triệu km.</p>\r\n<p class=\"Normal\">Nhiều h&agrave;nh tinh bay qua Tr&aacute;i Đất nằm trong danh mục vật thể gần Tr&aacute;i Đất (NEO), dựa theo khoảng c&aacute;ch v&agrave; độ lớn của ch&uacute;ng. Giới nghi&ecirc;n cứu đ&atilde; biết 30.000 NEO. Trong số đ&oacute;, những NEO bay trong phạm vi 7,4 triệu km quanh quỹ đạo Tr&aacute;i Đất v&agrave; c&oacute; đường k&iacute;nh lớn hơn 140 m nằm trong danh mục vật thể c&oacute; khả năng g&acirc;y nguy hiểm. Như vậy, chỉ c&oacute; tiểu h&agrave;nh tinh 2022 TE14 nằm ở nh&oacute;m n&agrave;y.</p>\r\n<p class=\"Normal\">\"Vật thể c&oacute; khả năng g&acirc;y nguy hiểm c&oacute; nghĩa qua nhiều thế kỷ, quỹ đạo của ch&uacute;ng c&oacute; thể tiến h&oacute;a v&agrave; c&oacute; nguy cơ va chạm với Tr&aacute;i Đất\", Paul Chodas, quản l&yacute; Trung t&acirc;m Nghi&ecirc;n cứu NEOS của NASA, giải th&iacute;ch. Theo NASA, kh&ocirc;ng c&oacute; tiểu h&agrave;nh tinh n&agrave;o c&oacute; khả năng đ&acirc;m v&agrave;o Tr&aacute;i Đất trong 100 năm tới.</p>\r\n</article>\r\n<p>&nbsp;</p>', 5, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (17, 'Sinh viên làm máy giảm run tay cho bệnh nhân Parkinson', '2022-12-25 10:33:33', 443, 'Sử dụng sóng hấp thụ các lực nhiễu, nhóm sinh viên trường Đại học Sư phạm Kỹ thuật Đà Nẵng làm thiết bị giúp bệnh nhân Parkinson giảm run tay.', '<p class=\"description\">Sử dụng s&oacute;ng hấp thụ c&aacute;c lực nhiễu, nh&oacute;m sinh vi&ecirc;n trường Đại học Sư phạm Kỹ thuật Đ&agrave; Nẵng l&agrave;m thiết bị gi&uacute;p bệnh nh&acirc;n Parkinson giảm run tay.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Parkinson l&agrave; một trong những bệnh về thần kinh, thường xảy ra khi nh&oacute;m tế b&agrave;o trong n&atilde;o bị tho&aacute;i h&oacute;a, l&agrave;m cho người bệnh kh&ocirc;ng thể kiểm so&aacute;t được vận động của cơ bắp. Điều n&agrave;y khiến họ đi lại kh&oacute; khăn, cử động chậm chạp v&agrave; ch&acirc;n tay bị run cứng, g&acirc;y hạn chế trong vận động v&agrave; sinh hoạt h&agrave;ng ng&agrave;y.</p>\r\n<p class=\"Normal\">Với mong muốn gi&uacute;p những người bệnh Parkinson giảm triệu chứng run tay, hai sinh vi&ecirc;n Bạch Ngọc B&iacute;ch Đ&agrave;o v&agrave; V&otilde; Văn Ho&agrave;ng, khoa cơ kh&iacute;, Đại học Sư phạm Kỹ thuật Đ&agrave; Nẵng nghi&ecirc;n cứu sử dụng s&oacute;ng hấp thụ c&aacute;c lực nhiễu (c&aacute;c lực l&agrave;m run tay) của bệnh nh&acirc;n.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o_JicDU5VcvFrErp5uMYVg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZvGLQ6DLPJrnCgqajorw1A 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dUn63xvtIbkN--0YY7JwaA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o_JicDU5VcvFrErp5uMYVg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZvGLQ6DLPJrnCgqajorw1A 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dUn63xvtIbkN--0YY7JwaA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o_JicDU5VcvFrErp5uMYVg\" alt=\"Găng tay giảm run cho người bệnh Parikinson của nh&oacute;m. Ảnh: H&agrave; An\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-1-5998-1669716762-16-9064-1580-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o_JicDU5VcvFrErp5uMYVg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Găng tay giảm run cho người bệnh Parikinson của nh&oacute;m. Ảnh:&nbsp;<em>H&agrave; An</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo B&iacute;ch Đ&agrave;o, triệu chứng run tay ảnh hưởng đến giao tiếp v&agrave; ăn uống. Người bệnh nặng c&oacute; thể khiến họ run to&agrave;n th&acirc;n. Nh&oacute;m t&igrave;m hiểu v&agrave; nhận thấy hai c&aacute;nh tay l&agrave; bộ phận run đầu ti&ecirc;n. Khi người bệnh cố gắng k&igrave;m tay lại th&igrave; khả năng cao những bộ phận kh&aacute;c như miệng hoặc to&agrave;n th&acirc;n bị run sau đ&oacute;. \"Thực tế n&agrave;y khiến nh&oacute;m l&agrave;m thiết bị hỗ trợ giảm run tay, gi&uacute;p bệnh nh&acirc;n giảm việc k&igrave;m n&eacute;n g&acirc;y run to&agrave;n th&acirc;n\", Đ&agrave;o kể về l&yacute; do l&agrave;m sản phẩm.</p>\r\n<p class=\"Normal\">Với sự hướng dẫn của TS Nguyễn Xu&acirc;n Bảo, giảng vi&ecirc;n khoa cơ kh&iacute;, trường Đại học Sư phạm Kỹ thuật Đ&agrave; Nẵng, nh&oacute;m thiết kế v&agrave; ho&agrave;n thiện thiết bị trong gần một năm. Sản phẩm được t&iacute;ch hợp v&agrave;o găng tay vải với bộ phận ch&iacute;nh l&agrave; một con quay hồi chuyển hoạt động dựa tr&ecirc;n nguy&ecirc;n tắc m&ocirc;-men động lượng. Khi con quay hồi chuyển hoạt động, tạo m&ocirc;-men qu&aacute;n t&iacute;nh sẽ hấp thụ c&aacute;c lực l&agrave;m run tay, gi&uacute;p giảm hiện tượng run ở bệnh nh&acirc;n.</p>\r\n<p class=\"Normal\">Để t&iacute;nh to&aacute;n mức độ hiệu quả, nh&oacute;m sử dụng hai cảm biến đo dao động tần số 3 - 7 Hz. Ph&acirc;n t&iacute;ch tr&ecirc;n m&aacute;y đo h&igrave;nh sin khi thiết bị kh&ocirc;ng hoạt động v&agrave; hoạt động. Kết quả, dao động tr&ecirc;n đồ thị c&oacute; xu hướng đi ngang thể hiện giảm run tay khi thiết bị hoạt động.</p>\r\n<p class=\"Normal\">Thử nghiệm tr&ecirc;n hai bệnh nh&acirc;n Parkinson, cho thấy sử dụng thiết bị mức độ run tay của họ giảm 70%. Bệnh nh&acirc;n c&oacute; cảm gi&aacute;c thoải m&aacute;i, tay c&oacute; thể cầm điện thoại. \"Sản phẩm c&oacute; thể ph&aacute;t huy hiệu quả ngay khi hoạt động\", Đ&agrave;o chia sẻ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ce6Wq8IlwN1C2YNDhf82Ww 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZWQrE-snl7zTctc2eyiybQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=kylmL1nfgEK_Z8X_fG-2Gw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ce6Wq8IlwN1C2YNDhf82Ww 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZWQrE-snl7zTctc2eyiybQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=kylmL1nfgEK_Z8X_fG-2Gw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ce6Wq8IlwN1C2YNDhf82Ww\" alt=\"Đồ thị h&igrave;nh sin đo dao động b&agrave;n tay khi thiết bị hỗ trợ hoạt kh&ocirc;ng hoạt động (h&igrave;nh tr&ecirc;n) v&agrave; hoạt động (h&igrave;nh dưới). Ảnh: NVCC\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-8730-4943-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ce6Wq8IlwN1C2YNDhf82Ww\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đồ thị h&igrave;nh sin đo dao động b&agrave;n tay khi thiết bị hỗ trợ hoạt kh&ocirc;ng hoạt động. Ảnh:&nbsp;<em>NVCC</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lE9HJ3x4kfXiC5-tdw4zNg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gRL0tdsCOuZzAB3e9A1S1w 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=A6NS0IDSLURecmT0bH87AA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lE9HJ3x4kfXiC5-tdw4zNg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gRL0tdsCOuZzAB3e9A1S1w 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=A6NS0IDSLURecmT0bH87AA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lE9HJ3x4kfXiC5-tdw4zNg\" alt=\"Đồ thị h&igrave;nh sin đo dao động b&agrave;n tay khi thiết bị hỗ trợ hoạt kh&ocirc;ng hoạt động (h&igrave;nh tr&ecirc;n) v&agrave; hoạt động (h&igrave;nh dưới). Ảnh: NVCC\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/parkinson-2-2957-1669716762-16-1825-8545-1670813378.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lE9HJ3x4kfXiC5-tdw4zNg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đồ thị h&igrave;nh sin đo dao động b&agrave;n tay khi thiết bị hỗ trợ hoạt động. Ảnh:&nbsp;<em>NVCC</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Nh&oacute;m nghi&ecirc;n cứu cho biết, thời gian tới sẽ cải tiến thiết bị nhỏ gọn hơn v&agrave; thử nghiệm tr&ecirc;n nhiều bệnh nh&acirc;n để đo bi&ecirc;n độ dao động theo từng cấp độ bệnh. Hệ thống gối đỡ v&agrave; khớp quay cũng được nh&oacute;m thiết kế gi&uacute;p con quay hồi chuyển ph&aacute;t huy hiệu quả, khử rung c&aacute;c phương c&ograve;n lại. Thiết bị sẽ được t&iacute;ch hợp đo nhịp tim, huyết &aacute;p, đo sức khỏe v&agrave; căn chỉnh tốc độ rung, cơ chế hoạt động thoải m&aacute;i nhất cho người bệnh.</p>\r\n<p class=\"Normal\">Theo PGS.TS Nguyễn Ngọc L&acirc;m, nguy&ecirc;n Viện ph&oacute; Viện nghi&ecirc;n cứu điện tử - tin học - tự động h&oacute;a (Bộ C&ocirc;ng thương), đ&acirc;y l&agrave; một nghi&ecirc;n cứu kh&aacute; mới ở trong nước, c&oacute; t&iacute;nh ứng dụng cao. Hiện Việt Nam c&oacute; hơn 85.000 người bệnh Parkinson. Tuy nhi&ecirc;n PGS L&acirc;m cho rằng, nh&oacute;m c&ograve;n hạn chế về dữ liệu, số liệu, khảo s&aacute;t tr&ecirc;n nhiều bệnh nh&acirc;n để khẳng định t&iacute;nh hiệu quả về mặt khoa học. Nh&oacute;m mới thực nghiệm tr&ecirc;n chiều h&igrave;nh sin, trong khi việc run tay bệnh nh&acirc;n c&oacute; thể từ nhiều chiều, n&ecirc;n kh&oacute; đ&aacute;nh gi&aacute; hiệu quả. \"Nh&oacute;m c&oacute; thể sử dụng cảm biến gia tốc để t&igrave;m được dao động kh&ocirc;ng phải h&igrave;nh sin\", PGS L&acirc;m n&oacute;i gợi &yacute;, ghi nhận th&agrave;nh c&ocirc;ng bước đầu của nh&oacute;m v&agrave; khuyến kh&iacute;ch tiếp tục đầu tư để ho&agrave;n thiện sản phẩm.</p>\r\n<p class=\"Normal\">Sản phẩm gi&agrave;nh giải nh&igrave; giải Sinh vi&ecirc;n nghi&ecirc;n cứu khoa học Eur&eacute;ka do Th&agrave;nh đo&agrave;n phối hợp Đại học Quốc gia TP HCM tổ chức th&aacute;ng 11/2022.</p>\r\n</article>', 6, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (18, 'Sản phẩm phân bón dành cho nông nghiệp đô thị', '2022-12-25 10:34:24', 223, 'Bộ sản phẩm của PVFCCo chia thành nhiều loại, phù hợp từng nhóm cây trồng tại thành thị như rau ăn lá, rau ăn củ quả, cây ăn quả, hoa - cây cảnh...', '<p class=\"description\">Bộ sản phẩm của PVFCCo chia th&agrave;nh nhiều loại, ph&ugrave; hợp từng nh&oacute;m c&acirc;y trồng tại th&agrave;nh thị như rau ăn l&aacute;, rau ăn củ quả, c&acirc;y ăn quả, hoa - c&acirc;y cảnh...</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Sau một thời gian nghi&ecirc;n cứu, th&iacute; điểm, Tổng c&ocirc;ng ty Ph&acirc;n b&oacute;n v&agrave; H&oacute;a chất Dầu kh&iacute; (PVFCCo) vừa giới thiệu bộ sản phẩm ph&acirc;n b&oacute;n d&agrave;nh cho trồng trọt tại c&aacute;c đ&ocirc; thị.</p>\r\n<p class=\"Normal\">C&aacute;c sản phẩm được đ&oacute;ng g&oacute;i trong bao b&igrave; tiện dụng, nhiều lựa chọn về dung lượng (200 gram, 500 gram, 1 kg), ph&acirc;n chia th&agrave;nh từng loại ri&ecirc;ng cho c&aacute;c nh&oacute;m c&acirc;y trồng phổ biến tại th&agrave;nh thị như rau ăn l&aacute;, rau ăn củ quả, c&acirc;y ăn quả (tr&aacute;i), hoa - c&acirc;y cảnh, với hướng dẫn, khuyến c&aacute;o sử dụng cụ thể.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gTVFZEWx6k1G0PQg9qo5DA 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Tb90yrzhDy1MmHB7gTZJLQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7zDcF7XHwipgr4S0WjizQQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gTVFZEWx6k1G0PQg9qo5DA 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Tb90yrzhDy1MmHB7gTZJLQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7zDcF7XHwipgr4S0WjizQQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gTVFZEWx6k1G0PQg9qo5DA\" alt=\"Thương hiệu Ph&acirc;n b&oacute;n Ph&uacute; Mỹ lần thứ 5 li&ecirc;n tiếp được c&ocirc;ng nhận l&agrave; Thương hiệu Quốc gia\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946551852581-81a18d22b6eebc1-3673-5673-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gTVFZEWx6k1G0PQg9qo5DA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ph&acirc;n b&oacute;n Ph&uacute; Mỹ lần thứ 5 li&ecirc;n tiếp được c&ocirc;ng nhận l&agrave; Thương hiệu Quốc gia.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đơn cử, với c&acirc;y ăn l&aacute;, PVFCCo gợi &yacute; bộ sản phẩm Đạm Ph&uacute; Mỹ+KeBo v&agrave; NPK Ph&uacute; Mỹ+Biowish 16-16-8+13S+TE).</p>\r\n<p class=\"Normal\">Theo doanh nghiệp, Đạm Ph&uacute; Mỹ+KeBo l&agrave; sự kết hợp ph&acirc;n đạm ur&ecirc; cung cấp dinh dưỡng đạm (N) cho c&acirc;y sinh trưởng ph&aacute;t triển tốt v&agrave; bổ sung vi lượng kẽm (Zn) gi&uacute;p c&acirc;y khỏe, Boron (B) gi&uacute;p tăng khả năng thụ phấn, đậu quả.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, NPK Ph&uacute; Mỹ+Biowish 16-16-8+13S+TE) cung cấp c&acirc;n đối dinh dưỡng đạm, l&acirc;n, kali, c&aacute;c chất trung vi lượng v&agrave; chứa b&agrave;o tử vi sinh vật ph&acirc;n giải xenllulo chủng Bacilus spp. mật độ cao (1 x 106 CFU/g). Khi b&oacute;n v&agrave;o đất, c&aacute;c hạt NPK chứa vi sinh n&agrave;y vừa cung cấp dinh dưỡng như NPK th&ocirc;ng thường, đồng thời c&aacute;c vi sinh vật c&oacute; trong hạt ph&acirc;n sẽ sinh s&ocirc;i nảy nở, ph&aacute;t triển trong m&ocirc;i trường đất, hoạt động ph&acirc;n giải chất hữu cơ l&agrave;m cho đất m&agrave;u mỡ, tơi xốp, giữ nước tốt, gi&uacute;p bộ rễ c&acirc;y ph&aacute;t triển.</p>\r\n<p class=\"Normal\">Với rau v&agrave; c&acirc;y ăn củ - quả, hay hoa, c&acirc;y cảnh, nh&atilde;n h&agrave;ng giới thiệu NPK Ph&uacute; Mỹ 20-5-5+TE v&agrave; NPK Ph&uacute; Mỹ 20-20-15+TE).</p>\r\n<p class=\"Normal\">NPK Ph&uacute; Mỹ 20-5-5+TE b&oacute;n v&agrave;o giai đoạn sinh trưởng sinh dưỡng khi c&acirc;y c&ograve;n nhỏ (sau gieo hạt, c&acirc;y con, sau cắt tỉa c&agrave;nh tạo t&aacute;n) gi&uacute;p ph&aacute;t triển th&acirc;n, rễ, l&aacute;, c&agrave;nh hay phục hồi c&acirc;y.</p>\r\n<p class=\"Normal\">NPK Ph&uacute; Mỹ 20-20-15+TE b&oacute;n v&agrave;o giai đoạn sinh trưởng sinh thực gi&uacute;p ra hoa, đậu quả, nu&ocirc;i củ, quả, hạt tăng chất lượng v&agrave; sản lượng sản phẩm c&acirc;y trồng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S5HxdreJbnu8VW6pdS5lvg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RZBpRvwAFQ4jwxPJ4zFITQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=MYkBav9PXsWgh3zZTM6Y4w 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S5HxdreJbnu8VW6pdS5lvg 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RZBpRvwAFQ4jwxPJ4zFITQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=MYkBav9PXsWgh3zZTM6Y4w 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S5HxdreJbnu8VW6pdS5lvg\" alt=\"C&aacute;n bộ kỹ thuật PVFCCo giới thiệu sản phẩm ph&acirc;n b&oacute;n d&agrave;nh n&ocirc;ng nghiệp đ&ocirc; thị với kh&aacute;ch h&agrave;ng\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946503656980-8914a835ef76c1c-6778-2144-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S5HxdreJbnu8VW6pdS5lvg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">C&aacute;n bộ kỹ thuật PVFCCo giới thiệu sản phẩm ph&acirc;n b&oacute;n d&agrave;nh n&ocirc;ng nghiệp đ&ocirc; thị với kh&aacute;ch h&agrave;ng.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ngo&agrave;i bộ 4 sản phẩm chuy&ecirc;n biệt cho từng loại c&acirc;y trồng tr&ecirc;n, PVFCCo c&ograve;n c&oacute; sản phẩm ph&acirc;n hữu cơ (AUTUS) với h&agrave;m lượng hữu cơ cao (85% hữu cơ), sử dụng b&oacute;n l&oacute;t cho nhiều loại đất, gi&uacute;p cải thiện độ ph&igrave; nhi&ecirc;u đất, đất tơi xốp, từ đ&oacute; rễ c&acirc;y ph&aacute;t triển v&agrave; c&acirc;y trồng sẽ khỏe v&agrave; xanh tốt hơn.</p>\r\n<p class=\"Normal\">C&aacute;c sản phẩm ph&acirc;n b&oacute;n n&ocirc;ng nghiệp đ&ocirc; thị Ph&uacute; Mỹ đều dạng một hạt, dễ tan trong nước. Sản phẩm tiện d&ugrave;ng, c&acirc;y trồng dễ hấp thu, hiệu quả nhanh, ph&ugrave; hợp với phương thức canh t&aacute;c đa dạng, quy m&ocirc; kh&aacute;c nhau t&ugrave;y mục đ&iacute;ch v&agrave; điều kiện canh t&aacute;c tr&ecirc;n thửa đất hay trong chậu, ngo&agrave;i trời hay trong nh&agrave; lưới...</p>\r\n<p class=\"Normal\">C&aacute;n bộ kỹ thuật của PVFCCo đồng thời đưa ra một số lưu &yacute; khi b&oacute;n ph&acirc;n cho c&acirc;y trồng. Theo đ&oacute;, mỗi loại c&acirc;y trồng kh&aacute;c nhau c&oacute; nhu cầu dinh dưỡng kh&aacute;c nhau. C&ugrave;ng một loại c&acirc;y trồng, thậm ch&iacute; c&ugrave;ng một giống nhưng nếu trồng tr&ecirc;n c&aacute;c loại đất kh&aacute;c nhau th&igrave; cũng cần c&oacute; những chế độ b&oacute;n ph&acirc;n kh&aacute;c nhau.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fbn3mla_PzN6qdnfMp9JqQ 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sGMuxOx1Sjok7sYVsKi0kA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=P6tLZ5CVdWhM0s08bjrLCg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fbn3mla_PzN6qdnfMp9JqQ 1x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sGMuxOx1Sjok7sYVsKi0kA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=P6tLZ5CVdWhM0s08bjrLCg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fbn3mla_PzN6qdnfMp9JqQ\" alt=\"Hướng dẫn sử dụng d&ograve;ng ph&acirc;n b&oacute;n cho n&ocirc;ng nghiệp đ&ocirc; thị.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/12/z3946580434628-a75456b9e8089ad-3292-7660-1670810268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fbn3mla_PzN6qdnfMp9JqQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hướng dẫn sử dụng d&ograve;ng ph&acirc;n b&oacute;n cho n&ocirc;ng nghiệp đ&ocirc; thị.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Cần b&oacute;n ph&acirc;n c&acirc;n đối v&agrave; hợp l&yacute; dựa tr&ecirc;n đặc điểm sinh l&yacute; v&agrave; nhu cầu dinh dưỡng của c&acirc;y trồng, t&iacute;nh chất từng loại đất v&agrave; điều kiện m&ugrave;a vụ, theo ti&ecirc;u ch&iacute; 4 đ&uacute;ng: đ&uacute;ng loại, đ&uacute;ng liều lượng, đ&uacute;ng l&uacute;c v&agrave; đ&uacute;ng c&aacute;ch.</p>\r\n<p class=\"Normal\">B&oacute;n ph&acirc;n v&agrave;o s&aacute;ng sớm hoặc chiều m&aacute;t, trước thu hoạch 10-14 ng&agrave;y. T&ugrave;y theo tr&igrave;nh trạng sinh trưởng của c&acirc;y, thể t&iacute;ch đất, loại đất, thời vụ c&oacute; thể tăng giảm lượng b&oacute;n ph&ugrave; hợp. B&oacute;n trực tiếp xa gốc c&acirc;y (quanh t&aacute;n c&acirc;y) sau đ&oacute; tưới nước hoặc pha lo&atilde;ng ph&acirc;n b&oacute;n với nước sau đ&oacute; tưới đều quanh gốc, tr&aacute;nh tưới l&ecirc;n l&aacute;.</p>\r\n<p class=\"Normal\">N&ocirc;ng nghiệp đ&ocirc; thị xuất ph&aacute;t l&agrave; m&ocirc; h&igrave;nh tận dụng c&aacute;c diện t&iacute;ch nhỏ, l&ocirc; đất trống, s&acirc;n vườn, thảm cỏ, ban c&ocirc;ng, s&acirc;n thượng, nơi sinh hoạt cộng đồng trong c&aacute;c đ&ocirc; thị để trồng c&acirc;y. Dần dần với sự hỗ trợ của khoa học cộng nghệ v&agrave; nhu cầu ng&agrave;y c&agrave;ng cao của người d&acirc;n, m&ocirc; h&igrave;nh n&agrave;y đ&atilde; được quan t&acirc;m đầu tư trở th&agrave;nh ng&agrave;nh kinh tế sinh th&aacute;i gi&aacute; trị cao trong v&agrave; ven đ&ocirc;.</p>\r\n</article>\r\n<p>&nbsp;</p>', 6, 0, 1, -1, NULL);
INSERT INTO `articles` VALUES (19, 'Xử lý nước thải bằng công nghệ của Nhật Bản, Hàn Quốc', '2022-12-25 10:35:30', 466, 'Công nghệ Jokaso, Nhật Bản và công nghệ GJ-R và GJ-S, Hàn Quốc được các chuyên gia đánh giá phù hợp với hiện trạng môi trường và ô nhiễm nguồn nước tại Việt Nam. ', '<p class=\"description\">C&ocirc;ng nghệ Jokaso, Nhật Bản v&agrave; c&ocirc;ng nghệ GJ-R v&agrave; GJ-S, H&agrave;n Quốc được c&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; ph&ugrave; hợp với hiện trạng m&ocirc;i trường v&agrave; &ocirc; nhiễm nguồn nước tại Việt Nam.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\"><strong>C&ocirc;ng nghệ Jokaso của Nhật Bản</strong></p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=x3sUmSgb8gyfrMxy3F431w 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0M7wo60aXOn-hq03xR3DDg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Q4ByoOcTNWWbKUQv13fLKQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=x3sUmSgb8gyfrMxy3F431w 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0M7wo60aXOn-hq03xR3DDg 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Q4ByoOcTNWWbKUQv13fLKQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=x3sUmSgb8gyfrMxy3F431w\" alt=\"Hệ thống xử l&yacute; nước thải c&ocirc;ng nghệ Jokaso, Nhật Bản. Ảnh: Tập đo&agrave;n Sơn H&agrave;\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-1-Out-7940-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=x3sUmSgb8gyfrMxy3F431w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hệ thống xử l&yacute; nước thải c&ocirc;ng nghệ Jokaso, Nhật Bản. Ảnh:&nbsp;<em>Tập đo&agrave;n Sơn H&agrave;</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Jokaso l&agrave; hệ thống xử l&yacute; nước thải ngay tại nguồn bằng c&ocirc;ng nghệ sinh học Nhật Bản, được sử dụng để lắp đặt trong c&aacute;c biệt thự, hộ gia đ&igrave;nh, khu chung cư cao tầng hay khu đ&ocirc; thị hoặc cho c&aacute;c nh&agrave; h&agrave;ng, kh&aacute;ch sạn, bệnh viện, nh&agrave; m&aacute;y...</p>\r\n<p class=\"Normal\">C&ocirc;ng suất xử l&yacute; từ 1m3 đến v&agrave;i trăm m3 một ng&agrave;y đ&ecirc;m. Hệ thống gi&uacute;p thanh lọc nước thải th&ocirc;ng qua qu&aacute; tr&igrave;nh xử l&yacute; nhờ v&agrave;o c&aacute;c vi sinh vật kỵ kh&iacute; v&agrave; hiếu kh&iacute; để đẩy l&ugrave;i BOD, c&aacute;c chất hữu cơ, v&ocirc; cơ c&ugrave;ng vi khuẩn độc hại kh&aacute;c c&oacute; trong nước thải.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AktwmyVgnMN3jkTy8MrVEg 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=z3EBaxnr5_YwzSB_ywOVug 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=UZdRZZktCILnVwdZRwVouA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AktwmyVgnMN3jkTy8MrVEg 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=z3EBaxnr5_YwzSB_ywOVug 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=UZdRZZktCILnVwdZRwVouA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AktwmyVgnMN3jkTy8MrVEg\" alt=\"Nguy&ecirc;n l&yacute; hoạt động của hệ thống xử l&yacute; nước thải Jokaso.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-ExtractWord-2-Out-2707-1670578795.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AktwmyVgnMN3jkTy8MrVEg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nguy&ecirc;n l&yacute; hoạt động của hệ thống xử l&yacute; nước thải Jokaso.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo nhiều chuy&ecirc;n gia, c&ocirc;ng nghệ n&agrave;y đ&atilde; được nghi&ecirc;n cứu, ho&agrave;n thiện v&agrave; ứng dụng rộng r&atilde;i tại Nhật Bản c&ugrave;ng nhiều nước tr&ecirc;n thế giới. Chất lượng nước sau xử l&yacute; đạt ti&ecirc;u chuẩn quốc tế, c&oacute; thể sử dụng sau khi xử l&yacute; đế tưới c&acirc;y, cứu hỏa, rửa xe... Theo nhiều nghi&ecirc;n cứu, tuổi thọ của hệ thống Jokaso c&oacute; thể l&ecirc;n đến hơn một trăm năm với khả năng chống chịu c&aacute;c chấn động địa chất từ động đất, &iacute;t bị hư hỏng trong m&ocirc;i trường sụt l&uacute;n nền m&oacute;ng, đảm bảo mỹ quan c&ocirc;ng tr&igrave;nh sau lắp đặt.</p>\r\n<p class=\"Normal\"><strong>C&ocirc;ng nghệ GJ-R v&agrave; GJ-S của H&agrave;n Quốc</strong></p>\r\n<p class=\"Normal\">C&ocirc;ng nghệ h&oacute;a l&yacute; (GJ-R) v&agrave; c&ocirc;ng nghệ vi sinh (GJ-S) l&agrave; hai c&ocirc;ng nghệ xử l&yacute; nước thải được ph&aacute;t triển bởi Viện Nghi&ecirc;n cứu nước th&agrave;nh phố Gyeongju v&agrave; đ&atilde; nhận bằng C&ocirc;ng nghệ Xanh của Bộ T&agrave;i Nguy&ecirc;n M&ocirc;i trường H&agrave;n Quốc. C&ocirc;ng nghệ n&agrave;y ph&ugrave; hợp với những l&agrave;ng nghề, nh&agrave; m&aacute;y, khu c&ocirc;ng nghiệp... c&oacute; c&ocirc;ng suất vừa v&agrave; lớn.</p>\r\n<p class=\"Normal\">Ưu điểm của hệ thống l&agrave; thiết bị gọn, kh&ocirc;ng tốn diện t&iacute;ch; vận h&agrave;nh tự động, c&oacute; thể quản l&yacute; từ xa qua internet. \"Tốc độ xử l&yacute; của hệ thống n&agrave;y nhanh hơn c&ocirc;ng nghệ cũ, đặc biệt, chất lượng nước thải sau xử l&yacute; đạt ti&ecirc;u chuẩn cao, loại bỏ hiệu quả chất thải g&acirc;y &ocirc; nhiễm\", chuy&ecirc;n gia ng&agrave;nh nước sạch v&agrave; xử l&yacute; nước thải của Tập đo&agrave;n Sơn H&agrave; cho biết.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=L6-MP7-yyckcRJ4uQwN5IA 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X3wZr4sXFv0tzimB2AcjwA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PzC3f1xFR0DW_ZRNs445sw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=L6-MP7-yyckcRJ4uQwN5IA 1x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X3wZr4sXFv0tzimB2AcjwA 1.5x, https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PzC3f1xFR0DW_ZRNs445sw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=L6-MP7-yyckcRJ4uQwN5IA\" alt=\"Khu thử nghiệm xử l&yacute; nước thải theo c&ocirc;ng nghệ Nhật Bản, H&agrave;n Quốc tại huyện Đ&ocirc;ng Anh - H&agrave; Nội.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/12/09/Image-573113288-ExtractWord-3-7837-6884-1670578796.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=L6-MP7-yyckcRJ4uQwN5IA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Khu thử nghiệm xử l&yacute; nước thải theo c&ocirc;ng nghệ Nhật Bản, H&agrave;n Quốc tại huyện Đ&ocirc;ng Anh - H&agrave; Nội.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Huyện Đ&ocirc;ng Anh, H&agrave; Nội l&agrave; địa phương đầu ti&ecirc;n tr&ecirc;n cả nước chạy thử nghiệm c&ocirc;ng nghệ xử l&yacute; nước thải &aacute;p dụng c&ocirc;ng nghệ Jokaso Nhật Bản, GJ-R v&agrave; GJ-S H&agrave;n Quốc. Cụ thể, th&ocirc;n Thiết &Uacute;ng v&agrave; th&ocirc;n H&agrave; Kh&ecirc;, x&atilde; V&acirc;n H&agrave;, huyện Đ&ocirc;ng Anh đ&atilde; lắp đặt 4 m&ocirc; h&igrave;nh, trong đ&oacute; c&oacute; 2 m&ocirc; h&igrave;nh xử l&yacute; nước thải l&agrave;ng nghề (c&ocirc;ng nghệ l&yacute; h&oacute;a v&agrave; vi sinh), c&oacute; c&ocirc;ng suất tr&ecirc;n 50 - 75m3 một ng&agrave;y đ&ecirc;m; 2 m&ocirc; h&igrave;nh xử l&yacute; nước thải sinh hoạt của hộ d&acirc;n (c&ocirc;ng nghệ vi sinh v&agrave; m&agrave;ng lọc), c&ocirc;ng suất 5m3 một ng&agrave;y đ&ecirc;m.</p>\r\n<p class=\"Normal\">Hiện nay, &ocirc; nhiễm m&ocirc;i trường nước đang c&oacute; xu hướng gia tăng v&agrave; l&agrave; vấn đề đ&aacute;ng b&aacute;o động ở Việt Nam v&agrave; tr&ecirc;n to&agrave;n thế giới. T&igrave;nh trạng n&agrave;y đặc biệt nghi&ecirc;m trọng ở c&aacute;c th&agrave;nh phố lớn do tốc độ đ&ocirc; thị h&oacute;a nhanh v&agrave; mật độ d&acirc;n số đ&ocirc;ng. Tại H&agrave; Nội, khu vực s&ocirc;ng T&ocirc; Lịch được đ&aacute;nh gi&aacute; l&agrave; một trong những nơi c&oacute; nguồn nước bị &ocirc; nhiễm nặng nề nhất th&agrave;nh phố. Mỗi khi đi qua đoạn s&ocirc;ng n&agrave;y, người d&acirc;n c&oacute; thể nh&igrave;n thấy m&agrave;u đen kịt v&agrave; đục ngầu của nước, c&ugrave;ng m&ugrave;i h&ocirc;i kh&oacute; chịu.</p>\r\n<p class=\"Normal\">Theo đ&aacute;nh gi&aacute; từ chuy&ecirc;n gia của Tập đo&agrave;n Sơn H&agrave;, với những c&ocirc;ng nghệ hiện đại đến từ c&aacute;c quốc gia ti&ecirc;n tiến tr&ecirc;n thế giới, việc giải quyết b&agrave;i to&aacute;n nước s&ocirc;ng T&ocirc; Lịch của Thủ đ&ocirc; l&agrave; khả thi, gi&uacute;p t&aacute;i tạo nguồn nước thải, trả lại nguồn nước tinh khiết cho m&ocirc;i trường.</p>\r\n</article>', 6, 0, 1, -1, NULL);
INSERT INTO `articles` VALUES (20, '2022 - năm bùng nổ của du lịch nội địa', '2022-12-25 10:37:15', 678, 'Du lịch Việt Nam ghi nhận sự tăng trưởng mạnh của khách nội địa 2022 khi đạt 101,3 triệu lượt, tăng 168,3% so với kế hoạch, vượt mức trước dịch. ', '<p class=\"description\">Du lịch Việt Nam ghi nhận sự tăng trưởng mạnh của kh&aacute;ch nội địa 2022 khi đạt 101,3 triệu lượt, tăng 168,3% so với kế hoạch, vượt mức trước dịch.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Theo số liệu thống k&ecirc; từ Tổng cục Du lịch, trước Covid-19, lượng kh&aacute;ch nội địa tăng đều qua c&aacute;c năm v&agrave; đạt đỉnh v&agrave;o 2019 với 85 triệu lượt. Năm 2022, sau khi gỡ bỏ mọi hạn chế đi lại, lượng kh&aacute;ch nội địa cả năm đ&atilde; đạt 101,3 triệu lượt, tăng 168,3% so với mục ti&ecirc;u 60 triệu v&agrave; vượt con số của 2019. Ri&ecirc;ng ba th&aacute;ng h&egrave;, lượng kh&aacute;ch đạt hơn 35 triệu. Doanh thu đạt 495.000 tỷ đồng, vượt 23% kế hoạch. Nhiều chuy&ecirc;n gia khẳng định, đ&acirc;y l&agrave; năm hồi sinh của du lịch nội địa.</p>\r\n<div>\r\n<div id=\"chart-5410\" class=\"width_common mb20\" data-component=\"true\" data-component-value=\"5410\" data-highcharts-chart=\"0\">\r\n<div id=\"highcharts-s5c99u5-0\" class=\"highcharts-container \" dir=\"ltr\">Triệu lượtLượng kh&aacute;ch nội địa qua c&aacute;c nămNguồn: Tổng cục Du lịchLượng kh&aacute;ch201520162017201820192020202120220255075100125VnExpress2019●&nbsp;Lượng kh&aacute;ch:&nbsp;85</div>\r\n</div>\r\n</div>\r\n<p class=\"Normal\">&Ocirc;ng Đặng Mạnh Phước, CEO Outbox, đơn vị chuy&ecirc;n nghi&ecirc;n cứu thị trường v&agrave; ph&acirc;n t&iacute;ch dữ liệu du lịch, cho rằng một trong những nguy&ecirc;n nh&acirc;n khiến kh&aacute;ch Việt ưu ti&ecirc;n du lịch nội địa sau Covid-19 v&igrave; việc di chuyển xa c&ograve;n nhiều hạn chế, gi&aacute; cả cao, mức độ an to&agrave;n về dịch bệnh chưa đảm bảo. Thời điểm Việt Nam mở cửa, nhiều nước v&agrave; v&ugrave;ng l&atilde;nh thổ trong khu vực v&agrave; l&acirc;n cận như Singapore, Malaysia, Nhật Bản, H&agrave;n Quốc, Đ&agrave;i Loan... vẫn chưa mở. Th&aacute;i Lan đ&atilde; mở nhưng thủ tục phức tạp, phải x&eacute;t nghiệm v&agrave; nhiều lớp khai b&aacute;o y tế.</p>\r\n<p class=\"Normal\">Theo b&aacute;o c&aacute;o của Outbox ng&agrave;y 21/6, du lịch trong nước từ 1 đến 2 đ&ecirc;m được đ&aacute;nh gi&aacute; &iacute;t g&acirc;y lo ngại nhất (4,34/10 điểm), sau đ&oacute; tới đi lại trong th&agrave;nh phố (4,58/10 điểm), trong nước d&agrave;i hơn 2 đ&ecirc;m (4,68/10 điểm). Trong khi đ&oacute;, việc giải tr&iacute; ở nước ngo&agrave;i vẫn g&acirc;y lo ngại lớn hơn (6,08/10 điểm).</p>\r\n<p class=\"Normal\">\"Đ&oacute; l&agrave; l&yacute; do giải th&iacute;ch v&igrave; sao m&ugrave;a h&egrave; vừa qua, du lịch nội địa nhộn nhịp\", &ocirc;ng Phước cho hay. Nhờ chi ph&iacute; hợp l&yacute;, sản phẩm đa dạng, mức độ an to&agrave;n về dịch bệnh cao, thậm ch&iacute; cả \"du lịch trả th&ugrave;\" cho những th&aacute;ng ng&agrave;y gi&atilde;n c&aacute;ch n&ecirc;n được nhiều du kh&aacute;ch lựa chọn đi trong nước.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qEMpAvCYn_rr61XYLwfEDA 1x, https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UQZAl17AAFoRciQdhn6LOw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=x16ITp1ZB68AuXouopVlMg 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qEMpAvCYn_rr61XYLwfEDA 1x, https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UQZAl17AAFoRciQdhn6LOw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=x16ITp1ZB68AuXouopVlMg 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qEMpAvCYn_rr61XYLwfEDA\" alt=\"Nh&oacute;m du kh&aacute;ch TPHCM nghe h&aacute;t Then v&agrave; trải nghiệm ngồi b&egrave; mảng khi tham quan Tuy&ecirc;n Quang. Ảnh: Vietravel\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/dukhach2-6956-1671786244.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qEMpAvCYn_rr61XYLwfEDA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nh&oacute;m du kh&aacute;ch TPHCM nghe h&aacute;t Then v&agrave; trải nghiệm ngồi b&egrave; mảng khi tham quan Tuy&ecirc;n Quang. Ảnh:&nbsp;<em>Vietravel</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Những con số v&agrave; kết quả khảo s&aacute;t cho thấy, sau Covid-19, du lịch nội địa l&agrave; \"cứu c&aacute;nh\" cho sự phục hồi v&agrave; tăng trưởng của to&agrave;n ng&agrave;nh. Hoạt động vui chơi tại c&aacute;c trung t&acirc;m lớn diễn ra s&ocirc;i động với nhiều sự kiện nổi bật, c&oacute; sức lan tỏa. Nhiều địa phương ghi nhận lượng kh&aacute;ch tăng cao như Ph&uacute; Y&ecirc;n đ&oacute;n hơn 2,2 triệu lượt kh&aacute;ch, tăng hơn 20% so với trước dịch, Ph&uacute; Quốc tăng hơn 25% so với kế hoạch năm.</p>\r\n<p class=\"Normal\">Ghi nhận của&nbsp;<em>VnExpress</em>, hơn 90% cơ sở lưu tr&uacute; tr&ecirc;n cả nước đ&atilde; hoạt động b&igrave;nh thường. Trong dịp cao điểm h&egrave;, c&ocirc;ng suất ph&ograve;ng kh&aacute;ch sạn tăng cao. Những ng&agrave;y cuối tuần mức trung b&igrave;nh cả nước đạt tr&ecirc;n 60%, c&aacute;c ng&agrave;y trong tuần đạt tới 50%. Một số điểm nổi tiếng như Hạ Long (Quảng Ninh), Sa Pa (L&agrave;o Cai), Quy Nhơn (B&igrave;nh Định), Ph&uacute; Quốc (Ki&ecirc;n Giang) c&oacute; thời điểm c&ocirc;ng suất ph&ograve;ng tr&ecirc;n 95%.</p>\r\n<p class=\"Normal\">Ngay sau khi t&igrave;nh h&igrave;nh dịch bệnh được kiểm so&aacute;t, c&aacute;c doanh nghiệp lữ h&agrave;nh cũng đ&atilde; quay lại thị trường. Để đ&aacute;p ứng xu hướng v&agrave; nhu cầu du lịch của kh&aacute;ch nội địa, c&aacute;c c&ocirc;ng ty x&acirc;y dựng nhiều nh&oacute;m sản phẩm.</p>\r\n<p class=\"Normal\">\"Giai đoạn dịch bệnh bắt đầu được kiểm so&aacute;t, c&aacute;c doanh nghiệp lữ h&agrave;nh lớn cũng đ&atilde; c&oacute; sự chuẩn bị kỹ, chạy đ&agrave; từ trước để sẵn s&agrave;ng phục vụ du kh&aacute;ch nội địa ngay khi c&oacute; thể. Kế hoạch kinh doanh thay đổi li&ecirc;n tục theo t&igrave;nh h&igrave;nh thực tế. Trong giai đoạn h&egrave; 2022, ch&uacute;ng t&ocirc;i đ&atilde; phục vụ hơn khoảng 30% lượng kh&aacute;ch nội địa so với năm 2019\", &ocirc;ng Trần Thế Dũng, Tổng gi&aacute;m đốc Lữ h&agrave;nh Fiditour-Vietluxtour, chia sẻ.</p>\r\n<p class=\"Normal\">Cũng theo &ocirc;ng Dũng, kết quả n&agrave;y kh&ocirc;ng bất ngờ v&igrave; thời điểm đ&oacute;, du lịch trong nước l&agrave; thị trường trọng t&acirc;m v&agrave; c&aacute;c doanh nghiệp đều tập trung khai th&aacute;c khi hoạt động outbound (đưa kh&aacute;ch Việt Nam đi nước ngo&agrave;i) chỉ mới giai đoạn khởi động lại. \"C&ugrave;ng với chiến lược r&otilde; r&agrave;ng, kế hoạch cụ thể v&agrave; c&aacute;c đơn vị dịch vụ phối hợp nhịp nh&agrave;ng, c&ugrave;ng hỗ trợ tạo điều kiện để th&uacute;c đẩy du lịch n&ecirc;n thị trường nội địa \'b&ugrave;ng nổ\' dịp h&egrave;\", &ocirc;ng Dũng n&oacute;i th&ecirc;m.</p>\r\n<p class=\"Normal\">C&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng cũng mở lại tất cả c&aacute;c đường bay nội địa v&agrave; tăng tần suất dịp h&egrave;. Từ 1/6 đến 15/8, Vietnam Airlines Group (bao gồm Vietnam Airlines, Pacific Airlines, VASCO) cung cấp gần 6,3 triệu chỗ bay nội địa, tăng 6% so với năm 2019. C&aacute;c chặng giữa H&agrave; Nội, TP HCM, Đ&agrave; Nẵng v&agrave; c&aacute;c điểm du lịch tăng chỗ gần 30% so với cuối th&aacute;ng 3.</p>\r\n<p class=\"Normal\">Vietravel Airlines tăng mỗi ng&agrave;y th&ecirc;m 2 chuyến H&agrave; Nội - TP HCM, 2 chuyến H&agrave; Nội - Đ&agrave; Nẵng, một chuyến H&agrave; Nội - Quy Nhơn, 1-2 chuyến chặng TP HCM - Đ&agrave; Nẵng/Quy Nhơn/Ph&uacute; Quốc. C&aacute;c chuyến bay nội địa trong thời gian từ th&aacute;ng 6 đến th&aacute;ng 8 lu&ocirc;n trong t&igrave;nh trạng đầy chỗ. C&aacute;c chuyến H&agrave; Nội - Đ&agrave; Nẵng v&agrave; TP HCM - Ph&uacute; Quốc được quan t&acirc;m nhiều nhất m&ugrave;a h&egrave;.</p>\r\n<p class=\"Normal\">Bamboo Airways, dịp cao điểm h&egrave; cũng tăng khoảng 15% số chỗ với c&aacute;c chuyến bay tăng cường tới c&aacute;c điểm du lịch như Ph&uacute; Quốc, Đ&agrave; Nẵng, Nha Trang, Quy Nhơn v&agrave; đường bay trục H&agrave; Nội - TP HCM - Đ&agrave; Nẵng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0HJpKY9WPM1u5GfWvTFHhA 1x, https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7gqOFp3KS3ZGhFs7SgGcRg 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9NtMhgYm1K0-BY7n8zdV4Q 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0HJpKY9WPM1u5GfWvTFHhA 1x, https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7gqOFp3KS3ZGhFs7SgGcRg 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9NtMhgYm1K0-BY7n8zdV4Q 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0HJpKY9WPM1u5GfWvTFHhA\" alt=\"H&agrave;nh kh&aacute;ch đợi l&agrave;m thủ tục ở s&acirc;n bay Nội B&agrave;i (H&agrave; Nội) cuối th&aacute;ng 6, giai đoạn cao điểm du lịch. Ảnh: Ngọc Th&agrave;nh\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/sanbay-jpeg-2343-1671857436.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0HJpKY9WPM1u5GfWvTFHhA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">H&agrave;nh kh&aacute;ch đợi l&agrave;m thủ tục ở s&acirc;n bay Nội B&agrave;i (H&agrave; Nội) cuối th&aacute;ng 6, giai đoạn cao điểm du lịch h&egrave;. Ảnh:&nbsp;<em>Ngọc Th&agrave;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo nhận định của c&aacute;c chuy&ecirc;n gia, nội địa vẫn tiếp tục l&agrave; động lực ch&iacute;nh của du lịch Việt Nam trong năm 2023. \"Trong bối cảnh năng lực phục hồi du lịch quốc tế c&ograve;n nhiều hạn chế, nội địa sẽ vẫn rất tiềm năng cho c&aacute;c doanh nghiệp v&agrave; điểm đến khai th&aacute;c\", &ocirc;ng Phước nhận định.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, kh&aacute;c với năm 2022, c&aacute;c chuy&ecirc;n gia cũng cho hay thị trường nội địa năm 2023 sẽ c&oacute; nhiều thử th&aacute;ch hơn khi c&aacute;c nhu cầu kết nối gia đ&igrave;nh v&agrave; nghỉ dưỡng sau dịch bệnh đ&atilde; được giải quyết n&ecirc;n sẽ kh&oacute; c&oacute; sự b&ugrave;ng nổ đột biến. Ngo&agrave;i ra, kinh tế trong nước giai đoạn cuối năm 2022 sẽ ảnh hưởng đến kế hoạch chi ti&ecirc;u của người d&acirc;n đối với hoạt động du lịch trong nửa đầu năm, thậm ch&iacute; cả năm 2023.</p>\r\n<p class=\"Normal\">C&aacute;c doanh nghiệp lữ h&agrave;nh cũng chia sẻ, sự trở lại của du lịch outbound cũng l&agrave; một &aacute;p lực cạnh tranh cho c&aacute;c điểm đến trong nước khi du kh&aacute;ch Việt sẽ c&oacute; xu hướng quay trở lại việc đi nước ngo&agrave;i với mức chi ph&iacute; hợp l&yacute; hơn, những ưu đ&atilde;i r&otilde; r&agrave;ng v&agrave; sản phẩm độc đ&aacute;o, nếu so s&aacute;nh với c&aacute;c sản phẩm đơn thuần nghỉ dưỡng trong nước.</p>\r\n<p class=\"Normal\">Năm 2023, ng&agrave;nh du lịch Việt Nam đặt mục ti&ecirc;u đ&oacute;n 110 triệu lượt kh&aacute;ch, trong đ&oacute; khoảng 102 triệu kh&aacute;ch nội địa v&agrave; 8 triệu lượt kh&aacute;ch quốc tế. Tổng thu từ du lịch 650.000 tỷ đồng.</p>\r\n<p class=\"Normal\">Theo chuy&ecirc;n gia, nhằm th&uacute;c đẩy du lịch nội địa tăng trưởng, cần x&acirc;y dựng sản phẩm hấp dẫn, gia tăng trải nghiệm cho kh&aacute;ch Việt, đa dạng h&oacute;a v&agrave; n&acirc;ng cao chất lượng sản phẩm dịch vụ ph&ugrave; hợp với nhu cầu kh&aacute;ch.</p>\r\n<p class=\"Normal\">\"Du lịch nội địa ng&agrave;y c&agrave;ng ph&aacute;t triển theo hướng bền vững. V&igrave; thế, phải quản l&yacute; tốt c&aacute;c điểm đến, kh&ocirc;ng r&aacute;c v&agrave; nước thải bẩn. Li&ecirc;n kết v&ugrave;ng l&agrave; điều cấp thiết để tạo ra sản phẩm mới, hấp dẫn\", &ocirc;ng Phạm H&agrave;, Chủ tịch Lux Group, n&oacute;i v&agrave; cho biết du lịch li&ecirc;n kết sẽ tạo n&ecirc;n gi&aacute; trị thu h&uacute;t được kh&aacute;ch nội địa.</p>\r\n</article>', 7, 0, 1, -1, NULL);
INSERT INTO `articles` VALUES (21, '\r\niVIVU thiết kế 6 tour quốc tế phục vụ du khách', '2022-12-25 10:38:18', 679, 'Các tour đến Ai Cập, Nam Phi, Maldives, Dubai, Lào, châu Âu của iVIVU có lịch trình đa dạng cùng dịch vụ đáp ứng nhu cầu du khách. ', '<p class=\"description\">C&aacute;c tour đến Ai Cập, Nam Phi, Maldives, Dubai, L&agrave;o, ch&acirc;u &Acirc;u của iVIVU c&oacute; lịch tr&igrave;nh đa dạng c&ugrave;ng dịch vụ đ&aacute;p ứng nhu cầu du kh&aacute;ch.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Theo ch&acirc;n những chuyến h&agrave;nh tr&igrave;nh n&agrave;y, du kh&aacute;ch được đi qua c&aacute;c điểm đến v&agrave; trải nghiệm nhiều điều mới lạ tr&ecirc;n thế giới.</p>\r\n<p class=\"Normal\"><strong>Tour Ai Cập 8 ng&agrave;y 7 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\">Đầu ti&ecirc;n l&agrave; h&agrave;nh tr&igrave;nh đến Ai Cập huyền b&iacute; k&eacute;o d&agrave;i 8 ng&agrave;y 7 đ&ecirc;m bao gồm c&aacute;c hoạt động th&uacute; vị như: trải nghiệm du thuyền 5 sao dọc s&ocirc;ng Nile huyền thoại, chi&ecirc;m ngưỡng cảnh quan h&ugrave;ng vĩ, tham quan kỳ quan Kim Tự Th&aacute;p v&agrave; đền thờ Cairo. Tour c&ograve;n đưa du kh&aacute;ch đến th&agrave;nh phố Luxor - nơi c&oacute; bảo t&agrave;ng ngo&agrave;i trời lớn c&ugrave;ng những di t&iacute;ch c&ograve;n s&oacute;t lại từ thời Thebes cổ đại, hay đắm m&igrave;nh trong v&ugrave;ng biển Đỏ của Ai Cập.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zrsMRUt7VC4uz5XCoqULGA 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=y-dpW0ETovUJh7T3_ShAMA 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=x3-GzfcJC4PnPBGaXvG4EA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zrsMRUt7VC4uz5XCoqULGA 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=y-dpW0ETovUJh7T3_ShAMA 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=x3-GzfcJC4PnPBGaXvG4EA 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zrsMRUt7VC4uz5XCoqULGA\" alt=\"S&ocirc;ng Nile ở Ai Cập. Ảnh: worldatlas\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-83709359-ExtractWord-0-O-6640-3793-1671780206.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zrsMRUt7VC4uz5XCoqULGA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">S&ocirc;ng Nile ở Ai Cập. Ảnh:&nbsp;<em>worldatlas</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 84,99 triệu đồng một kh&aacute;ch.</p>\r\n<p class=\"Normal\"><strong>Tour Nam Phi 8 ng&agrave;y 7 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\">Tour đưa du kh&aacute;ch đến h&agrave;nh tr&igrave;nh cảm nhận bản giao hưởng của hoang d&atilde; v&agrave; hiện đại. Du kh&aacute;ch c&oacute; cơ hội ngắm nh&igrave;n động vật hoang d&atilde; trong cự ly gần, đến th&agrave;nh phố Johannesburg t&igrave;m hiểu văn h&oacute;a - lịch sử sống động của ch&acirc;u Phi đan xen giữa qu&aacute; khứ v&agrave; hiện tại;hay gh&eacute; thăm Cape Town để chi&ecirc;m ngưỡng n&eacute;t đẹp của th&agrave;nh phố thuộc \"top đẹp nhất thế giới\". Đặc biệt, trong chuyến đi đến Johannesburg, du kh&aacute;ch c&oacute; cơ hội chi&ecirc;m ngưỡng m&ugrave;a hoa phượng t&iacute;m Jacaranda rực rỡ tr&ecirc;n khắp phố phường Pretoria.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=REHiq6X2QQGs7_YD_rU6Jg 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KZZzW3lSD6GmMy7wv3VXtw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=KcPqbSoVGkqmy0gE-_haLw 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=REHiq6X2QQGs7_YD_rU6Jg 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KZZzW3lSD6GmMy7wv3VXtw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=KcPqbSoVGkqmy0gE-_haLw 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=REHiq6X2QQGs7_YD_rU6Jg\" alt=\"Th&agrave;nh phố hoa phượng t&iacute;m Pretoria. Ảnh: @richougabriela_photos\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-397559654-ExtractWord-1-5226-7175-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=REHiq6X2QQGs7_YD_rU6Jg\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Th&agrave;nh phố hoa phượng t&iacute;m Pretoria. Ảnh:&nbsp;<em>@richougabriela_photos</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 84,79 triệu đồng một kh&aacute;ch.</p>\r\n<p class=\"Normal\"><strong>Tour Maldives 4 ng&agrave;y 3 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\">Với tour&nbsp;<a href=\"https://www.ivivu.com/du-lich/trai-nghiem-du-lich-maldives-4n3d-thien-duong-bien-mua-he/1740\" target=\"_blank\" rel=\"nofollow noopener\">Maldives</a>, du kh&aacute;ch sẽ khởi h&agrave;nh chuyến đi c&ugrave;ng h&atilde;ng h&agrave;ng kh&ocirc;ng 5 sao Singapore Airlines; c&oacute; cơ hội trải nghiệm những dịch vụ sang trọng tại khu nghỉ dưỡng năm sao v&agrave; tham quan c&aacute;c h&ograve;n đảo đẹp ở Maldives. Một số hoạt động nổi bật m&agrave; du kh&aacute;ch phải thử ở đ&acirc;y như: c&acirc;u c&aacute;, lướt s&oacute;ng, ngắm c&aacute; heo tung tăng bơi lội lặn biển ngắm san h&ocirc;.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=W1V1R6MsYP7GFtZbrx2Szw 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Hh8QKsvvHQof7QgxO9DMoQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=H3RvTej91L97PEk8csEeWg 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=W1V1R6MsYP7GFtZbrx2Szw 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Hh8QKsvvHQof7QgxO9DMoQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=H3RvTej91L97PEk8csEeWg 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=W1V1R6MsYP7GFtZbrx2Szw\" alt=\"Maldives ghi điểm với biển xanh, c&aacute;t trắng, nắng v&agrave;ng. Ảnh: Velassaru Maldives Resort\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-ExtractWord-2-Out-6015-1671780207.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=W1V1R6MsYP7GFtZbrx2Szw\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Maldives ghi điểm với biển xanh, c&aacute;t trắng, nắng v&agrave;ng.<strong>&nbsp;</strong>Ảnh:&nbsp;<em>Velassaru Maldives Resort</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 41,99 triệu đồng một kh&aacute;ch.</p>\r\n<p class=\"Normal\"><strong>Tour Dubai 4 ng&agrave;y 4 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\">Tham gia tour, du kh&aacute;ch được trải nghiệm cuộc sống xa hoa tại kh&aacute;ch sạn Burj Al Arab Jumeirah 7 sao, c&oacute; thiết kế ph&ograve;ng nghỉ nằm ngay dưới đại dương. Điểm đặc biệt trong tour Dubai n&agrave;y l&agrave; du kh&aacute;ch c&oacute; thể trải nghiệm theo sở th&iacute;ch ri&ecirc;ng. Một số điểm đến gợi &yacute; như: sa mạc Safari, đảo cọ nh&acirc;n tạo lớn nhất thế giới Palm Jumeirah, th&aacute;p Burj Khalifa, th&aacute;nh đường Sheikh Zayed Dubai... B&ecirc;n cạnh đ&oacute;, Dubai c&ograve;n nhiều điểm tham quan th&uacute; vị đậm chất văn h&oacute;a, lịch sử v&agrave; t&ocirc;n gi&aacute;o của Trung Đ&ocirc;ng đang chờ bạn kh&aacute;m ph&aacute;.</p>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 73,99 triệu đồng một kh&aacute;ch.</p>\r\n<p class=\"Normal\"><strong>Tour L&agrave;o 5 ng&agrave;y 4 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\">Tour L&agrave;o 5 ng&agrave;y 4 đ&ecirc;m đưa du kh&aacute;ch gh&eacute; thăm những địa điểm du lịch nổi tiếng như: th&aacute;c nước Kuang Si, ch&ugrave;a Xi&ecirc;ng Thoong tại th&agrave;nh phố Luang Prabang v&agrave; hang động Pak Ou. Trong chuyến h&agrave;nh tr&igrave;nh n&agrave;y, du kh&aacute;ch c&oacute; dịp chi&ecirc;m ngưỡng thi&ecirc;n nhi&ecirc;n tươi đẹp quanh ngọn th&aacute;c, những ng&ocirc;i ch&ugrave;a cổ k&iacute;nh c&ugrave;ng v&ocirc; số pho tượng Phật h&igrave;nh d&aacute;ng độc đ&aacute;o.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fT1ngB_bO1q-B6EBuBmAHw 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rj-WjsxcuXxhak9L-Lvfew 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=h35dlGp3wNV6RohKsjEtEg 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fT1ngB_bO1q-B6EBuBmAHw 1x, https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rj-WjsxcuXxhak9L-Lvfew 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=h35dlGp3wNV6RohKsjEtEg 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fT1ngB_bO1q-B6EBuBmAHw\" alt=\"Ch&ugrave;a Xi&ecirc;ng Thoong. Ảnh: freepik\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/Image-217210720-ExtractWord-4-7067-1436-1671780208.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fT1ngB_bO1q-B6EBuBmAHw\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ch&ugrave;a Xi&ecirc;ng Thoong. Ảnh:<em>&nbsp;Freepik</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đến với Luang Prabang, du kh&aacute;ch sẽ chi&ecirc;m ngưỡng ng&ocirc;i đền m&aacute;i bằng v&agrave;ng, nh&agrave; gỗ v&agrave; những t&agrave;n t&iacute;ch t&ograve;a nh&agrave; Ph&aacute;p. Đặc biệt, du kh&aacute;ch c&oacute; thể h&ograve;a m&igrave;nh trải nghiệm phong tục của người d&acirc;n địa phương khi đến đ&acirc;y.</p>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 8,99 triệu đồng một kh&aacute;ch.</p>\r\n<p class=\"Normal\"><strong>Tour ch&acirc;u &Acirc;u 11 ng&agrave;y 10 đ&ecirc;m</strong></p>\r\n<p class=\"Normal\"><a href=\"https://www.ivivu.com/du-lich/tour-chau-au-11n10d-trai-nghiem-phap-thuy-si-y/1668\" target=\"_blank\" rel=\"nofollow noopener\">Tour</a>&nbsp;đưa du kh&aacute;ch trải nghiệm những n&eacute;t lịch sử, văn h&oacute;a, khung cảnh thi&ecirc;n nhi&ecirc;n v&agrave; ẩm thực mang t&iacute;nh biểu trưng ở Ph&aacute;p - Thụy Sĩ - Italy.</p>\r\n<p class=\"Normal\">Đến Ph&aacute;p, du kh&aacute;ch sẽ c&oacute; dịp chi&ecirc;m ngưỡng th&aacute;p Eiffel c&ugrave;ng những đại lộ rộng lớn của Paris, hay gh&eacute; thăm nh&agrave; thờ Notre Dame Strasbourg lớn thứ 4 thế giới. Tại Thụy Sĩ, du kh&aacute;ch c&oacute; cơ hội lạc bước v&agrave;o thị trấn cổ t&iacute;ch Zermatt nhỏ, đắm ch&igrave;m trong vẻ đẹp của những ng&ocirc;i nh&agrave; cổ k&iacute;nh ẩn m&igrave;nh tr&ecirc;n d&atilde;y n&uacute;i h&ugrave;ng vĩ.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, Italy mời gọi du kh&aacute;ch bằng n&eacute;t đẹp l&atilde;ng mạn của th&agrave;nh phố t&igrave;nh y&ecirc;u Venice; n&eacute;t độc đ&aacute;o của th&aacute;p nghi&ecirc;ng Pisa, c&ugrave;ng những điều mới mẻ ở t&ograve;a th&aacute;nh Vatican. B&ecirc;n cạnh đ&oacute;, du kh&aacute;ch đừng qu&ecirc;n thưởng thức những m&oacute;n ăn ngon đặc trưng Italy trong h&agrave;nh tr&igrave;nh.</p>\r\n<p class=\"Normal\">Gi&aacute; tham khảo chỉ từ 75,9 triệu đồng một kh&aacute;ch.</p>\r\n</article>\r\n<p>&nbsp;</p>', 7, 0, 1, -1, NULL);
INSERT INTO `articles` VALUES (22, '\r\n25 điểm đến và trải nghiệm truyền cảm hứng năm 2023', '2022-07-20 10:41:54', 892, 'Muốn trốn chạy cuộc sống ồn ào, tìm đến thiên nhiên, hãy tới Botswana, còn nếu khám phá các di sản xưa cũ, Hy Lạp luôn là nơi chào đón. ', '<p class=\"description\">Muốn trốn chạy cuộc sống ồn &agrave;o, t&igrave;m đến thi&ecirc;n nhi&ecirc;n, h&atilde;y tới Botswana, c&ograve;n nếu kh&aacute;m ph&aacute; c&aacute;c di sản xưa cũ, Hy Lạp lu&ocirc;n l&agrave; nơi ch&agrave;o đ&oacute;n.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Mỗi cuối năm, tạp ch&iacute; Mỹ National Geographic (NatGeo) c&ocirc;ng bố hai dự &aacute;n lớn: c&aacute;c bức ảnh đẹp nhất v&agrave; gợi &yacute; điểm đến, trải nghiệm kh&ocirc;ng thể bỏ qua khi đi du lịch trong năm tới.</p>\r\n<p class=\"Normal\">Sau hai năm dịch bệnh, ti&ecirc;u ch&iacute; mới được NatGeo &aacute;p dụng để chọn điểm đến cho 2023 l&agrave;: những nơi kh&aacute;c biệt, mới mẻ v&agrave; truyền cảm hứng. Đ&oacute; cũng l&agrave; l&yacute; do danh s&aacute;ch của NatGeo năm nay ch&uacute; trọng v&agrave;o c&aacute;c vườn quốc gia v&agrave; động vật hoang d&atilde; khắp thế giới, hoạt động v&agrave; trải nghiệm ngo&agrave;i trời, du lịch xanh, điểm đến ph&ugrave; hợp đa thế hệ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dHJRYvViUj-NkPXzxY_zEA 1x, https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4m1K2nkeZP2lRsjnGr5c3Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qeb5wCGe1PsTe4YA6CWSQQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dHJRYvViUj-NkPXzxY_zEA 1x, https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4m1K2nkeZP2lRsjnGr5c3Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qeb5wCGe1PsTe4YA6CWSQQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dHJRYvViUj-NkPXzxY_zEA\" alt=\"Con t&agrave;u Train des Vignes đi qua hồ Geneva khi di chuyển giữa Vevey v&agrave; Puidoux c&ugrave;ng những vườn nho Lavaux ở Thụy Sĩ. Ảnh: NatGeo\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/6-7507-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dHJRYvViUj-NkPXzxY_zEA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Con t&agrave;u Train des Vignes đi qua hồ Geneva khi di chuyển giữa Vevey v&agrave; Puidoux c&ugrave;ng những vườn nho Lavaux ở Thụy Sĩ. Ảnh:&nbsp;<em>NatGeo</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">25 điểm đến được chia đều ở 5 hạng mục: h&agrave;nh tr&igrave;nh trải nghiệm cho mọi thế hệ (Family); hoạt động ở những nơi mạo hiểm (Adventure); kh&aacute;m ph&aacute; lịch sử v&agrave; di sản (Culture); những nơi hoang d&atilde;, đẹp đẽ (Natural) v&agrave; h&agrave;nh tr&igrave;nh bảo tồn cộng đồng, c&aacute;c điểm đến bền vững (Comunity).</p>\r\n<p class=\"Normal\">Tại hạng mục Comunity, c&aacute;c điểm đến được NatGeo nhắc đến l&agrave; kh&aacute;m ph&aacute; đảo Karoathos,&nbsp;<strong>Hy Lạp</strong>, nơi c&oacute; c&aacute;c dự &aacute;n kinh doanh do phụ nữ l&atilde;nh đạo đang dẫn đầu về t&iacute;nh du lịch bền vững. Ngo&agrave;i ra, bạn h&atilde;y d&agrave;nh một ng&agrave;y cuối tuần ở Milwaukee, Wisconsin,&nbsp;<strong>Mỹ</strong>&nbsp;để tận hưởng kh&ocirc;ng kh&iacute; s&ocirc;i động của người d&acirc;n đại phương. Alberta,&nbsp;<strong>Canada&nbsp;</strong>l&agrave; nơi mang đến cho du kh&aacute;ch trải nghiệm giao thoa giữa truyền thống v&agrave; hiện đại. Nếu muốn dừng ch&acirc;n tại ch&acirc;u &Aacute;, h&atilde;y đến&nbsp;<strong>L&agrave;o</strong>&nbsp;để kh&aacute;m ph&aacute;. V&agrave; nếu dừng ở ch&acirc;u Phi,&nbsp;<strong>Ghana&nbsp;</strong>l&agrave; c&aacute;i t&ecirc;n được nhắc đến. Du kh&aacute;ch đến đ&acirc;y c&oacute; thể kh&aacute;m ph&aacute; di sản của người ch&acirc;u Phi qua c&aacute;c bảo t&agrave;ng, v&agrave; giao lưu với đ&aacute;m đ&ocirc;ng y&ecirc;u th&iacute;ch thời trang ở thủ đ&ocirc; Accra.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JAUPB-I9fpiOacC73uC08w 1x, https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5zEqURjRrs7YkwSOkHd8Kw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zOS78KMZ5TWKrbKf_QbXJA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JAUPB-I9fpiOacC73uC08w 1x, https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5zEqURjRrs7YkwSOkHd8Kw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zOS78KMZ5TWKrbKf_QbXJA 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JAUPB-I9fpiOacC73uC08w\" alt=\"nh&agrave; khảo cổ học Mohamed Megahed đang l&agrave;m c&ocirc;ng việc khai quật tại ng&ocirc;i mộ Khuwy 4.300 năm tuổi ở nghĩa địa Saqqara. Ảnh: NatGeo\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/3-1697-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JAUPB-I9fpiOacC73uC08w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nh&agrave; khảo cổ học Mohamed Megahed đang l&agrave;m c&ocirc;ng việc khai quật tại ng&ocirc;i mộ Khuwy 4.300 năm tuổi ở nghĩa địa Saqqara, Ai Cập. Ảnh:<em>&nbsp;NatGeo</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Nếu muốn chạy trốn đến những nơi c&oacute; thi&ecirc;n nhi&ecirc;n đẹp đẽ (Nature),&nbsp;<strong>Botswana&nbsp;</strong>l&agrave; gợi &yacute; đầu ti&ecirc;n. Nơi đ&acirc;y sẽ khiến bạn ngạc nhi&ecirc;n trước c&aacute;c chương tr&igrave;nh phục hồi những lo&agrave;i c&oacute; nguy cơ tuyệt chủng, tạo h&agrave;nh lang an to&agrave;n cho động vật hoang d&atilde; v&agrave; ph&aacute;t triển c&aacute;c dự &aacute;n du lịch cộng đồng. H&atilde;y n&aacute;n lại ở cao nguy&ecirc;n&nbsp;<strong>Scotland&nbsp;</strong>để t&igrave;m hiểu kỹ hơn về phong tr&agrave;o t&aacute;i thiết nhằm mục đ&iacute;ch kh&ocirc;i phục cảnh quan thi&ecirc;n nhi&ecirc;n v&agrave; hệ động thực vật bản địa. Tại&nbsp;<strong>Slovenia</strong>, người d&acirc;n bắt đầu khai th&aacute;c c&aacute;c tour du lịch ẩm thực, gh&eacute; thăm trang trại, vườn nho, ph&ocirc; mai. Vườn quốc gia Big Bend ở Texas,&nbsp;<strong>Mỹ</strong>&nbsp;v&agrave; v&ugrave;ng Azores,<strong>&nbsp;Bồ Đ&agrave;o Nha</strong>&nbsp;l&agrave; hai c&aacute;i t&ecirc;n c&ograve;n lại nằm trong danh s&aacute;ch.</p>\r\n<p class=\"Normal\">Với du kh&aacute;ch đam m&ecirc; lịch sử, văn h&oacute;a, h&atilde;y gh&eacute; Cairo,&nbsp;<strong>Ai Cập</strong>&nbsp;để tham quan bảo t&agrave;ng Grand Egypt, nh&agrave; mới của vua Tut (Tutankhamun) v&agrave; ngắm tượng nh&acirc;n sư ở Luxor. Bạn cũng c&oacute; thể gh&eacute; Busan,&nbsp;<strong>H&agrave;n Quốc</strong>&nbsp;để tham dự li&ecirc;n hoan phim Busan v&agrave; nh&acirc;m nhi bia thủ c&ocirc;ng. Thực hiện một chuyến h&agrave;nh hương dọc theo \"si&ecirc;u xa lộ\" Appian,&nbsp;<strong>Italy</strong>; kh&aacute;m ph&aacute; văn h&oacute;a - lịch sử th&ocirc;ng qua kh&iacute;a cạnh ẩm thực của th&agrave;nh phố Charleston, South Carolina; chi&ecirc;m ngưỡng một trong những bộ sưu tập tượng đ&aacute; lớn nhất thế giới ở Long M&ocirc;n,&nbsp;<strong>Trung Quốc&nbsp;</strong>l&agrave; c&aacute;c trải nghiệm tiếp theo được nhắc đến.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qwPbZqV6Y_9M-K6IKMaoTA 1x, https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vceO3BxALL6aHCiTrLZ0Rw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BInY8jqsjyVSiRbaje4KbQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qwPbZqV6Y_9M-K6IKMaoTA 1x, https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vceO3BxALL6aHCiTrLZ0Rw 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BInY8jqsjyVSiRbaje4KbQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qwPbZqV6Y_9M-K6IKMaoTA\" alt=\"Cao nguy&ecirc;n Scotland. Ảnh: NatGeo\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/4-2446-1671731789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qwPbZqV6Y_9M-K6IKMaoTA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cao nguy&ecirc;n Scotland. Ảnh:&nbsp;<em>NatGeo</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Tại hạng mục Family, h&atilde;y đến&nbsp;<strong>Thụy Sĩ&nbsp;</strong>v&agrave; đi xe lửa tới c&aacute;c thị trấn cổ k&iacute;nh tr&ecirc;n d&atilde;y Alps để thưởng thức chocolate, đi bộ đường d&agrave;i v&agrave; trượt tuyết c&ugrave;ng nhau. Tham gia giải cứu r&ugrave;a ở<strong>&nbsp;Trinidad &amp; Tobago,</strong>&nbsp;đốt lửa trại tại San Francisco ngắm cầu Cổng v&agrave;ng hay đi bộ đường d&agrave;i ở California,&nbsp;<strong>Mỹ</strong>; ngắm chim ở thi&ecirc;n đường tại&nbsp;<strong>Colombia&nbsp;</strong>l&agrave; c&aacute;c trải nghiệm tiếp theo được nhắc đến. Nếu l&agrave; fan b&oacute;ng đ&aacute;, bạn c&oacute; thể gh&eacute; thăm Manchester,&nbsp;<strong>Anh</strong>&nbsp;để tham quan s&acirc;n b&oacute;ng của một trong những đội nổi tiếng nhất thế giới.</p>\r\n<p class=\"Normal\">Với những người m&ecirc; đi bộ đường d&agrave;i, kh&aacute;m ph&aacute; thi&ecirc;n nhi&ecirc;n, h&atilde;y gh&eacute; Choquequirao, một t&agrave;n t&iacute;ch biệt lập của người Inca tại&nbsp;<strong>Peru</strong>. Ở<strong>&nbsp;New Zealand</strong>, quốc gia ph&aacute;t minh ra tr&ograve; nhảy bungee, h&atilde;y tiếp tục thử sức với tr&ograve; chơi mạo hiểm n&agrave;y. Nếu c&aacute;c địa điểm nhảy bungee ở quốc gia n&agrave;y vẫn chưa khiến bạn cảm thấy đủ hưng phấn, h&atilde;y đến Utah, Mỹ. Tại&nbsp;<strong>&Aacute;o</strong>, h&atilde;y đi bộ xuy&ecirc;n quốc gia để nếm thử ẩm thực v&agrave; kh&aacute;m ph&aacute; văn h&oacute;a địa phương. Cuối c&ugrave;ng, h&atilde;y th&ecirc;m vườn quốc gia Revillagigedo,&nbsp;<strong>Mexico</strong>&nbsp;v&agrave;o h&agrave;nh tr&igrave;nh của bạn. V&ugrave;ng biển được bảo vệ tại nơi đ&oacute; l&agrave; một trong những quần thể c&aacute; mập v&agrave; c&aacute; đuối lớn nhất thế giới.</p>\r\n<p class=\"Normal\">National Geographic l&agrave; tạp ch&iacute; Mỹ của Hiệp hội Địa l&yacute; Quốc gia v&agrave; l&agrave; k&ecirc;nh truyền h&igrave;nh thu ph&iacute;. Tạp ch&iacute; c&oacute; hơn một trăm triệu độc giả v&agrave; h&agrave;ng chục văn ph&ograve;ng tr&ecirc;n khắp thế giới.&nbsp;<a href=\"https://vnexpress.net/anh-du-lich-nam-2022-do-national-geographic-chon-4550056.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_DiemDen_QuocTe-4551600&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-AnhDuLichCuaNationalGeographic&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Ảnh du lịch của National Geographic</a>&nbsp;nhận được hơn 2,2 triệu bức ảnh của 132 nhiếp ảnh gia đến từ 60 quốc gia</p>\r\n</article>\r\n<p>&nbsp;</p>', 7, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (23, '8 món Giáng sinh truyền thống', '2020-05-22 10:43:48', 980, 'Bánh mận, bánh quy gừng hay mì lá là những đồ ăn xuất hiện thường xuyên trong dịp Giáng sinh tại các quốc gia trên thế giới. ', '<p class=\"description\">B&aacute;nh mận, b&aacute;nh quy gừng hay m&igrave; l&aacute; l&agrave; những đồ ăn xuất hiện thường xuy&ecirc;n trong dịp Gi&aacute;ng sinh tại c&aacute;c quốc gia tr&ecirc;n thế giới.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NA0b3Bd8FW2NfH6erZPRXw 1x, https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vuyRvbx_3ffK_feTgknufA 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XpxtVzors2FVHheS_Dh74A 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NA0b3Bd8FW2NfH6erZPRXw 1x, https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vuyRvbx_3ffK_feTgknufA 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XpxtVzors2FVHheS_Dh74A 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NA0b3Bd8FW2NfH6erZPRXw\" alt=\"B&aacute;nh mậnĐ&acirc;y l&agrave; một đặc sản thường thấy v&agrave;o dịp gi&aacute;ng sinh ở ch&acirc;u &Acirc;u, c&oacute; nguồn gốc từ Anh. B&aacute;nh được l&agrave;m bằng c&aacute;ch trộn tr&aacute;i c&acirc;y kh&ocirc;, c&aacute;c loại hạt, một số loại gia vị l&agrave;m ấm, ng&acirc;m trong nhiều ng&agrave;y trong rượu mạnh rồi trộn với bột để tạo khu&ocirc;n. Ảnh: Saltandzest\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/1-2198-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NA0b3Bd8FW2NfH6erZPRXw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>B&aacute;nh mận</strong><br>Đ&acirc;y l&agrave; một đặc sản Gi&aacute;ng sinh ở ch&acirc;u &Acirc;u, đặc biệt l&agrave; Anh v&agrave; Ireland. B&aacute;nh được l&agrave;m bằng c&aacute;ch trộn tr&aacute;i c&acirc;y kh&ocirc;, c&aacute;c loại hạt, một số loại gia vị l&agrave;m ấm, ng&acirc;m trong nhiều ng&agrave;y trong rượu mạnh rồi trộn với bột để tạo khu&ocirc;n. B&aacute;nh mận truyền thống kh&ocirc;ng c&oacute; mận, người ta d&ugrave;ng nho kh&ocirc; v&agrave; c&aacute;c loại quả mọng kh&aacute;c để thay thế. Ảnh:&nbsp;<em>Saltandzest</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U__oZTP6UpChMPFi5do9DA 1x, https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LlcpoXpjpxzbeXYrJLmYrA 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=yvo5b4gzZeVP_LEYWaOADQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U__oZTP6UpChMPFi5do9DA 1x, https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LlcpoXpjpxzbeXYrJLmYrA 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=yvo5b4gzZeVP_LEYWaOADQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U__oZTP6UpChMPFi5do9DA\" alt=\"B&aacute;nh quy gừngL&agrave;m m&oacute;n ăn được trẻ em c&aacute;c nước phương t&acirc;y thường ăn dịp lễ hội cuối năm. Tuy nhi&ecirc;n, m&oacute;n ăn n&agrave;y lại c&oacute; nguồn gốc từ Hy Lạp v&agrave; phục vụ trong c&aacute;c buổi nghi lễ quan trọng. Nguy&ecirc;n liệu l&agrave;m b&aacute;nh gồm gừng, bột mỳ, bột quế, đường, bơ. T&ugrave;y từng đầu bếp, b&aacute;nh c&oacute; thể gồm nhục đậu khấu, đinh hương xay nhuyễn để tăng th&ecirc;m m&ugrave;i vị. Trước đ&acirc;y, b&aacute;nh gừng thường được b&agrave;y b&aacute;n ở tu viện, chợ địa phương với mục đ&iacute;ch ăn cho ấm người, chữa bệnh. Ng&agrave;y nay, n&oacute; phổ biến trong c&aacute;c hiệu b&aacute;nh.\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/Classic-Norwegian-Gingerbread-5086-8399-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U__oZTP6UpChMPFi5do9DA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>B&aacute;nh quy gừng</strong><br>L&agrave;m m&oacute;n ăn được trẻ em c&aacute;c nước phương T&acirc;y thường ăn dịp lễ hội cuối năm. M&oacute;n ăn n&agrave;y c&oacute; nguồn gốc từ Hy Lạp v&agrave; phục vụ trong c&aacute;c buổi nghi lễ quan trọng. Nguy&ecirc;n liệu l&agrave;m b&aacute;nh gồm gừng, bột mỳ, bột quế, đường, bơ. T&ugrave;y từng đầu bếp, b&aacute;nh c&oacute; thể gồm nhục đậu khấu, đinh hương xay nhuyễn để tăng th&ecirc;m m&ugrave;i vị. Trước đ&acirc;y, b&aacute;nh gừng thường được b&agrave;y b&aacute;n ở tu viện, chợ địa phương với mục đ&iacute;ch ăn cho ấm người, chữa bệnh. Ng&agrave;y nay, n&oacute; phổ biến trong c&aacute;c hiệu b&aacute;nh. Ảnh:&nbsp;<em>Ginger with spice</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n9Mo0JfaQoFbgHYCt5Oojg 1x, https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=q7gJKG1OBj_VRcSiAltNog 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XxEF01YMS5aUxhQoVq8XWw 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n9Mo0JfaQoFbgHYCt5Oojg 1x, https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=q7gJKG1OBj_VRcSiAltNog 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XxEF01YMS5aUxhQoVq8XWw 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n9Mo0JfaQoFbgHYCt5Oojg\" alt=\"Stollen Đ&acirc;y l&agrave; một loại b&aacute;nh m&igrave; ngọt, v&agrave; l&agrave; m&oacute;n ăn ch&iacute;nh trong lễ Gi&aacute;ng sinh ở Đức. Thoạt nh&igrave;n, thực kh&aacute;ch sẽ thấy loại b&aacute;nh mỳ n&agrave;y kh&ocirc;, với ruột b&ecirc;n trong tơi thay v&igrave; mịn m&agrave;ng. Nh&acirc;n b&aacute;nh l&agrave; c&aacute;c loại hạt, b&aacute;nh hạnh nh&acirc;n, kẹo tr&aacute;i c&acirc;y mang hương vị rượu rum. Vỏ ngo&agrave;i b&aacute;nh được phủ một lớp đường trắng. Ảnh: Almanac\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/10-3697-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n9Mo0JfaQoFbgHYCt5Oojg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>Stollen</strong><br>Đ&acirc;y l&agrave; một loại b&aacute;nh m&igrave; ngọt, v&agrave; l&agrave; m&oacute;n ăn ch&iacute;nh trong lễ Gi&aacute;ng sinh ở Đức. Loại b&aacute;nh mỳ n&agrave;y kh&ocirc;, với ruột b&ecirc;n trong tơi thay v&igrave; mịn m&agrave;ng. Nh&acirc;n b&aacute;nh l&agrave; c&aacute;c loại hạt, hạnh nh&acirc;n, kẹo tr&aacute;i c&acirc;y mang hương vị rượu rum. Vỏ ngo&agrave;i b&aacute;nh được phủ một lớp đường trắng. Ảnh:&nbsp;<em>Almanac</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DomWrOEUox5ZvLpovLAHog 1x, https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m9PhFzDRHldUXOOMruvntQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=eZIdGRKg_DepNDQj5dCbww 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DomWrOEUox5ZvLpovLAHog 1x, https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m9PhFzDRHldUXOOMruvntQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=eZIdGRKg_DepNDQj5dCbww 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DomWrOEUox5ZvLpovLAHog\" alt=\"B&aacute;nh m&igrave; l&aacute;Loại b&aacute;nh m&igrave; mỏng n&agrave;y rất phổ biến ở Iceland cho lễ Gi&aacute;ng sinh. Th&ocirc;ng thường, b&aacute;nh được l&agrave;m với k&iacute;ch thước lớn để thực kh&aacute;ch c&oacute; thể x&eacute; ra khi ăn. Nguy&ecirc;n liệu l&agrave;m b&aacute;nh gồm: bột được c&aacute;n mỏng, mịn rồi d&ugrave;ng khu&ocirc;n rập hoa văn trang tr&iacute;. Sau đ&oacute;, n&oacute; được chi&ecirc;n ngập dầu cho ch&iacute;n gi&ograve;n th&igrave; vớt ra. Ảnh: Pinterest\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/17-3850-1671815308.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DomWrOEUox5ZvLpovLAHog\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>B&aacute;nh m&igrave; l&aacute;</strong><br>Loại b&aacute;nh m&igrave; mỏng n&agrave;y rất phổ biến ở Iceland cho lễ Gi&aacute;ng sinh. Th&ocirc;ng thường, b&aacute;nh được l&agrave;m với k&iacute;ch thước lớn để thực kh&aacute;ch c&oacute; thể x&eacute; ra khi ăn. Nguy&ecirc;n liệu l&agrave;m b&aacute;nh gồm: bột được c&aacute;n mỏng, mịn rồi d&ugrave;ng khu&ocirc;n rập hoa văn trang tr&iacute;. Sau đ&oacute;, b&aacute;nh được chi&ecirc;n ngập dầu cho ch&iacute;n gi&ograve;n. Ảnh:&nbsp;<em>Pinterest</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KSTXhq_bEquLyf5J3MABSA 1x, https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SZsOGVNOIOiTcuUtTcsgnw 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=kf3Grd-yN0LBR7u6PAP7zA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KSTXhq_bEquLyf5J3MABSA 1x, https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SZsOGVNOIOiTcuUtTcsgnw 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=kf3Grd-yN0LBR7u6PAP7zA 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KSTXhq_bEquLyf5J3MABSA\" alt=\"Kul kulĐ&acirc;y l&agrave; m&oacute;n ăn thường thấy trong c&aacute;c gia đ&igrave;nh người Ấn Độ dịp cuối năm. Ch&uacute;ng được l&agrave;m từ bột, bơ, sữa, đường v&agrave; phủ b&ecirc;n ngo&agrave;i lớp đường đặc v&agrave; được chi&ecirc;n gi&ograve;n trong chảo dầu s&ocirc;i. M&oacute;n b&aacute;nh c&oacute; vỏ ngo&agrave;i gi&ograve;n, b&ecirc;n trong mềm. Ảnh: Local Tak\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/3-5375-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KSTXhq_bEquLyf5J3MABSA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>Kul kul</strong><br>Đ&acirc;y l&agrave; m&oacute;n ăn thường thấy trong c&aacute;c gia đ&igrave;nh người Ấn Độ dịp cuối năm. Ch&uacute;ng được l&agrave;m từ bột, bơ, sữa, đường v&agrave; phủ b&ecirc;n ngo&agrave;i lớp đường đặc v&agrave; được chi&ecirc;n gi&ograve;n trong chảo dầu s&ocirc;i. M&oacute;n b&aacute;nh c&oacute; vỏ ngo&agrave;i gi&ograve;n, b&ecirc;n trong mềm. Ảnh:&nbsp;<em>Local Tak</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ygtq-Kkqxc6VGpagJtvqeA 1x, https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sgnBBdio0ZnpE8K6XlvPWg 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=arCrhZNElhPDIf32el_cxA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ygtq-Kkqxc6VGpagJtvqeA 1x, https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sgnBBdio0ZnpE8K6XlvPWg 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=arCrhZNElhPDIf32el_cxA 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ygtq-Kkqxc6VGpagJtvqeA\" alt=\"EggnogL&agrave; một loại đổ uống lễ được phục vụ phổ biến trong lễ Gi&aacute;ng sinh ở Mỹ. Nguy&ecirc;n liệu để l&agrave;m n&ecirc;n thức uống n&agrave;y l&agrave; sữa, đường, kem v&agrave; trứng, d&ugrave;ng ngon nhất khi ướp lạnh. Ảnh: Neighbor food blog\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/9-9043-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ygtq-Kkqxc6VGpagJtvqeA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>Eggnog</strong><br>L&agrave; một loại đổ uống được phục vụ phổ biến trong lễ Gi&aacute;ng sinh ở Mỹ. Nguy&ecirc;n liệu để l&agrave;m n&ecirc;n thức uống n&agrave;y l&agrave; sữa, đường, kem v&agrave; trứng, d&ugrave;ng ngon nhất khi ướp lạnh. Ảnh:&nbsp;<em>Neighbor food blog</em></p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyMrc19lf2BZJUI-OBY_3g 1x, https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sm5RNUDcQl9bWidX-aEQ4Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=EtwgutfUooyOU-2ZgLuGmA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyMrc19lf2BZJUI-OBY_3g 1x, https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=sm5RNUDcQl9bWidX-aEQ4Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=EtwgutfUooyOU-2ZgLuGmA 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyMrc19lf2BZJUI-OBY_3g\" alt=\"B&ograve; hầmỞ Ireland, bạn sẽ lu&ocirc;n t&igrave;m thấy m&oacute;n hầm n&oacute;ng n&agrave;y như một phần của lễ Gi&aacute;ng sinh. Theo truyền thống, m&oacute;n ăn được ninh nhừ từ thịt cừu v&agrave; c&aacute;c loại rau theo m&ugrave;a. Ảnh: Simple Recipes\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/24/18-5640-1671815309.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyMrc19lf2BZJUI-OBY_3g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><strong>B&ograve; hầm</strong><br>Ở Ireland, bạn sẽ lu&ocirc;n t&igrave;m thấy m&oacute;n hầm n&oacute;ng n&agrave;y như một phần của lễ Gi&aacute;ng sinh. Theo truyền thống, m&oacute;n ăn được ninh nhừ từ thịt cừu v&agrave; c&aacute;c loại rau theo m&ugrave;a. Ảnh:&nbsp;<em>Simple Recipes</em></p>\r\n</figcaption>\r\n</figure>\r\n</article>\r\n<p>&nbsp;</p>', 8, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (24, 'Cách uống rượu vang Giáng sinh đúng cách', '2022-07-14 10:45:03', 789, 'Mulled wine thường được phục vụ trong các cốc gốm sứ có tay cầm và miệng rộng, để uống dễ hơn mà không bị bỏng. ', '<p class=\"description\">Mulled wine thường được phục vụ trong c&aacute;c cốc gốm sứ c&oacute; tay cầm v&agrave; miệng rộng, để uống dễ hơn m&agrave; kh&ocirc;ng bị bỏng.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/ruou-vang-nong-thuc-uong-mua-giang-sinh-4407022.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_AmThuc_QuocTe-4551903&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-MulledWine&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Mulled wine</a>&nbsp;(vang n&oacute;ng) l&agrave; loại đồ uống nổi tiếng v&agrave; phổ biến nhất ở c&aacute;c nước phương T&acirc;y trong m&ugrave;a đ&ocirc;ng, được b&agrave;y b&aacute;n nhiều trong c&aacute;c khu chợ Gi&aacute;ng sinh. Mulled wine thường c&oacute; m&agrave;u đỏ v&igrave; hợp với dịp lễ hội. T&ugrave;y theo từng nước, vang n&oacute;ng c&oacute; c&ocirc;ng thức v&agrave; c&aacute;ch gọi kh&aacute;c nhau. Tại Đức, mọi người gọi l&agrave; Gl&uuml;hwein, tại Bắc &Acirc;u c&oacute; t&ecirc;n l&agrave; Glogg, H&agrave; Lan v&agrave; Bỉ gọi l&agrave; bisschopswijn.</p>\r\n<p class=\"Normal\">Th&agrave;nh phần đồ uống n&agrave;y chủ yếu gồm rượu vang đỏ, cam, quế, hồi, đinh hương, bột nhục đậu khấu, bột quế, đường... sau đ&oacute; cho v&agrave;o nồi đun n&oacute;ng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2ovyMNOLnDsgcQFx5krbWA 1x, https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=saGagqKhd14S_1eo0-q35A 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=znJ70m29AO6iZT4vsX3Baw 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2ovyMNOLnDsgcQFx5krbWA 1x, https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=saGagqKhd14S_1eo0-q35A 1.5x, https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=znJ70m29AO6iZT4vsX3Baw 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2ovyMNOLnDsgcQFx5krbWA\" alt=\"Mulled wine thường c&oacute; m&agrave;u đỏ, v&igrave; m&agrave;u đỏ hợp với kh&ocirc;ng khsi lễ hội v&agrave; nh&igrave;n ấm &aacute;p trong thời tiết lạnh gi&aacute;. Ảnh: Pinterest\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/23/mull1-5508-1671789975.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2ovyMNOLnDsgcQFx5krbWA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Mulled wine thường c&oacute; m&agrave;u đỏ, v&igrave; m&agrave;u đỏ hợp với kh&ocirc;ng khsi lễ hội v&agrave; nh&igrave;n ấm &aacute;p trong thời tiết lạnh gi&aacute;. Ảnh:&nbsp;<em>Pinterest</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Mulled wine phải d&ugrave;ng n&oacute;ng, c&oacute; vị cay, ngọt v&agrave; thơm, được l&agrave;m kh&aacute; đơn giản, d&ugrave; hơi tốn thời gian. V&igrave; thế, mọi người thường chuẩn bị một mẻ lớn v&agrave; chỉ cần giữ ấm cho cả bữa tiệc. C&oacute; ba c&aacute;ch để thưởng thức rượu lu&ocirc;n ấm n&oacute;ng:</p>\r\n<p class=\"Normal\">Chuẩn bị trong nồi nấu chậm, mất khoảng 3 tiếng ở chế độ thấp, nhưng việc giữ ấm rượu sẽ dễ d&agrave;ng hơn nhiều.</p>\r\n<p class=\"Normal\">R&oacute;t rượu đ&atilde; nấu v&agrave;o b&igrave;nh giữ nhiệt. Nếu bạn c&oacute; một b&igrave;nh giữ nhiệt đủ lớn, c&oacute; thể giữ rượu ng&acirc;m trong suốt bữa tiệc. B&igrave;nh thủy điện sẽ giữ ấm tốt v&agrave; dễ d&agrave;ng phục vụ m&agrave; kh&ocirc;ng cần phải v&agrave;o bếp.</p>\r\n<p class=\"Normal\">C&aacute;ch cuối c&ugrave;ng l&agrave; h&acirc;m n&oacute;ng lại. Nếu bạn l&agrave;m rượu trong nồi, l&uacute;c n&agrave;o cũng c&oacute; thể đặt nồi l&ecirc;n bếp v&agrave; đun lại. Nhưng c&oacute; một lưu &yacute;, kh&ocirc;ng được đun s&ocirc;i v&igrave; hương vị của rượu sẽ bị ảnh hưởng, cũng kh&ocirc;ng đun đi đun lại nhiều lần, sẽ mất đi nồng độ cồn.</p>\r\n<p class=\"Normal\">Vang n&oacute;ng thường được d&ugrave;ng k&egrave;m với b&aacute;nh quy, phomai v&agrave; tr&aacute;i c&acirc;y. N&oacute; cũng hợp để ăn k&egrave;m với thịt hoặc x&uacute;c x&iacute;ch nướng. Nếu d&ugrave;ng trong bữa ăn, c&aacute;ch tốt nhất l&agrave; d&ugrave;ng k&egrave;m ph&ocirc; mai mặn, b&aacute;nh nướng thịt băm.</p>\r\n<p class=\"Normal\">Những chiếc cốc đựng mulled wine thường l&agrave; đồ gốm hoặc sứ, c&oacute; quai, gi&uacute;p việc cầm uống dễ d&agrave;ng hơn m&agrave; kh&ocirc;ng bị bỏng tay. Một số nơi sử dụng loại ly thủy tinh cao, d&agrave;y. Miệng cốc thường rộng để c&oacute; thể uống rượu m&agrave; kh&ocirc;ng bị c&aacute;ch m&oacute;n đồ trang tr&iacute; xung quanh như l&aacute;t cam mỏng, thanh quế, hoa hồi cản trở.</p>\r\n<p class=\"Normal\">Mulled wine l&agrave; một phần kh&ocirc;ng thể thiếu trong dịp Gi&aacute;ng sinh kể từ thế kỷ thứ 2. V&agrave;o thời trung cổ, ch&uacute;ng được gọi l&agrave; ypocras hoặc hipocris, đặt theo t&ecirc;n của b&aacute;c sĩ Hippocrates. Rượu tốt cho sức khỏe v&igrave; n&oacute;ng, gi&uacute;p người uống giữ ấm trong m&ugrave;a đ&ocirc;ng lạnh gi&aacute;.</p>\r\n</article>\r\n<p>&nbsp;</p>', 8, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (25, 'Nhà hàng ở Bangkok chế biến món ăn từ hoa', '2022-07-22 10:46:45', 908, 'THÁI LAN Đầu bếp Thierry Drapeau của nhà hàng Signature Bangkok lên thực đơn món ăn từ các loại hoa, thảo mộc và nguyên liệu đặc biệt theo mùa từ đất liền, biển cả. ', '<p class=\"description\">Đầu bếp Thierry Drapeau của nh&agrave; h&agrave;ng Signature Bangkok l&ecirc;n thực đơn m&oacute;n ăn từ c&aacute;c loại hoa, thảo mộc v&agrave; nguy&ecirc;n liệu đặc biệt theo m&ugrave;a từ đất liền, biển cả.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Nh&agrave; h&agrave;ng Ph&aacute;p Signature Bangkok phục vụ ẩm thực theo phong c&aacute;ch hiện đại tại Bangkok vừa được gắn 1 sao Michelin Star, được dẫn dắt bởi đầu bếp Thierry Drapeau.</p>\r\n<p class=\"Normal\">Đại diện nh&agrave; h&agrave;ng chia sẻ, ẩm thực hoa l&agrave; một phần trong triết l&yacute; \"ẩm thực từ đất mẹ\" của đầu bếp Thierry, trong đ&oacute; mỗi th&agrave;nh phần của m&oacute;n ăn đều c&oacute; nguồn gốc r&otilde; r&agrave;ng v&agrave; thể hiện n&eacute;t đặc trưng về v&ugrave;ng đất đ&oacute;.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=15vMlTki-rgWsr4GVr0eLA 1x, https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jNflhZ8d46q6ZM7mEm-JaQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=3buaqesVeajUf_IBSj6EpA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=15vMlTki-rgWsr4GVr0eLA 1x, https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jNflhZ8d46q6ZM7mEm-JaQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=3buaqesVeajUf_IBSj6EpA 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=15vMlTki-rgWsr4GVr0eLA\" alt=\"Đầu bếp Thierry Drapeau c&ugrave;ng c&aacute;c nh&acirc;n vi&ecirc;n tại nh&agrave; h&agrave;ng Signature Bangkok. Ảnh: Signature Bangkok\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/signature-bangkok-is-awarded-1-8484-1746-1671439573.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=15vMlTki-rgWsr4GVr0eLA\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đầu bếp Thierry Drapeau c&ugrave;ng c&aacute;c nh&acirc;n vi&ecirc;n tại nh&agrave; h&agrave;ng Signature Bangkok. Ảnh:&nbsp;<em>Signature Bangkok</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đầu bếp Thierry đ&atilde; s&aacute;ng tạo m&oacute;n ăn dựa theo nguy&ecirc;n tắc l&agrave;m nổi bật hương vị tự nhi&ecirc;n của c&aacute;c loại thịt gia cầm, thịt v&agrave; c&aacute;. Để tr&aacute;nh sử dụng qu&aacute; nhiều bơ v&agrave; gia vị, vị đầu bếp n&agrave;y đ&atilde; lấy c&aacute;c nguồn hoa hiếm, đẹp, ăn được của Th&aacute;i Lan v&agrave; c&aacute;c loại thảo mộc để tăng hương vị cho m&oacute;n ăn. Kết quả, c&aacute;c m&oacute;n ăn Ph&aacute;p do Thierry s&aacute;ng tạo được nhiều thực kh&aacute;ch nhận x&eacute;t tr&ocirc;ng rất bắt mắt, nhẹ nh&agrave;ng...</p>\r\n<p class=\"Normal\"><strong>Thực đơn m&ugrave;a đ&ocirc;ng: sự kết hợp giữa ẩm thực Ph&aacute;p v&agrave; ch&acirc;u &Aacute;</strong></p>\r\n<p class=\"Normal\">Thực đơn m&ugrave;a đ&ocirc;ng của Signature Bangkok được s&aacute;ng tạo dựa tr&ecirc;n nguồn cảm hứng về m&ugrave;a lễ hội ấm &aacute;p c&ugrave;ng sự ấm c&uacute;ng, đo&agrave;n vi&ecirc;n v&agrave; tiết trời se lạnh của m&ugrave;a đ&ocirc;ng.</p>\r\n<p class=\"Normal\">C&aacute;c m&oacute;n ăn được tr&igrave;nh b&agrave;y v&agrave; trang tr&iacute; từ hoa tươi v&agrave; thảo mộc được lấy từ khu vườn trong khu&ocirc;n vi&ecirc;n nh&agrave; h&agrave;ng. Nguy&ecirc;n liệu của c&aacute;c m&oacute;n ngon theo m&ugrave;a được nhập khẩu từ c&aacute;c nh&agrave; cung cấp ở thung lũng Loire của Ph&aacute;p v&agrave; từ Chiang Mai. Thực kh&aacute;ch được nếm thử c&aacute;c loại hoa v&agrave; thảo mộc như hoa c&uacute;c, rau m&ugrave;i, th&igrave; l&agrave; v&agrave; cải c&uacute;c với lối tr&igrave;nh b&agrave;y đẹp mắt, mang đến sự bất ngờ trong suốt bữa ăn.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UyLwvKyOMZVDLIDqiTVh5g 1x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Aimn4D_I-1UpJWhR0pW_Xg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=QV1ktHINWCQQFcCIZms6SA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UyLwvKyOMZVDLIDqiTVh5g 1x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Aimn4D_I-1UpJWhR0pW_Xg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=QV1ktHINWCQQFcCIZms6SA 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UyLwvKyOMZVDLIDqiTVh5g\" alt=\"Cho biết t&ecirc;n m&oacute;n ăn, được l&agrave;m từ nguy&ecirc;n liệu g&igrave;?\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102007-1671426590-7746-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UyLwvKyOMZVDLIDqiTVh5g\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ẩm thực từ hoa được s&aacute;ng tạo bởi đầu bếp Thierry Drapeau. Ảnh:&nbsp;<em>Singature Bangkok</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">L&agrave; người đến tư thung lũng Loire, đầu bếp Thierry đ&atilde; thiết lập mối quan hệ th&acirc;n thiết với n&ocirc;ng d&acirc;n, ngư d&acirc;n v&agrave; những &ocirc;ng chủ vườn nho trong v&ugrave;ng. Ngo&agrave;i ra, anh c&ograve;n l&agrave; bắt tay với Jacques Cavin, một n&ocirc;ng d&acirc;n trồng rau hữu cơ ở v&ugrave;ng đồi ph&iacute;a bắc Chiang Mai. Độ cao v&agrave; nhiệt độ thấp tại trang trại của Jacques Cavin l&agrave; điều kiện th&iacute;ch hợp để trồng rau v&agrave; hoa m&ugrave;a đ&ocirc;ng, trong c&oacute; c&oacute; nhiều loại được trồng đặc biệt d&agrave;nh cho Signature Bangkok.</p>\r\n<p class=\"Normal\">Một điểm nổi bật kh&aacute;c trong thực đơn của Signature Bangkok đ&oacute; l&agrave; \'La Mer\', trong đ&oacute; đầu bếp Thierry phục vụ c&aacute; s&ocirc;ng Loire tươi từ Laurent Daniel, nh&agrave; cung cấp c&aacute; ưa th&iacute;ch của anh ở miền Bắc nước Ph&aacute;p. Sau khi được hấp c&ugrave;ng hoa th&igrave; l&agrave;, hương hồi nhẹ v&agrave; cam thảo, c&aacute; rất ẩm v&agrave; thơm.</p>\r\n<p class=\"Normal\">Với \"Le Pigeon\", đầu bếp Thierry giới thiệu m&oacute;n chim non nướng với sốt l&uacute;a m&igrave; v&agrave; m&egrave; đen. Qu&aacute; tr&igrave;nh chuẩn bị được ho&agrave;n th&agrave;nh với l&aacute; t&iacute;a t&ocirc; đỏ, g&oacute;p phần tạo n&ecirc;n hương vị cam qu&yacute;t nhẹ, phảng phất hương quế, đinh hương v&agrave; bạc h&agrave;.</p>\r\n<p class=\"Normal\">\'Le Fromage\' giới thiệu loạt loại pho m&aacute;t d&ecirc; như Crottin de Chavignol Les, Pouligny Saint Pierre, Valencay. Trong đ&oacute; gồm c&aacute;c loại pho m&aacute;t c&oacute; nguồn gốc từ Les Freres Marchand - một trong những nh&agrave; sản xuất pho m&aacute;t thủ c&ocirc;ng được k&iacute;nh trọng nhất của thung lũng Loire.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pyp5Na-vKw4wqSTXFaQuXQ 1x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4EbAnzJCucooeG1hOIRqxg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=SUCmTFwkNk4ilW3-FsKWfA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pyp5Na-vKw4wqSTXFaQuXQ 1x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4EbAnzJCucooeG1hOIRqxg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=SUCmTFwkNk4ilW3-FsKWfA 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pyp5Na-vKw4wqSTXFaQuXQ\" alt=\"Cho biết t&ecirc;n m&oacute;n ăn, được l&agrave;m từ nguy&ecirc;n liệu g&igrave;?\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/vie-hotel-a2102050-1671426603-5857-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pyp5Na-vKw4wqSTXFaQuXQ\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">C&aacute;c m&oacute;n ăn được tr&igrave;nh b&agrave;y v&agrave; trang tr&iacute; bởi hoa tươi v&agrave; thảo mộc được thu hoạch từ khu vườn trong khu&ocirc;n vi&ecirc;n nh&agrave; h&agrave;ng. Ảnh:<em>&nbsp;Signature Bangkok</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Với m&oacute;n tr&aacute;ng miệng, đầu bếp Thierry giới thiệu Mille Feuille, một m&oacute;n tr&aacute;ng miệng cổ điển của Ph&aacute;p. Phục vụ n&oacute;ng, được chế biến theo c&ocirc;ng thức m&agrave; anh đ&atilde; học c&aacute;ch đ&acirc;y hơn 30 năm tại nh&agrave; h&agrave;ng Gilles. Anh sẽ điều chỉnh m&oacute;n tr&aacute;ng miệng mỗi tối để ph&ugrave; hợp với t&acirc;m trạng v&agrave; sự sẵn c&oacute; của c&aacute;c loại thảo mộc v&agrave; gia vị. Sự ngạc nhi&ecirc;n v&agrave; vui tươi l&agrave; điều kh&ocirc;ng thể thiếu trong nguồn cảm hứng s&aacute;ng tạo m&oacute;n ăn của vị đầu bếp n&agrave;y.</p>\r\n<p class=\"Normal\">Thực đơn 8 m&oacute;n bắt đầu với m&oacute;n Amuse Bouche, tiếp theo l&agrave; Le Ferme, La Mer (c&aacute;), Le Pigeon (chim non), Le Fromage (ph&ocirc; mai) v&agrave; Plaisairs Sucr&eacute; (m&oacute;n tr&aacute;ng miệng), c&ugrave;ng với m&oacute;n amuse bouche v&agrave; c&aacute;c m&oacute;n chuyển tiếp.</p>\r\n<p class=\"Normal\">Thực đơn m&ugrave;a đ&ocirc;ng hiện tại của Signature Bangkok c&oacute; c&aacute;c lựa chọn 5 v&agrave; 8 m&oacute;n, thực đơn chay 5 m&oacute;n, được phục vụ v&agrave;o bữa tối từ thứ Ba đến Chủ nhật, giới hạn cho 30 kh&aacute;ch mỗi buổi tối.</p>\r\n<p class=\"Normal\"><strong>Trải nghiệm bữa tối đặc biệt trong \"nh&agrave; h&aacute;t\"</strong></p>\r\n<p class=\"Normal\">Nằm tr&ecirc;n tầng 11 của VIE Hotel Bangkok, M-Gallery, một kh&aacute;ch sạn 5 sao sang trọng thuộc Accor tại trung t&acirc;m Bangkok, Signature Bangkok sở hữu tầm nh&igrave;n to&agrave;n cảnh ra một trong những th&agrave;nh phố th&uacute; vị nhất ch&acirc;u &Aacute;. Nội thất của nh&agrave; h&agrave;ng được trang tr&iacute; theo phong c&aacute;ch nghệ thuật với vải nhung, thảm v&agrave; t&ocirc;ng m&agrave;u đậm cổ điển, trong khi cửa sổ bằng k&iacute;nh suốt trải d&agrave;i từ trần đến s&agrave;n v&agrave; đồ đạc bằng đồng đ&aacute;nh b&oacute;ng mang đến kh&ocirc;ng gian hiện đại.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ebJdlRFbCnJWLG3chSuNyg 1x, https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FdnZGMxuNuRsyesmZWAcqg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BWSPhzKnFPwdjeeb3Zm_sA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ebJdlRFbCnJWLG3chSuNyg 1x, https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FdnZGMxuNuRsyesmZWAcqg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BWSPhzKnFPwdjeeb3Zm_sA 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ebJdlRFbCnJWLG3chSuNyg\" alt=\"Nội thất của nh&agrave; h&agrave;ng Signature Bangkok được trang tr&iacute; theo t&ocirc;ng m&agrave;u đậm cổ điển. Ảnh: Signature Bangkok\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/Signature-01-4348-1671439574.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ebJdlRFbCnJWLG3chSuNyg\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nội thất của nh&agrave; h&agrave;ng Signature Bangkok được trang tr&iacute; theo t&ocirc;ng m&agrave;u đậm cổ điển. Ảnh:&nbsp;<em>Signature Bangkok</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Lấy cảm hứng từ nh&agrave; h&aacute;t, nơi những c&acirc;u chuyện được mang l&ecirc;n s&acirc;n khấu để biểu diễn cho kh&aacute;n giả, nh&agrave; bếp của Signature Bangkok được thiết kế mở ra ph&ograve;ng ăn, giống như s&acirc;n khấu trong nh&agrave; h&aacute;t, l&agrave; nơi c&aacute;c đầu bếp thể hiện t&agrave;i năng trước thực kh&aacute;ch. \"Kh&aacute;n giả\" được khuyến kh&iacute;ch tương t&aacute;c với c&aacute;c đầu bếp v&agrave; ngắm nh&igrave;n c&aacute;c \"nghệ sĩ\" thể hiện t&agrave;i năng nghệ thuật của m&igrave;nh trong khi chế biến m&oacute;n ăn.</p>\r\n<p class=\"Normal\">Thực kh&aacute;ch sẽ bắt đầu h&agrave;nh tr&igrave;nh trải nghiệm ẩm thực mỹ vị trong sảnh chơi piano sang trọng, phủ nhung của nh&agrave; h&agrave;ng. Tại đ&acirc;y, thực kh&aacute;ch sẽ được ch&agrave;o đ&oacute;n bằng c&aacute;c m&oacute;n khai vị kết hợp với rượu khai vị, rượu s&acirc;m panh v&agrave; rượu vang sủi. Bữa ăn ch&iacute;nh diễn ra trong ph&ograve;ng ăn của nh&agrave; h&agrave;ng nh&igrave;n ra đường ch&acirc;n trời th&agrave;nh phố Bangkok. Đặc biệt, hầm rượu của Signature Bangkok l&agrave; nơi lưu giữ c&aacute;c loại rượu vang qu&yacute; từ khắp nơi tr&ecirc;n thế giới.</p>\r\n</article>\r\n<p>&nbsp;</p>', 8, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (26, 'Bốn xu hướng du lịch phổ biến thế giới năm 2022', '2022-03-15 10:48:24', 712, 'Sau Covid-19, nhiều thói quen du lịch thay đổi, nhiều xu hướng mới xuất hiện và nhu cầu ngày càng lớn. ', '<p class=\"description\">Sau Covid-19, nhiều th&oacute;i quen du lịch thay đổi, nhiều xu hướng mới xuất hiện v&agrave; nhu cầu ng&agrave;y c&agrave;ng lớn.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Dưới đ&acirc;y l&agrave; những xu hướng được c&aacute;c tạp ch&iacute; lớn tr&ecirc;n thế giới giới thiệu trong năm vừa qua như xu hướng mới của thế giới.</p>\r\n<p class=\"Normal\"><strong>Du lịch xanh</strong></p>\r\n<p class=\"Normal\">Sau hơn 2 năm ng&agrave;nh du lịch to&agrave;n cầu đ&oacute;ng băng, người d&acirc;n phải ở nh&agrave; c&aacute;ch ly, việc di chuyển, lưu tr&uacute; v&agrave; thủ tục x&eacute;t nghiệm g&acirc;y ra nhiều bất lợi khiến h&agrave;nh vi của con người cũng thay đổi. Thay v&igrave; chọn du lịch nghỉ dưỡng đắt đỏ như trước, kh&aacute;ch du lịch quan t&acirc;m tới du lịch xanh - loại h&igrave;nh dựa v&agrave;o việc ph&aacute;t triển tự nhi&ecirc;n v&agrave; văn h&oacute;a, hạn chế tối đa t&aacute;c động xấu tới m&ocirc;i trường như xả thải, x&acirc;m hại v&agrave; b&oacute;c lột động thực vật, khắc phục hậu quả do biến đổi kh&iacute; hậu, sử dụng năng lượng t&aacute;i tạo v&agrave; ph&aacute;t huy c&aacute;c di sản thi&ecirc;n nhi&ecirc;n văn h&oacute;a, c&aacute;c sản phẩm th&acirc;n thiện với m&ocirc;i trường.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dO_hft5NlhYXi2PZ8uM40w 1x, https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vmfnj1PXIyrXPWUX7d_1Tw 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=VQvhpcVP-zRuyRr7eow_7w 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dO_hft5NlhYXi2PZ8uM40w 1x, https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=vmfnj1PXIyrXPWUX7d_1Tw 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=VQvhpcVP-zRuyRr7eow_7w 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dO_hft5NlhYXi2PZ8uM40w\" alt=\"Xu hướng du lịch xanh. Ảnh: Travel Channel\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/xuhuong-4139-1671442920.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dO_hft5NlhYXi2PZ8uM40w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Xu hướng du lịch xanh. Ảnh:&nbsp;<em>Travel Channel</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Một nghi&ecirc;n cứu v&agrave;o th&aacute;ng 8 của C&ocirc;ng ty tư vấn Anh c&oacute; trụ sở tại 27 quốc gia tr&ecirc;n thế giới,&nbsp;<em>Simon-Kucher &amp; Partners</em>, cho thấy 61% số người d&acirc;n nước n&agrave;y khi được hỏi đều sẵn s&agrave;ng trả nhiều tiền hơn cho một chuyến du lịch nghỉ dưỡng th&acirc;n thiện với m&ocirc;i trường so với kiểu truyền thống. Họ chấp nhận di chuyển bằng những phương tiện giao th&ocirc;ng xả ra &iacute;t kh&iacute; thải hơn như xe điện, xe đạp trong qu&aacute; tr&igrave;nh đi nghỉ dưỡng, hạn chế tối đa đồ ăn nhanh, cốc nhựa, chai nhựa v&agrave; việc sử dụng vật liệu t&aacute;i chế được ưu ti&ecirc;n.</p>\r\n<p class=\"Normal\">Theo&nbsp;<em>Travel Channel</em>, xu hướng du lịch n&agrave;y kh&ocirc;ng chỉ mang lại &yacute; nghĩa t&iacute;ch cực tới m&ocirc;i trường, thi&ecirc;n nhi&ecirc;n m&agrave; c&ograve;n mang lại nguồn lợi kinh tế to lớn, n&acirc;ng cao cơ hội việc l&agrave;m cho những người d&acirc;n sinh sống ở những v&ugrave;ng n&uacute;i, v&ugrave;ng s&acirc;u v&ugrave;ng xa nơi c&ograve;n c&oacute; nhiều lạc hậu nhưng c&oacute; bề d&agrave;y văn ho&aacute; v&agrave; cảnh quan thi&ecirc;n nhi&ecirc;n hoang sơ.</p>\r\n<p class=\"Normal\"><strong>Du lịch kết hợp l&agrave;m việc từ xa</strong></p>\r\n<p class=\"Normal\">\"Digital Nomad\" (<a href=\"https://vnexpress.net/du-muc-ky-thuat-so-phat-trien-sau-covid-19-4490134.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_DauChan-4550083&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-DuMucKyThuatSo&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Du mục kỹ thuật số</a>) l&agrave; một thuật ngữ tiếng Anh chỉ những người l&agrave;m việc từ xa đến từ nhiều nơi. Những người n&agrave;y người kiếm tiền th&ocirc;ng qua m&ocirc;i trường Internet trong c&aacute;c lĩnh vực như: marketing, s&aacute;ng tạo nội dung, IT, tư vấn vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n hay thiết kế. Họ ho&agrave;n to&agrave;n độc lập v&agrave; kh&ocirc;ng bị r&agrave;ng buộc bởi những c&ocirc;ng việc văn ph&ograve;ng hay l&agrave;m việc tại nh&agrave;. Họ thường kh&ocirc;ng ở cố định tại một nơi m&agrave; đi khắp nơi vừa tận hưởng vừa thay đổi kh&ocirc;ng gian l&agrave;m việc.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UgGOrgBam-Mn2CeQ1vm3xg 1x, https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MP3SAvr6ddOzqZ5kC94hwQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=tUevci-S1pmafqu8QTK7Zw 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UgGOrgBam-Mn2CeQ1vm3xg 1x, https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MP3SAvr6ddOzqZ5kC94hwQ 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=tUevci-S1pmafqu8QTK7Zw 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UgGOrgBam-Mn2CeQ1vm3xg\" alt=\"Ảnh: Raconteur\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/2-Xu-huong-du-lich-ket-hop-lam-8400-3658-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UgGOrgBam-Mn2CeQ1vm3xg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ng&agrave;y c&agrave;ng nhiều người trẻ theo xu hướng l&agrave;m việc từ xa. Ảnh:&nbsp;<em>Raconteur</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Xu hướng l&agrave;m việc từ xa n&agrave;y c&agrave;ng trở n&ecirc;n phố kể từ sau Covid-19, nhận được nhiều sự quan t&acirc;m, đặc biệt của giới trẻ. Theo tạp ch&iacute;&nbsp;<em>Forbes,&nbsp;</em>hiện tr&ecirc;n to&agrave;n cầu ước t&iacute;nh c&oacute; khoảng 4,8 triệu du mục kỹ thuật số, trong đ&oacute; thế hệ Gen Y v&agrave; Gen Z chiếm hơn 50%. Một cuộc khảo s&aacute;t kh&aacute;c của&nbsp;<em>Gallup</em>&nbsp;Mỹ, c&oacute; tới hai phần ba số người tham gia chia sẻ rằng họ muốn duy tr&igrave; lối sống l&agrave;m việc từ xa cũng ngay cả sau khi đại dịch Covid đ&atilde; qua đi.</p>\r\n<p class=\"Normal\">C&oacute; nhiều l&yacute; do để giải th&iacute;ch cho sức h&uacute;t của xu hướng n&agrave;y với giới trẻ hiện nay như: m&ocirc;i trường v&agrave; thời gian l&agrave;m việc kh&ocirc;ng g&ograve; b&oacute;, giảm bớt căng thẳng v&agrave; &aacute;p lực c&ocirc;ng việc, được gặp gỡ nhiều nền văn ho&aacute; v&agrave; kh&aacute;m ph&aacute; nhiều điều mới mẻ mở rộng tầm hiểu biết của bản th&acirc;n.</p>\r\n<p class=\"Normal\"><strong>Du lịch ngủ</strong></p>\r\n<p class=\"Normal\">Kh&aacute;i niệm \"Sleep tourism\" (<a href=\"https://vnexpress.net/di-du-lich-de-ngu-4520135.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_DauChan-4550083&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-DuLichNgu&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">du lịch ngủ</a>) chỉ những du kh&aacute;ch chọn đi du lịch chỉ để ngủ trong nh&agrave; nghỉ, homestay, kh&aacute;ch sạn. Theo nghi&ecirc;n cứu của nền tảng&nbsp;<em>Sleep Foundation</em>, ước t&iacute;nh c&oacute; tới 50 đến 70 triệu người Mỹ trưởng th&agrave;nh bị mắc hội chứng rối loạn giấc ngủ. Nghi&ecirc;n cứu kh&aacute;c tr&ecirc;n Tạp ch&iacute; y học về giấc ngủ l&acirc;m s&agrave;ng thống k&ecirc; hơn 40% trong số hơn 2.500 người lớn tham gia khảo s&aacute;t cho biết chất lượng giấc ngủ của họ bị suy giảm kể từ khi mắc Covid-19. Nắm bắt nhu cầu đ&oacute; nhiều cơ sở du lịch tr&ecirc;n thế giới đ&atilde; tập trung ph&aacute;t triển cơ sở hạ tầng để đ&aacute;p ứng nhu cầu n&agrave;y v&agrave; đặc biệt hướng tới phục vụ những người bị thiếu ngủ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v6b_SOycZ47Co6c5rMrouQ 1x, https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U8fhPv5YgfUsLPa2zgrpFg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=LkBtwqCRIGemtfoWwVjjgA 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v6b_SOycZ47Co6c5rMrouQ 1x, https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U8fhPv5YgfUsLPa2zgrpFg 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=LkBtwqCRIGemtfoWwVjjgA 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v6b_SOycZ47Co6c5rMrouQ\" alt=\"Ảnh: CNN\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/3-9254-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v6b_SOycZ47Co6c5rMrouQ\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Kh&aacute;ch sạn thiết kế ri&ecirc;ng cho du lịch ngủ. Ảnh:&nbsp;<em>CNN</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo&nbsp;<em>CNN</em>, trong năm 2022 vừa qua, kh&aacute;ch sạn kh&aacute;ch sạn Park Hyatt New York (Mỹ) đ&atilde; khai trương Bryte Restorative Sleep Suite - loại ph&ograve;ng rộng hơn 80 m2 thiết kế đầy đủ tiện nghi v&agrave; khả năng c&aacute;ch &acirc;m đặc biệt để n&acirc;ng cao chất lượng giấc ngủ. Tại London (Anh) Zedwell l&agrave; kh&aacute;ch sạn ti&ecirc;n phong trong lĩnh vực kinh doanh ph&ograve;ng nghỉ, tập trung tối đa v&agrave;o giấc ngủ, khai trương năm 2020. C&aacute;c ph&ograve;ng ốc ở đ&acirc;y đều được trang bị hệ thống c&aacute;ch &acirc;m tốt, giường đệm &ecirc;m &aacute;i v&agrave; ph&ugrave; hợp nhất cho việc ngủ. Năm 2021, một thương hiệu giường tại Thụy Điển đ&atilde; th&agrave;nh lập H&auml;stens Sleep Spa (Kh&aacute;ch sạn trị liệu giấc ngủ) ở th&agrave;nh phố Coimbra, Bồ Đ&agrave;o Nha, gồm 15 ph&ograve;ng đặc biệt để phục vụ giấc ngủ trọn vẹn nhất.</p>\r\n<p class=\"Normal\">Ngo&agrave;i gi&uacute;p cải thiện giấc ngủ, xu hướng du lịch n&agrave;y n&agrave;y c&ograve;n gi&uacute;p du kh&aacute;ch trẻ ho&aacute; cơ thể v&agrave; t&acirc;m tr&iacute;, cải thiện t&acirc;m trạng, giảm căng thẳng, lo &acirc;u với chi ph&iacute; tiết kiệm, ph&ugrave; hợp nhiều đối tượng.</p>\r\n<p class=\"Normal\"><strong>Du lịch chữa l&agrave;nh</strong></p>\r\n<p class=\"Normal\">Sau Covid-19, mọi người ng&agrave;y c&agrave;ng quan t&acirc;m đến vấn đề chăm s&oacute;c sức khỏe, sống l&agrave;nh mạnh hơn. Wellness Travel l&agrave; h&igrave;nh thức du lịch kh&ocirc;ng mới nhưng c&oacute; xu hướng ph&aacute;t triển mạnh trong 2 năm nay, tập trung v&agrave;o hoạt động thể chất v&agrave; tinh thần. Đ&acirc;y ch&iacute;nh l&agrave; loại h&igrave;nh du lịch kh&ocirc;ng hướng du kh&aacute;ch đến c&aacute;c hoạt động cần phải di chuyển, tham quan, kh&aacute;m ph&aacute; qu&aacute; nhiều.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XmHolZ1pNlByZ4j75mqSow 1x, https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1YijD4HwsVa7FrEzMqR51Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=HlTmuryGInq_jQqlyGWbWQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XmHolZ1pNlByZ4j75mqSow 1x, https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1YijD4HwsVa7FrEzMqR51Q 1.5x, https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=HlTmuryGInq_jQqlyGWbWQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XmHolZ1pNlByZ4j75mqSow\" alt=\"Ảnh: Karma Group\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/19/4-Xu-huong-du-lich-chua-lanh-N-4512-7659-1671442921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XmHolZ1pNlByZ4j75mqSow\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Wellness Travel hướng tới hoạt động thể chất v&agrave; tinh thần. Ảnh:&nbsp;<em>Karma Group</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Những tour du lịch chữa l&agrave;nh được thiết kế nhắm v&agrave;o mục đ&iacute;ch tăng cường sức khỏe cho du kh&aacute;ch, t&aacute;i tạo cơ thể bằng việc khai th&aacute;c nhiều hoạt động về thể dục thể thao, t&acirc;m l&yacute; hay t&acirc;m linh. Điển h&igrave;nh l&agrave; việc thực hiện những liệu tr&igrave;nh chăm s&oacute;c sức khỏe như massage, yoga, thiền kết hợp việc tham quan du lịch những địa điểm linh thi&ecirc;ng, gi&agrave;u t&iacute;nh t&acirc;m linh từ đ&oacute; du kh&aacute;ch sẽ cảm thấy thư th&aacute;i, khoẻ mạnh, đưa cơ thể đạt trạng th&aacute;i c&acirc;n bằng.</p>\r\n<p class=\"Normal\">Theo b&aacute;o c&aacute;o của&nbsp;<em>The Global Wellness Institute</em>, đến cuối 2019, m&ocirc; h&igrave;nh du lịch n&agrave;y đ&atilde; c&oacute; mặt ở hơn 100 quốc gia v&agrave; được dự b&aacute;o tăng trưởng với tốc độ trung b&igrave;nh 7,5% hằng năm. Đến hết năm 2022 kinh doanh trong mảng du lịch chữa l&agrave;nh ước đạt mức 919 tỷ USD, chiếm 18% tỷ trọng thị trường du lịch thế giới. C&aacute;c quốc gia đi đầu về m&ocirc; h&igrave;nh n&agrave;y phải kể đến Nhật Bản với h&igrave;nh thức tắm onsen, tắm đ&aacute; muối tại H&agrave;n Quốc, c&aacute;c tour du lịch kết hợp thiền định v&agrave; yoga tại Ấn Độ, Philipines với những h&ograve;n đảo trong l&agrave;nh kết hợp với tham quan ch&ugrave;a chiền.</p>\r\n</article>\r\n<p>&nbsp;</p>', 9, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (27, 'Khách Việt một mình đến vòng Bắc Cực đón Giáng sinh', '2022-12-05 10:49:23', 322, 'Thúy Anh cảm động khi biết đêm Giáng sinh, người dân Greenland sẽ tắt hết đèn và thắp sáng những ngôi mộ, tưởng nhớ người đã khuất. ', '<p class=\"description\">Th&uacute;y Anh cảm động khi biết đ&ecirc;m Gi&aacute;ng sinh, người d&acirc;n Greenland sẽ tắt hết đ&egrave;n v&agrave; thắp s&aacute;ng những ng&ocirc;i mộ, tưởng nhớ người đ&atilde; khuất.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ho&agrave;ng Th&uacute;y Anh, ngo&agrave;i 40 tuổi, sống tại Mỹ, đến Greenland, địa danh nằm trong v&ograve;ng Bắc Cực v&agrave;o ng&agrave;y 16/12 v&agrave; dự định ở đến cuối th&aacute;ng. Nữ du kh&aacute;ch gốc Việt đ&atilde;&nbsp;<a href=\"https://vnexpress.net/co-gai-goc-viet-di-99-nuoc-tren-the-gioi-4529800.html?fbclid=IwAR2eZKZOlsATh4BClEeUCgW8eh5dMaHX7goCSJmzEkCeDWHP5mDRbG-1Zgc\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_DauChan-4550682&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-DenTham99QuocGia&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">đến thăm 99 quốc gia</a>, v&ugrave;ng l&atilde;nh thổ v&agrave; Greenland l&agrave; điểm thứ 100.</p>\r\n<p class=\"Normal\">\"T&ocirc;i chọn Greenland v&igrave; nhiều yếu tố. Thứ nhất v&igrave; đ&atilde; cuối năm, t&ocirc;i muốn đi đ&acirc;u đ&oacute; để đ&oacute;n Gi&aacute;ng sinh, thay v&igrave; Mỹ. Thứ hai, đ&oacute; phải l&agrave; nơi lạnh v&agrave; c&oacute; tuyết. Thứ ba, nơi n&agrave;y chỉ c&aacute;ch Bắc Cực khoảng 1.000 km. T&ocirc;i đ&atilde; đến Nam Cực hồi th&aacute;ng hai. Nếu đến Greenland, t&ocirc;i ho&agrave;n th&agrave;nh kế hoạch đi đủ một v&ograve;ng tr&aacute;i đất, từ Nam Cực đến Bắc Cực\", chị n&oacute;i về l&yacute; do bắt đầu của chuyến đi một m&igrave;nh tới Greenland lần n&agrave;y.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VDz0d9uWjxl1gWofvc64gA 1x, https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jdJSVBXBL8iXoTx0O2m4aA 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=UIyKdkltMNqcxiieORr8Xg 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VDz0d9uWjxl1gWofvc64gA 1x, https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jdJSVBXBL8iXoTx0O2m4aA 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=UIyKdkltMNqcxiieORr8Xg 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VDz0d9uWjxl1gWofvc64gA\" alt=\"Ho&agrave;ng Th&uacute;y Anh chụp ảnh tại\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/320888516-3014911752147654-228-2452-4274-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VDz0d9uWjxl1gWofvc64gA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ho&agrave;ng Th&uacute;y Anh chụp ảnh tại</p>\r\n</figcaption>\r\n</figure>\r\n<div class=\"width_common slide_on_detail\">\r\n<div id=\"js_slider_detail_0\" class=\"swiper-container swiper-container-horizontal\">\r\n<div class=\"swiper-wrapper\">\r\n<div class=\"swiper-slide swiper-slide-active\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/sao-1509-1671567928-4398-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bDiMj_qA4e8blD06-41EmA\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/sao-1509-1671567928-4398-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1eUajdVvlRNIsHv_kvodug\"></a></div>\r\n<div class=\"swiper-slide swiper-slide-next\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/sao1-9244-1671567929-1920-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UvEEmNI2kdXrMOzVcGmLwA\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/sao1-9244-1671567929-1920-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BlK4MPjlniyXv6fSpS_WHQ\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/bang1-4733-1671567976-6742-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=33jpcdPTLDEjYu5XAMKh6w\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/bang1-4733-1671567976-6742-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OnH9xc66vAIoBxei4aUQuQ\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/bang3-6031-1671567977-7490-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=IpF8VgmCtUHWa5MLM9tW9w\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/bang3-6031-1671567977-7490-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Oxp_aduwiBFEIJ1i67SK9w\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/nha1-4591-1671568023-1301-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UwRA6eHM6ctc_O8cZ73L-Q\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/nha1-4591-1671568023-1301-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4AnPXLDxGaVF1zkuAjtpIg\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/319910661-3069385819871388-310-2908-7876-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Y8fNecbDboMIGQiC-1ge-Q\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/319910661-3069385819871388-310-2908-7876-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ISNmTeucPR9AoYNyxcseIQ\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/bo-680-1671568291-8314-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mekp0-EdqYxFg3mLW53hwQ\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/bo-680-1671568291-8314-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ll8USis6mutvcs1TEsryVA\"></a></div>\r\n<div class=\"swiper-slide\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/cucquang111-6143-1671568715-9239-1671568947.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uEJlEyDsdrV4QqYC2YWCKg\"><a><img src=\"https://i1-dulich.vnecdn.net/2022/12/21/cucquang111-6143-1671568715-9239-1671568947.jpg?w=300&amp;h=180&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8zv1WJNiGPunTv1S-w-SVw\"></a></div>\r\n</div>\r\n<div class=\"swiper-button-slide-detail swiper-button-slide-detail-next\" tabindex=\"0\" role=\"button\" aria-label=\"Next slide\" aria-disabled=\"false\">&nbsp;</div>\r\n<div class=\"swiper-button-slide-detail swiper-button-slide-detail-prev swiper-button-disabled\" tabindex=\"0\" role=\"button\" aria-label=\"Previous slide\" aria-disabled=\"true\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; h&ograve;n đảo lớn nhất thế giới, thuộc Đan Mạch, c&oacute; diện t&iacute;ch hơn 2,1 triệu km2, với 80% diện t&iacute;ch bề mặt phủ băng tuyết, 20% c&ograve;n lại rộng gần bằng Thụy Điển, theo&nbsp;<em>Visit Greenland,</em>&nbsp;website ch&iacute;nh thức của sở du lịch h&ograve;n đảo.</p>\r\n<p class=\"Normal\">Sau hai tuần nghi&ecirc;n cứu về nơi sẽ đến, Th&uacute;y Anh mua v&eacute; tới Copenhagen, Đan Mạch, sau đ&oacute; bay tiếp đến Greenland, ở tại th&agrave;nh phố Ilulissat.</p>\r\n<p class=\"Normal\">Laarseeraq, 23 tuổi, l&agrave;m nghề đ&aacute;nh c&aacute;, l&agrave; người Th&uacute;y Anh tiếp x&uacute;c nhiều nhất trong những ng&agrave;y ở đảo. Ban đầu, Th&uacute;y Anh định đi theo tour nhưng v&igrave; &iacute;t kh&aacute;ch, tour hủy. Khi đang lang thang tr&ecirc;n phố, chị gặp Laseearaq v&agrave; bố chuẩn bị đi đ&aacute;nh c&aacute; n&ecirc;n đi theo họ. \"Họ rất th&acirc;n thiện, dễ thương. Chiếc cano c&oacute; t&ecirc;n Oscar lướt ra khỏi vịnh kh&ocirc;ng mấy dễ d&agrave;ng, v&igrave; phải tr&aacute;nh nhiều băng tr&ocirc;i. T&ocirc;i c&oacute; thể nghe tiếng băng vỡ khi can&ocirc; lướt đi\", Th&uacute;y Anh n&oacute;i.</p>\r\n<p class=\"Normal\">Greenland c&oacute; ng&agrave;nh c&ocirc;ng nghiệp ch&iacute;nh l&agrave; du lịch v&agrave; xuất khẩu c&aacute; hồi. Đ&aacute;nh c&aacute; phải ra xa bờ, gi&oacute; lạnh buốt. Bố của Laseearag l&aacute;i t&agrave;u, c&ograve;n anh chuẩn bị th&ugrave;ng mồi cho c&aacute;. Tr&ecirc;n khoang t&agrave;u c&oacute; m&aacute;y sưởi để khi n&agrave;o lạnh th&igrave; chạy v&agrave;o sưởi ấm.</p>\r\n<div class=\"x11i5rnm x126k92a x1mh8g0r x1vvkbs xat24cr xtlvy1s\">\r\n<p>Th&uacute;y Anh cũng gh&eacute; thăm gia đ&igrave;nh Laarseeraq để hiểu hơn về cuộc sống của người địa phương. Mẹ v&agrave; d&igrave; của anh đang chuẩn bị quần &aacute;o cho dịp Gi&aacute;ng sinh. Những người phụ nữ Eskimo sẽ mặc quần &aacute;o d&agrave;i truyền thống một lần. \"Bộ n&agrave;y rất đặc biệt, được truyền từ mẹ đến con g&aacute;i. &Aacute;o bằng len nhiều m&agrave;u, quần đ&ugrave;i v&agrave; ủng may bằng da sư tử biển. Loại n&agrave;y rất cứng, nhưng kh&ocirc;ng thấm nước v&agrave; rất ấm. Đ&acirc;y l&agrave; bộ đồ để mặc v&agrave;o dịp Gi&aacute;ng sinh v&agrave; khi kết h&ocirc;n\", mẹ Laarseeraq n&oacute;i.</p>\r\n</div>\r\n<p class=\"Normal\">Ililissat c&oacute; 5.000 d&acirc;n th&igrave; c&oacute; đến 3.000 con ch&oacute;, đều l&agrave; giống lai giữa ch&oacute; hoang v&agrave; thuần chủng, thường sống ở m&ocirc;i trường lạnh gi&aacute;. Ch&uacute;ng c&oacute; mặt ở mọi nơi ngo&agrave;i đường. Mỗi tối, chủ của ch&uacute;ng sẽ đến v&agrave; cho ăn. \"Khi tuyết rơi nhiều, ch&uacute;ng được người d&acirc;n sử dụng để k&eacute;o xe sled (xe trượt tuyết) cho kh&aacute;ch du lịch, cũng như chở h&agrave;ng h&oacute;a. Ch&uacute;ng kh&ocirc;ng tấn c&ocirc;ng kh&aacute;ch, kh&aacute;ch đi ngang qua cũng kh&ocirc;ng sao nhưng kh&ocirc;ng được lại gần hay vuốt ve\", Laarseeraq cho hay. Gi&aacute; mỗi lần ngồi xe do ch&oacute; k&eacute;o từ 180 USD.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mbQ82WkO9L8_soDx51Q6xQ 1x, https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M_iGmr_KzrzzaDq9Qrw5EA 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Rp7vIquArXMLVyMzxMdtNQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mbQ82WkO9L8_soDx51Q6xQ 1x, https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M_iGmr_KzrzzaDq9Qrw5EA 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Rp7vIquArXMLVyMzxMdtNQ 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mbQ82WkO9L8_soDx51Q6xQ\" alt=\"Bầu trời sẽ đỏ thẫm khi mặt trời xuất hiện ph&iacute;a đường ch&acirc;n trời ở Greenland.\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/320897860-471123271856945-6521-7272-9020-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mbQ82WkO9L8_soDx51Q6xQ\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Bầu trời sẽ đỏ thẫm khi mặt trời xuất hiện ph&iacute;a đường ch&acirc;n trời ở Greenland.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">\"T&ecirc;n l&agrave; Greenland (v&ugrave;ng đất xanh), nhưng t&ocirc;i chẳng thấy m&agrave;u xanh c&acirc;y cối đ&acirc;u hết\", l&agrave; cảm nhận đầu ti&ecirc;n của chị Th&uacute;y Anh về nơi vừa đặt ch&acirc;n đến. M&ugrave;a đ&ocirc;ng ở đ&acirc;y mặt trời chỉ xuất hiện ở đường ch&acirc;n trời từ 10h đến 13h30 rồi lặn. Phải đợi đến cuối th&aacute;ng một, mặt trời mới bắt đầu xuất hiện l&acirc;u hơn.</p>\r\n<p class=\"Normal\">Những ng&agrave;y n&agrave;y, đường phố được trang tr&iacute; với những ngọn nến, ng&ocirc;i sao bằng đ&egrave;n điện m&agrave;u v&agrave;ng, gắn l&ecirc;n cột điện. \"Nếu bạn nh&igrave;n v&agrave;o &ocirc; cửa sổ của mỗi ng&ocirc;i nh&agrave; sẽ thấy những v&igrave; sao m&agrave;u cam rực rỡ hoặc m&agrave;u đỏ\", Th&uacute;y Anh n&oacute;i. Mọi người chọn m&agrave;u cam, đỏ cho ng&ocirc;i sao v&igrave; đ&acirc;y l&agrave; m&agrave;u sắc mang lại cảm gi&aacute;c ấm &aacute;p trong m&ugrave;a đ&ocirc;ng.</p>\r\n<p class=\"Normal\">Greenland lạnh (thường &acirc;m độ C v&agrave; hiếm khi cao tr&ecirc;n 13 độ C), c&acirc;y kh&ocirc;ng mọc nổi n&ecirc;n người d&acirc;n nhập c&acirc;y th&ocirc;ng trang tr&iacute; từ ch&acirc;u &Acirc;u. Họ thường đợi đến ng&agrave;y 23 mới bắt đầu trang tr&iacute; c&acirc;y th&ocirc;ng. C&aacute;c gia đ&igrave;nh thường đợi con ngủ say mới bắt tay dựng c&acirc;y th&ocirc;ng v&igrave; muốn mang lại bất ngờ cho lũ trẻ khi thức dậy v&agrave;o s&aacute;ng h&ocirc;m sau. Dưới gốc c&acirc;y thường đặt rất nhiều qu&agrave;, l&agrave; những đ&ocirc;i tất ấm, bao tay l&agrave;m bằng da sư tử biển hoặc những chiếc sled cho trẻ con, một người d&acirc;n địa phương chia sẻ với Th&uacute;y Anh về phong tục tr&ecirc;n h&ograve;n đảo.</p>\r\n<p class=\"Normal\">V&agrave;o ng&agrave;y Gi&aacute;ng sinh, trẻ con sẽ mặc đồ trắng, đeo v&ograve;ng kim tuyến tr&ecirc;n đầu, tay cầm nến h&aacute;t b&ecirc;n c&acirc;y th&ocirc;ng. Ch&uacute;ng cũng đi diễu h&agrave;nh qua c&aacute;c đường phố quanh nơi ở v&agrave; nhận qu&agrave; l&agrave; những chiếc b&aacute;nh ngọt. Phụ nữ sẽ mặc quần &aacute;o truyền thống. Hầu hết người d&acirc;n đều theo đạo C&ocirc;ng gi&aacute;o, n&ecirc;n cả gia đ&igrave;nh sẽ đi nh&agrave; thờ dự th&aacute;nh lễ hai lần trong ng&agrave;y Gi&aacute;ng sinh. Mỗi lần khoảng 90 ph&uacute;t. \"Gi&aacute;ng sinh sẽ kh&ocirc;ng được coi l&agrave; trọn vẹn nếu kh&ocirc;ng đi lễ nh&agrave; thờ\", một người địa phương n&oacute;i.</p>\r\n<p class=\"Normal\">Đ&ecirc;m Gi&aacute;ng sinh, mọi người thường ăn vịt hoặc heo nướng gi&ograve;n b&igrave;. Ngo&agrave;i ra, họ c&ograve;n c&oacute; thịt cừu hoặc tuần lộc. Đ&ecirc;m 24/12, cả th&agrave;nh phố đều tắt đ&egrave;n v&agrave; thắp s&aacute;ng c&aacute;c ng&ocirc;i mộ ở nghĩa trang để tưởng nhớ tổ ti&ecirc;n, người th&acirc;n bạn b&egrave; đ&atilde; mất. \"Đ&acirc;y l&agrave; một truyền thống rất đẹp v&agrave; cảm động của người d&acirc;n địa phương\", Th&uacute;y Anh n&oacute;i.</p>\r\n<div class=\"x11i5rnm x126k92a x1mh8g0r x1vvkbs xat24cr xtlvy1s\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XzsQeJhdXExYicvXoUvEcw 1x, https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=s-ONdlAXRN2cEsdGYmI78g 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IhD2EcViJtAi0ReNc9TPZQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XzsQeJhdXExYicvXoUvEcw 1x, https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=s-ONdlAXRN2cEsdGYmI78g 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IhD2EcViJtAi0ReNc9TPZQ 2x\"><img class=\"lazy loading\" src=\"https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XzsQeJhdXExYicvXoUvEcw\" alt=\"&Ocirc;ng ngoại v&agrave; d&igrave; của Laarseeraq.\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/nha12-6638-1671568947.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XzsQeJhdXExYicvXoUvEcw\" data-ll-status=\"loading\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Ocirc;ng ngoại v&agrave; d&igrave; của Laarseeraq. D&igrave; anh mặc quần &aacute;o truyền thống v&igrave; Th&uacute;y Anh muốn được nh&igrave;n thấy trang phục n&agrave;y của người Eskimo.</p>\r\n</figcaption>\r\n</figure>\r\n<p>Gi&aacute;ng sinh ở Greenland c&ograve;n c&oacute; cực quang. Đ&acirc;y l&agrave; thời điểm cực quang rất mạnh, n&ecirc;n bầu trời rất s&aacute;ng. Chị Th&uacute;y Anh ở lại trong hai kh&aacute;ch sạn: Hotel Arctic (200 USD một đ&ecirc;m) v&agrave; Hotel Icefiord (300 USD một đ&ecirc;m), đều l&agrave; những nơi c&oacute; thể ngắm cực quang từ ph&ograve;ng ngủ. Nhưng v&igrave; kh&aacute;ch sạn đặt trong th&agrave;nh phố, n&ecirc;n &aacute;nh s&aacute;ng của đ&egrave;n đường v&agrave; kh&aacute;ch sạn l&agrave;m m&agrave;u xanh cực quang bị mất, thay v&agrave;o đ&oacute; l&agrave; m&agrave;u trắng. Để ngắm cực quang với m&agrave;u sắc nguy&ecirc;n bản nhất, chị phải đến nơi tối đen, kh&ocirc;ng c&oacute; &aacute;nh đ&egrave;n.</p>\r\n<p>\"Greenland &iacute;t nơi giải tr&iacute;, n&ecirc;n t&ocirc;i v&agrave; bạn b&egrave; th&iacute;ch leo l&ecirc;n n&uacute;i ngắm sao v&agrave; cực quang v&agrave;o những ng&agrave;y kh&ocirc;ng m&acirc;y. Cứ nh&igrave;n l&ecirc;n trời, nếu thấy sao lấp l&aacute;nh rực rỡ khắp nơi l&agrave; tối đ&oacute; sẽ c&oacute; cực quang. Nếu muốn ngắm cực quang đẹp nhất, n&ecirc;n ở lại từ 3-4 ng&agrave;y\", Laarseeraq n&oacute;i. C&ograve;n Th&uacute;y Anh, chuyến đi lần n&agrave;y mới ở 4 ng&agrave;y, chị đ&atilde; 3 lần thấy cực quang.</p>\r\n<p>Chị Th&uacute;y Anh gợi &yacute; n&ecirc;n nhờ người d&acirc;n địa phương dẫn đi chơi, gi&aacute; khoảng 100 USD một ng&agrave;y. Bạn cũng c&oacute; thể mua tour gi&aacute; 85 USD một người, tham quan 4 tiếng, nhưng rủi ro l&agrave; tour c&oacute; thể bị hủy v&agrave; thời gian kh&aacute;m ph&aacute; ngắn.</p>\r\n<p>Th&uacute;y Anh cho biết, nếu kh&ocirc;ng chịu được lạnh, du kh&aacute;ch c&oacute; thể gh&eacute; thăm nơi n&agrave;y v&agrave;o 3 th&aacute;ng m&ugrave;a h&egrave;, từ 6 đến 9. Khi đ&oacute;, trời ấm hơn, c&oacute; nhiều tour v&agrave; chuyến bay hơn m&ugrave;a đ&ocirc;ng. Từ th&aacute;ng 11 đến 1, trời tối cả ng&agrave;y, c&oacute; &aacute;nh s&aacute;ng m&agrave;u cam đỏ của mặt trời v&agrave;o l&uacute;c 10h đến 13h30, sau đ&oacute; tối đen. Nhưng buổi tối c&oacute; cực quang.</p>\r\n<p>\"Trước khi đến Greenland, t&ocirc;i đ&atilde; phải đắn đo rất nhiều liệu rằng c&oacute; n&ecirc;n liều lĩnh kh&ocirc;ng. L&yacute; do l&agrave; thời tiết khắc nghiệt, lạnh dưới &acirc;m độ C v&agrave; lu&ocirc;n trải nghiệm c&aacute;c hoạt động ngo&agrave;i trời dưới điều kiện lạnh buốt. Nhưng cuối c&ugrave;ng, t&ocirc;i rất tự h&agrave;o v&igrave; đ&atilde; vượt qua được nỗi sợ h&atilde;i của ch&iacute;nh m&igrave;nh để ho&agrave;n th&agrave;nh mục ti&ecirc;u đến 100 quốc gia, v&ugrave;ng l&atilde;nh thổ trước 2023\", Th&uacute;y Anh n&oacute;i.</p>\r\n</div>\r\n</article>\r\n<p>&nbsp;</p>', 9, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (28, 'Christina Aguilera bay trực thăng, khen vịnh Hạ Long \'kỳ diệu\'', '2021-12-21 10:51:08', 731, 'Bay trực thăng ngắm vịnh Hạ Long là trải nghiệm du lịch đầu tiên nữ ca sĩ Mỹ, Christina Aguilera, chọn khi đến Việt Nam.', '<p class=\"description\">Bay trực thăng ngắm vịnh Hạ Long l&agrave; trải nghiệm du lịch đầu ti&ecirc;n nữ ca sĩ Mỹ, Christina Aguilera, chọn khi đến Việt Nam.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 18/12, Christina c&ugrave;ng h&ocirc;n phu Matthew Rutler v&agrave; 8 người bạn xuất ph&aacute;t từ s&acirc;n bay Gia L&acirc;m, H&agrave; Nội để đến Tuần Ch&acirc;u, Hạ Long. Chuyến bay khởi h&agrave;nh l&uacute;c 10h20 v&agrave; hạ c&aacute;nh sau đ&oacute; 50 ph&uacute;t. Chị Chử Minh T&uacute;, đại diện đơn vị cung cấp dịch vụ bay, cho biết đo&agrave;n đặt dịch vụ 5 ng&agrave;y trước.</p>\r\n<p class=\"Normal\">Chuyến bay của Christina được thu&ecirc; ri&ecirc;ng, thời gian ngắm vịnh Hạ Long l&agrave; 10 ph&uacute;t, qua c&aacute;c h&ograve;n Đinh hương, h&ograve;n G&agrave; chọi, h&ograve;n Vạn bội, đảo Titop, h&ograve;n Ch&acirc;n Voi...</p>\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_369090\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003400\" data-vscaten=\"Du lịch\" data-vid=\"369090\" data-ratio=\"2\" data-articleoriginal=\"4550809\" data-ads=\"0\" data-license=\"1\" data-duration=\"33\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_369090\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n<div class=\"bg_overlay_small\">&nbsp;</div>\r\n<div class=\"\">\r\n<div id=\"video-369090\">\r\n<div id=\"parser_player_369090\" class=\"media_content\">\r\n<div id=\"videoContainter_369090\" class=\"videoContainter\">\r\n<div id=\"media-video-369090\" class=\"video-js vjs-default-skin vjs-controls-enabled vjs-workinghover vjs-v7 media-video-369090-dimensions vjs-playing vjs-has-started vjs-user-inactive\" lang=\"vi\" role=\"region\" data-subtitle=\"0\" data-mode=\"240|360|480|720\" aria-label=\"Video Player\">\r\n<div class=\"vjs-text-track-display\" aria-live=\"off\" aria-atomic=\"true\">&nbsp;</div>\r\n<div class=\"vjs-loading-spinner\" dir=\"ltr\"><span class=\"vjs-control-text\">Video Player is loading.</span></div>\r\n<button class=\"vjs-big-play-button\" title=\"\" type=\"button\" aria-disabled=\"false\"></button>\r\n<div class=\"vjs-control-bar\" dir=\"ltr\"><button class=\"vjs-play-control vjs-control vjs-button vjs-playing\" title=\"Dừng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Dừng</span></button>\r\n<div class=\"vjs-current-time vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Hiện tại&nbsp;</span><span class=\"vjs-current-time-display\" aria-live=\"off\">0:03</span></div>\r\n<div class=\"vjs-time-control vjs-time-divider\">\r\n<div>/</div>\r\n</div>\r\n<div class=\"vjs-duration vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Thời lượng&nbsp;</span><span class=\"vjs-duration-display\" aria-live=\"off\">0:33</span></div>\r\n<div class=\"vjs-progress-control vjs-control\">\r\n<div class=\"vjs-progress-holder vjs-slider vjs-slider-horizontal\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"12.10\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Progress Bar\" aria-valuetext=\"0:04 of 0:33\">\r\n<div class=\"vjs-load-progress\"><span class=\"vjs-control-text\">Đ&atilde; tải: 0%</span>\r\n<div>&nbsp;</div>\r\n</div>\r\n<div class=\"vjs-play-progress vjs-slider-bar\">\r\n<div class=\"vjs-time-tooltip\">&nbsp;</div>\r\n<span class=\"vjs-control-text\">Tiến tr&igrave;nh: 0%</span></div>\r\n</div>\r\n</div>\r\n<div class=\"vjs-volume-panel vjs-control vjs-volume-panel-vertical\"><button class=\"vjs-mute-control vjs-control vjs-button vjs-vol-0\" title=\"Bỏ tắt tiếng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Bỏ tắt tiếng</span></button>\r\n<div class=\"vjs-volume-control vjs-control vjs-volume-vertical\">\r\n<div class=\"vjs-volume-bar vjs-slider-bar vjs-slider vjs-slider-vertical\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"0\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Volume Level\" aria-live=\"polite\" aria-valuetext=\"0%\">\r\n<div class=\"vjs-volume-level\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<button class=\"vjs-fullscreen-control vjs-control vjs-button\" title=\"To&agrave;n m&agrave;n h&igrave;nh\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">To&agrave;n m&agrave;n h&igrave;nh</span></button></div>\r\n<div id=\"369090_soundTopRight\" class=\"fp-sound-status-container\"></div>\r\n<div class=\"share-hover\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">\r\n<p class=\"Image\">Trải nghiệm của Christina Aguilera tại Hạ Long. Video:<em>&nbsp;Instagram</em></p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n</div>\r\n<p class=\"Normal\">Thượng &uacute;y Trần Việt Anh, 27 tuổi, cơ ph&oacute; chuyến bay cho biết ban đầu kh&ocirc;ng nhận ra nữ ca sĩ nổi tiếng, nhưng ấn tượng v&igrave; nh&oacute;m kh&aacute;ch tỏ ra rất th&acirc;n thiện. Họ ch&agrave;o hỏi tổ bay, hỏi trước về lịch tr&igrave;nh sẽ ngắm cảnh ở những địa điểm n&agrave;o. Sau khi hạ c&aacute;nh, Việt Anh được c&aacute;c đồng nghiệp tiết lộ về vị kh&aacute;ch đặc biệt. Anh bất ngờ n&ecirc;n mới l&ecirc;n mạng để kiểm tra c&aacute;c th&ocirc;ng tin về nữ ca sĩ.</p>\r\n<p class=\"Normal\">\"T&ocirc;i rất bất ngờ, sau đ&oacute; l&agrave; vui v&agrave; đ&ocirc;i ch&uacute;t tự h&agrave;o v&igrave; tổ bay vừa đi c&ugrave;ng một nh&acirc;n vật nổi tiếng của l&agrave;ng &acirc;m nhạc thế giới. Trước đ&oacute;, ch&uacute;ng t&ocirc;i cũng từng chở nhiều người nổi tiếng nhưng Christina l&agrave; một trong những vị kh&aacute;ch quốc tế th&acirc;n thiện nhất. Mọi người tr&ograve; chuyện rất vui vẻ. Khi hạ c&aacute;nh xuống Tuần Ch&acirc;u, Christina v&agrave; nh&oacute;m bạn c&ograve;n vỗ tay. Họ bắt tay tổ bay trước khi rời đi để tiếp tục h&agrave;nh tr&igrave;nh kh&aacute;c kh&aacute;m ph&aacute; Hạ Long tr&ecirc;n du thuyền\", Việt Anh n&oacute;i.</p>\r\n<p class=\"Normal\">Trong video trả lời phỏng vấn với MC Ph&iacute; Linh, nữ ca sĩ cũng chia sẻ: \"T&ocirc;i đến đ&acirc;y đ&uacute;ng dịp sinh nhật. Vịnh Hạ Long thật kỳ diệu, một địa điểm xinh đẹp. Đ&oacute; l&agrave; một chuyến đi đặc biệt. Nh&igrave;n từ tr&ecirc;n trực thăng mọi thứ đều đẹp, những d&atilde;y n&uacute;i h&ugrave;ng vĩ. T&ocirc;i cảm thấy như m&igrave;nh được sống lại. Lần n&agrave;y t&ocirc;i đi c&ugrave;ng những người bạn th&acirc;n. Chuyến đi gi&uacute;p ch&uacute;ng t&ocirc;i x&iacute;ch lại gần nhau hơn\".</p>\r\n<p class=\"Normal\">Christina cũng gửi lời c&aacute;m ơn tới Việt Nam, v&igrave; đ&atilde; cho c&ocirc; c&oacute; cơ hội trải nghiệm những điều đặc biệt n&agrave;y, trong dịp đặc biệt của m&igrave;nh. Sau khi rời s&acirc;n bay, đo&agrave;n của nữ ca sĩ tiếp tục h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Hạ Long tr&ecirc;n du thuyền. Đến 16h c&ugrave;ng ng&agrave;y, họ quay lại Tuần Ch&acirc;u để bay về H&agrave; Nội.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uvzZoKc0jFVaEXR2gukSng 1x, https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fH_MYyX4NBboAmKpMBYngw 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=I24ygBnPcL7QmT-K2qBKvQ 2x\" data-srcset=\"https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uvzZoKc0jFVaEXR2gukSng 1x, https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fH_MYyX4NBboAmKpMBYngw 1.5x, https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=I24ygBnPcL7QmT-K2qBKvQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uvzZoKc0jFVaEXR2gukSng\" alt=\"Christina Aguilera\" loading=\"lazy\" data-src=\"https://i1-dulich.vnecdn.net/2022/12/21/319803563-719432029376106-2501-1196-5306-1671598116.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uvzZoKc0jFVaEXR2gukSng\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Christina Aguilera tr&ecirc;n trực thăng khởi h&agrave;nh từ H&agrave; Nội tới Hạ Long. Ảnh:&nbsp;<em>Instagram</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/bay-tren-vinh-ha-long-4126355.html\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-DuLich_DauChan-4550809&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-BayTrucThangTrenVinhHaLong&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Bay trực thăng tr&ecirc;n vịnh Hạ Long</a>&nbsp;l&agrave; một trong những tour du lịch cao cấp ở Quảng Ninh. Ngo&agrave;i h&agrave;nh tr&igrave;nh 10 ph&uacute;t với gi&aacute; 2,5 triệu đồng một người, tương tự Christina Aguilera, du kh&aacute;ch cũng c&oacute; thể chọn thời gian bay l&acirc;u hơn, 15 hoặc 30 ph&uacute;t.</p>\r\n<p class=\"Normal\">Tour 15 ph&uacute;t gi&aacute; 3,6 triệu đồng một người bay qua động Thi&ecirc;n Cung, h&ograve;n Trống m&aacute;i, hang Sửng Sốt, đảo Titop, V&ograve;ng quay mặt trời, cầu B&atilde;i Ch&aacute;y, ngọn hải đăng, b&atilde;i biển B&atilde;i Ch&aacute;y, đảo Riều. Tour 30 ph&uacute;t gi&aacute; 7,35 triệu đồng bay qua động Thi&ecirc;n Cung, h&ograve;n Trống M&aacute;i, đảo Titop, hang Sửng sốt, l&agrave;ng ch&agrave;i Cửa Vạn, vịnh B&aacute;i Tử Long, đảo Cống đỏ, c&ugrave;ng c&aacute; heo, bảo t&agrave;ng Quảng Ninh, v&ograve;ng quay mặt trời, cầu B&atilde;i Ch&aacute;y, ngọn Hải đăng, b&atilde;i biển B&atilde;i Ch&aacute;y, đảo Riều. Gi&aacute; tour c&ograve;n phụ thuộc v&agrave;o lịch tr&igrave;nh bay cũng như số lượng người bay v&agrave; loại m&aacute;y bay.</p>\r\n<p class=\"Normal\">Christina Aguilera sinh năm 1980 tại Mỹ, nổi tiếng từ b&eacute; khi tham gia c&acirc;u lạc bộ Mickey của Walt Disney c&ugrave;ng nhiều ng&ocirc;i sao kh&aacute;c như Britney Spears, Justin Timberlake... C&ocirc; l&agrave; một trong những nghệ sĩ b&aacute;n được nhiều đĩa nhất mọi thời đại, từng gi&agrave;nh 5 giải Grammy v&agrave; một giải Latin Grammy.</p>\r\n</article>\r\n<p>&nbsp;</p>', 9, 0, 1, 1, NULL);
INSERT INTO `articles` VALUES (29, 'Dàn người đẹp dự thảm đỏ Hoa hậu Việt Nam', '2020-12-15 10:52:23', 9031, 'TP HCM - Hoa hậu Đỗ Thị Hà, Mai Phương, Bảo Ngọc... diện váy áo cắt xẻ dự chung kết Hoa hậu Việt Nam, tối 23/12.', '<p class=\"description\">Hoa hậu Đỗ Thị H&agrave;, Mai Phương, Bảo Ngọc... diện v&aacute;y &aacute;o cắt xẻ dự chung kết Hoa hậu Việt Nam, tối 23/12.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cXgsdaIC4MZ20hpbgjIzWw 1x, https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k3oCrNFyYZ88pv9e92Znvg 1.5x, https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=OcyUdKbq6f6WH2LcWS9cGw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cXgsdaIC4MZ20hpbgjIzWw 1x, https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k3oCrNFyYZ88pv9e92Znvg 1.5x, https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=OcyUdKbq6f6WH2LcWS9cGw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cXgsdaIC4MZ20hpbgjIzWw\" alt=\"&Aacute; hậu Phương Nhi diện đầm hai d&acirc;y xẻ ngực.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/12/23/tham-do-hoa-hau-11-5664-1671801642.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cXgsdaIC